// Console integration for error logs
function setupConsoleIntegration() {
  // Prevent infinite loops with recursion guard
  let isProcessingConsoleMessage = false;

  // Override console methods to capture and forward logs
  const originalConsole = {
    log: console.log,
    error: console.error,
    warn: console.warn,
    info: console.info,
  };

  // Override console.error to capture renderer errors
  console.error = (...args) => {
    originalConsole.error(...args);

    // Prevent recursion loop
    if (isProcessingConsoleMessage) return;

    const message = args
      .map(arg => (typeof arg === 'object' ? JSON.stringify(arg) : String(arg)))
      .join(' ');

    // Skip if message contains our own console integration calls to prevent loops
    if (message.includes('log-console-message') || message.includes('appendErrorLog')) {
      return;
    }

    isProcessingConsoleMessage = true;

    try {
      // Forward to error logs
      appendErrorLog({
        level: 'error',
        message,
        source: 'Renderer',
        timestamp: new Date().toISOString(),
      });
      updateErrorCounts('app-errors');
    } catch (err) {
      // Silently fail to prevent further recursion
    } finally {
      isProcessingConsoleMessage = false;
    }
  };

  // Override console.warn to capture renderer warnings
  console.warn = (...args) => {
    originalConsole.warn(...args);

    // Prevent recursion loop
    if (isProcessingConsoleMessage) return;

    const message = args
      .map(arg => (typeof arg === 'object' ? JSON.stringify(arg) : String(arg)))
      .join(' ');

    // Skip if message contains our own console integration calls to prevent loops
    if (message.includes('log-console-message') || message.includes('appendErrorLog')) {
      return;
    }

    isProcessingConsoleMessage = true;

    try {
      appendErrorLog({
        level: 'warning',
        message,
        source: 'Renderer',
        timestamp: new Date().toISOString(),
      });
    } catch (err) {
      // Silently fail to prevent further recursion
    } finally {
      isProcessingConsoleMessage = false;
    }
  };

  // Listen for console messages from main process and webview
  if (window.electronAPI?.onConsoleMessage) {
    window.electronAPI.onConsoleMessage(consoleData => {
      // Prevent recursion from our own console integration
      if (isProcessingConsoleMessage) return;

      // Skip messages that could cause loops
      if (
        consoleData.message?.includes('log-console-message') ||
        consoleData.message?.includes('appendErrorLog')
      ) {
        return;
      }

      isProcessingConsoleMessage = true;

      try {
        appendErrorLog({
          level: consoleData.level,
          message: consoleData.message,
          source: consoleData.source,
          timestamp: consoleData.timestamp,
        });

        // Update appropriate error count
        if (consoleData.level === 'error') {
          if (consoleData.source.includes('Chrome') || consoleData.source.includes('Browser')) {
            updateErrorCounts('chrome-errors');
          } else {
            updateErrorCounts('app-errors');
          }
        }
      } catch (err) {
        // Silently fail to prevent recursion
      } finally {
        isProcessingConsoleMessage = false;
      }
    });
  }

  // Capture webview console messages
  const webview = document.querySelector('webview');
  if (webview) {
    webview.addEventListener('console-message', event => {
      // Prevent recursion during console processing
      if (isProcessingConsoleMessage) return;

      const { level, message, line, sourceId } = event;

      isProcessingConsoleMessage = true;

      try {
        appendErrorLog({
          level: level === 0 ? 'info' : level === 1 ? 'warning' : 'error',
          message: `${message} (${sourceId}:${line})`,
          source: 'Webview',
          timestamp: new Date().toISOString(),
        });

        if (level >= 2) {
          // error level
          updateErrorCounts('chrome-errors');
        }
      } catch (err) {
        // Silently fail to prevent recursion
      } finally {
        isProcessingConsoleMessage = false;
      }
    });
  }
}

