// Terminal output integration
function setupTerminalLogIntegration() {
  // Monitor terminal output and forward relevant logs to error logs
  const originalWriteTerminal = window.writeTerminal;
  if (originalWriteTerminal) {
    window.writeTerminal = function (terminalId, data) {
      const result = originalWriteTerminal.call(this, terminalId, data);

      // Check if terminal output contains error patterns
      if (typeof data === 'string') {
        const lowerData = data.toLowerCase();
        if (
          lowerData.includes('error') ||
          lowerData.includes('failed') ||
          lowerData.includes('exception')
        ) {
          appendErrorLog({
            level: 'error',
            message: data.trim(),
            source: `Terminal-${terminalId}`,
            timestamp: new Date().toISOString(),
          });
          updateErrorCounts('app-errors');
        } else if (lowerData.includes('warning') || lowerData.includes('warn')) {
          appendErrorLog({
            level: 'warning',
            message: data.trim(),
            source: `Terminal-${terminalId}`,
            timestamp: new Date().toISOString(),
          });
        }
      }

      return result;
    };
  }
}

