// Create unified context dropdown system
async function showUnifiedContextDropdown(atButton, inputEl) {
  console.log('üîç DOM TRACE: showUnifiedContextDropdown called');
  console.log('üîç DOM TRACE: @ button element:', atButton);
  console.log('üîç DOM TRACE: Input element:', inputEl);
  console.log('üîç DOM TRACE: @ button position:', atButton.getBoundingClientRect());

  // Check if dropdown exists
  let dropdown = document.querySelector('.context-dropdown-unified');
  console.log('üîç DOM TRACE: Existing dropdown found:', dropdown);

  if (dropdown) {
    console.log('üîç DOM TRACE: Toggling existing dropdown visibility');
    console.log('üîç DOM TRACE: Current display style:', dropdown.style.display);
    dropdown.style.display = dropdown.style.display === 'none' ? 'block' : 'none';
    console.log('üîç DOM TRACE: New display style:', dropdown.style.display);
    return;
  }

  console.log('üîç DOM TRACE: Creating new dropdown...');
  dropdown = createUnifiedContextDropdown();
  console.log('üîç DOM TRACE: Created dropdown element:', dropdown);
  console.log('üîç DOM TRACE: Dropdown className:', dropdown.className);

  // Position dropdown above button
  const inputBar = document.querySelector('.terminal-input-bar');
  console.log('üîç DOM TRACE: Input bar for positioning:', inputBar);

  if (inputBar) {
    console.log('üîç DOM TRACE: Appending dropdown to input bar');
    inputBar.appendChild(dropdown);
    console.log('üîç DOM TRACE: Dropdown appended successfully');
  } else {
    console.error('üö® DOM TRACE ERROR: Input bar not found for dropdown positioning!');
    // Fallback to document body
    document.body.appendChild(dropdown);
    console.log('üîç DOM TRACE: Dropdown appended to body as fallback');
  }

  // Verify dropdown structure before population
  const sections = dropdown.querySelectorAll('.dropdown-section');
  console.log('üîç DOM TRACE: Dropdown sections count:', sections.length);
  sections.forEach((section, index) => {
    const sectionId = section.id || section.className;
    console.log(`üîç DOM TRACE: Section ${index}: ${sectionId}`);
  });

  // Populate all sections with debugging
  console.log('üîç DOM TRACE: Starting section population...');

  const filesContainer = document.getElementById('files-context-items');
  console.log('üîç DOM TRACE: Files container:', filesContainer);
  if (filesContainer) {
    await populateFilesContext(filesContainer);
    console.log('üîç DOM TRACE: Files context populated');
  }

  const docsContainer = document.getElementById('docs-context-items');
  console.log('üîç DOM TRACE: Docs container:', docsContainer);
  if (docsContainer) {
    await populateDocsContext(docsContainer);
    console.log('üîç DOM TRACE: Docs context populated');
  }

  const errorsContainer = document.getElementById('errors-context-items');
  console.log('üîç DOM TRACE: Errors container:', errorsContainer);
  if (errorsContainer) {
    await populateErrorsContext(errorsContainer);
    console.log('üîç DOM TRACE: Errors context populated');
  }

  const terminalsContainer = document.getElementById('terminal-context-items');
  console.log('üîç DOM TRACE: Terminals container:', terminalsContainer);
  if (terminalsContainer) {
    await populateTerminalsContext(terminalsContainer);
    console.log('üîç DOM TRACE: Terminals context populated');
  }

  // NOTE: Screenshots now integrated into planning section with tiny previews

  // Setup item click handlers with debugging
  const contextItems = dropdown.querySelectorAll('.context-item');
  console.log('üîç DOM TRACE: Context items found for click handlers:', contextItems.length);

  contextItems.forEach((item, index) => {
    console.log(
      `üîç DOM TRACE: Setting up click handler for item ${index}:`,
      item.textContent.substring(0, 30)
    );
    item.addEventListener('click', () => {
      console.log('üîç DOM TRACE: Context item clicked:', item);
      handleContextSelection(item);
      item.classList.add('selected');
      console.log('üîç DOM TRACE: Item marked as selected');
    });
  });

  console.log('üîç DOM TRACE: Making dropdown visible...');

  // Force dropdown visibility with multiple CSS properties
  dropdown.style.display = 'block';
  dropdown.style.visibility = 'visible';
  dropdown.style.opacity = '1';
  dropdown.style.pointerEvents = 'auto';
  dropdown.style.zIndex = '9999';

  console.log('üîç DOM TRACE: Forced dropdown visibility');
  console.log('üîç DOM TRACE: Dropdown final styles:', {
    display: dropdown.style.display,
    visibility: dropdown.style.visibility,
    opacity: dropdown.style.opacity,
    zIndex: dropdown.style.zIndex,
    position: dropdown.style.position || 'from CSS',
  });

  // Force immediate render by accessing properties that trigger layout
  const rect = dropdown.getBoundingClientRect();
  console.log('üîç DOM TRACE: Dropdown final position:', rect);
  console.log('üîç DOM TRACE: Dropdown is visible in viewport:', rect.height > 0 && rect.width > 0);

  // Close dropdown on outside click
  const closeHandler = e => {
    if (!e.target.closest('.context-at-btn') && !e.target.closest('.context-dropdown-unified')) {
      dropdown.style.display = 'none';
      document.removeEventListener('click', closeHandler);
    }
  };
  document.addEventListener('click', closeHandler);
}

