async function populateFilesContext(container) {
  if (!container) return;

  try {
    // Get uploaded files
    const uploadedFiles = (await window.electronAPI?.getUploadedFiles?.()) || [];
    const recentFiles = (await window.electronAPI?.getRecentFiles?.()) || [];

    // Get screenshots from temp folders and DOM
    const screenshots = await getScreenshotsFromTemp();

    const items = [];

    // Add uploaded files
    uploadedFiles.slice(0, 3).forEach(file => {
      items.push({
        icon: getFileIcon(file.ext || file.name),
        name: file.name,
        path: file.path,
        type: 'file',
      });
    });

    // Add screenshots
    screenshots.slice(0, 2).forEach(shot => {
      items.push({
        icon: 'üì∑',
        name: shot.filename,
        path: shot.path,
        type: 'screenshot',
      });
    });

    if (items.length === 0) {
      container.innerHTML = `
        <div class="context-item no-items">
          <span class="item-icon">üìÅ</span>
          <span class="item-name">No files available</span>
        </div>
      `;
    } else {
      container.innerHTML = items
        .map(
          item => `
        <div class="context-item" data-path="${item.path}" data-type="${item.type}">
          <span class="item-icon">${item.icon}</span>
          <span class="item-name">${item.name}</span>
        </div>
      `
        )
        .join('');
    }
  } catch (error) {
    container.innerHTML = `
      <div class="context-item no-items">
        <span class="item-icon">‚ö†Ô∏è</span>
        <span class="item-name">Error loading files</span>
      </div>
    `;
  }
}

async function populateDocsContext(container) {
  console.log('üîç DOM TRACE: populateDocsContext called', container);
  if (!container) return;

  try {
    const items = [];

    // Get ALL planning files from imported-files section
    const planningFiles = document.querySelectorAll('#imported-files .file-item:not(.template)');
    console.log('üîç DOM TRACE: Found planning files:', planningFiles.length);

    planningFiles.forEach((file, index) => {
      const fileName = file.querySelector('.file-name')?.textContent;
      const filePath = file.querySelector('.file-path')?.textContent;
      const fileContent = file.dataset.content;

      console.log(`üîç DOM TRACE: Planning file ${index}:`, fileName, filePath);

      if (fileName) {
        items.push({
          type: 'planning-file',
          name: fileName,
          path: filePath || fileName,
          icon: fileName.endsWith('.md') ? 'üìÑ' : 'üìã',
          content: fileContent || `Planning document: ${fileName}`,
        });
      }
    });

    // Get guidance content from textarea
    const guidanceTextarea = document.getElementById('guidance-content');
    const guidanceContent = guidanceTextarea?.value;
    console.log('üîç DOM TRACE: Guidance content length:', guidanceContent?.length || 0);

    if (guidanceContent && guidanceContent.trim()) {
      items.push({
        type: 'guidance',
        name: 'User Guidance Instructions',
        path: 'guidance-content',
        icon: 'üí°',
        content: guidanceContent,
      });
    }

    // Also check for MD files in uploaded file results
    const fileResults = document.querySelectorAll('.file-upload-result');
    console.log('üîç DOM TRACE: File upload results found:', fileResults.length);

    fileResults.forEach((result, index) => {
      const fileName = result.textContent?.trim();
      if (fileName && fileName.endsWith('.md')) {
        console.log(`üîç DOM TRACE: Uploaded MD file ${index}:`, fileName);
        items.push({
          type: 'uploaded-md',
          name: fileName,
          path: result.dataset.path || fileName,
          icon: 'üìù',
          content: `Uploaded markdown: ${fileName}`,
        });
      }
    });

    // ENHANCED: Add screenshots with tiny previews to planning section
    const screenshots = await getScreenshotsFromTemp();
    const screenshotItems = document.querySelectorAll('#screenshots-list .screenshot-item');
    console.log('üîç DOM TRACE: Found screenshots for tiny previews:', screenshotItems.length);

    // Add screenshots from DOM list with tiny previews
    screenshotItems.forEach((item, index) => {
      const timeEl = item.querySelector('.screenshot-time');
      const pathEl = item.querySelector('.screenshot-path code');
      const imgEl = item.querySelector('img'); // Get actual image element

      if (pathEl) {
        const path = pathEl.textContent;
        const filename = path.split('/').pop();
        const timestamp = timeEl?.textContent || 'Unknown time';
        const imgSrc = imgEl?.src || path; // Use image src for preview

        console.log(`üîç DOM TRACE: Screenshot with preview ${index}:`, filename, path);
        items.push({
          type: 'screenshot',
          name: filename,
          path: path,
          icon: 'üì∑',
          preview: imgSrc, // Store image src for tiny preview
          content: `Screenshot taken at ${timestamp}: ${filename}`,
        });
      }
    });

    console.log(
      'üîç DOM TRACE: Total planning context items (including screenshots):',
      items.length
    );

    if (items.length === 0) {
      container.innerHTML = `
        <div class="context-item no-items">
          <span class="item-icon">üìö</span>
          <span class="item-name">No planning files or screenshots</span>
          <span class="item-hint">Upload MD files or take screenshots</span>
        </div>
      `;
    } else {
      container.innerHTML = items
        .map(item => {
          if (item.type === 'screenshot' && item.preview) {
            // Screenshot with tiny tiny tiny preview (24x18px)
            return `
            <div class="context-item" data-type="${item.type}" data-path="${item.path}" data-content="${encodeURIComponent(item.content)}">
              <span class="item-icon">${item.icon}</span>
              <img class="tiny-preview" src="${item.preview}" alt="Preview" onerror="this.style.display='none'" />
              <div class="item-details">
                <span class="item-name">${item.name}</span>
                <span class="item-path tiny-path">${item.path}</span>
              </div>
            </div>
          `;
          } else if (
            item.type.includes('md') ||
            item.type === 'planning-file' ||
            item.type === 'uploaded-md'
          ) {
            // MD files with content preview
            const contentPreview = item.content
              ? item.content.substring(0, 35).replace(/\n/g, ' ') + '...'
              : '';
            return `
            <div class="context-item" data-type="${item.type}" data-path="${item.path}" data-content="${encodeURIComponent(item.content)}">
              <span class="item-icon">${item.icon}</span>
              <div class="item-details">
                <span class="item-name">${item.name}</span>
                <span class="md-preview">${contentPreview}</span>
              </div>
            </div>
          `;
          } else {
            // Regular planning items (guidance, etc)
            return `
            <div class="context-item" data-type="${item.type}" data-path="${item.path}" data-content="${encodeURIComponent(item.content)}">
              <span class="item-icon">${item.icon}</span>
              <div class="item-details">
                <span class="item-name">${item.name}</span>
              </div>
            </div>
          `;
          }
        })
        .join('');
    }
  } catch (error) {
    container.innerHTML = `
      <div class="context-item no-items">
        <span class="item-icon">‚ö†Ô∏è</span>
        <span class="item-name">Error loading docs</span>
      </div>
    `;
  }
}

async function populateErrorsContext(container) {
  console.log('üîç DOM TRACE: populateErrorsContext called', container);
  if (!container) return;

  try {
    // Get ALL errors from error log container
    const errorLog = document.getElementById('error-log');
    console.log('üîç DOM TRACE: Error log container:', errorLog);

    if (!errorLog) {
      container.innerHTML = `
        <div class="context-item no-items">
          <span class="item-icon">‚ö†Ô∏è</span>
          <span class="item-name">Error log container not found</span>
        </div>
      `;
      return;
    }

    // Get all error items from the error log
    const errorItems = errorLog.querySelectorAll('.error-item');
    console.log('üîç DOM TRACE: Found error items:', errorItems.length);

    if (errorItems.length === 0) {
      container.innerHTML = `
        <div class="context-item no-items">
          <span class="item-icon">‚úÖ</span>
          <span class="item-name">No recent errors</span>
          <span class="item-hint">Error log is clean</span>
        </div>
      `;
    } else {
      // Create single consolidated error log context item
      const totalErrors = errorItems.length;
      const recentErrors = Array.from(errorItems).slice(-5); // Get last 5 errors

      // Build complete error log content
      const fullErrorLog = Array.from(errorItems)
        .map(error => {
          const level = error.dataset.level || 'error';
          const message =
            error.querySelector('.error-message')?.textContent ||
            error.textContent.replace(/\s+/g, ' ').trim();
          const timestamp =
            error.querySelector('.error-timestamp')?.textContent || new Date().toLocaleTimeString();
          return `[${timestamp}] ${level.toUpperCase()}: ${message}`;
        })
        .join('\n');

      console.log('üîç DOM TRACE: Full error log length:', fullErrorLog.length);

      // Create consolidated error context item FIRST - this is the complete log
      const consolidatedError = `
        <div class="context-item error-summary" data-type="error-log" data-content="${encodeURIComponent(fullErrorLog)}">
          <span class="item-icon">üî¥</span>
          <div class="item-details">
            <span class="item-name">Complete Error Log (${totalErrors} errors)</span>
            <span class="item-hint">Full error history for context</span>
          </div>
        </div>
      `;

      container.innerHTML = consolidatedError;
    }
  } catch (error) {
    console.error('üö® DOM TRACE ERROR: populateErrorsContext failed:', error);
    container.innerHTML = `
      <div class="context-item no-items">
        <span class="item-icon">‚ö†Ô∏è</span>
        <span class="item-name">Error loading logs</span>
      </div>
    `;
  }
}

