async function getScreenshotsFromTemp() {
  try {
    const screenshots = [];

    // Check for screenshots in DOM first
    const screenshotItems = document.querySelectorAll('.screenshot-item');
    screenshotItems.forEach(item => {
      const path = item.querySelector('.screenshot-path code')?.textContent;
      if (path && isScreenshotPath(path)) {
        screenshots.push({
          filename: path.split('/').pop(),
          path: path,
        });
      }
    });

    return screenshots;
  } catch (error) {
    return [];
  }
}

function isScreenshotPath(path) {
  const tempPatterns = ['/var/folders/', '/tmp/', '/private/var/folders/'];

  return (
    tempPatterns.some(pattern => path.includes(pattern)) &&
    path.toLowerCase().includes('screenshot')
  );
}

function getFileIcon(filename) {
  const ext = filename?.split('.').pop()?.toLowerCase();
  const icons = {
    js: 'üìÑ',
    html: 'üåê',
    css: 'üé®',
    md: 'üìù',
    json: 'üìã',
    png: 'üì∑',
    jpg: 'üì∑',
    jpeg: 'üì∑',
    gif: 'üì∑',
    svg: 'üñºÔ∏è',
  };
  return icons[ext] || 'üìÑ';
}

function handleContextSelection(item) {
  console.log('üîç DOM TRACE: Context item selected:', item);
  console.log('üîç DOM TRACE: Item element:', item.outerHTML.substring(0, 200));

  const type = item.dataset.type;
  const path = item.dataset.path;
  const content = item.dataset.content;

  console.log('üîç DOM TRACE: Item type:', type);
  console.log('üîç DOM TRACE: Item path:', path);
  console.log('üîç DOM TRACE: Item content:', content ? content.substring(0, 50) + '...' : 'none');

  if (!selectedContextItems) {
    console.error('üö® DOM TRACE ERROR: selectedContextItems array not initialized!');
    selectedContextItems = [];
  }

  console.log('üîç DOM TRACE: Current selected items before:', selectedContextItems.length);

  const contextItem = {
    type: type,
    path: path,
    content: content,
    timestamp: Date.now(),
    name: item.querySelector('.item-name')?.textContent,
  };

  console.log('üîç DOM TRACE: Context item to add:', contextItem);

  // Add to selected context
  selectedContextItems.push(contextItem);
  console.log('üîç DOM TRACE: Selected items after push:', selectedContextItems.length);

  // Update visual indicator
  console.log('üîç DOM TRACE: Calling updateContextIndicator...');
  updateContextIndicator();

  // Mark item as selected
  console.log('üîç DOM TRACE: Marking item as selected');
  item.classList.add('selected');
  console.log('üîç DOM TRACE: Item classes after selection:', item.className);
}

function updateContextIndicator() {
  console.log('üîç DOM TRACE: updateContextIndicator called');
  console.log('üîç DOM TRACE: Selected context items:', selectedContextItems);
  console.log(
    'üîç DOM TRACE: Selected items count:',
    selectedContextItems ? selectedContextItems.length : 'undefined'
  );

  const contextBar = document.querySelector('.terminal-input-context');
  console.log('üîç DOM TRACE: Context bar element:', contextBar);

  if (!contextBar) {
    console.error('üö® DOM TRACE ERROR: Context bar (.terminal-input-context) not found!');
    // Try alternative selectors
    const alternatives = ['.input-container', '.terminal-input-container', '.context-controls'];
    for (const selector of alternatives) {
      const alt = document.querySelector(selector);
      if (alt) {
        console.log(`üîç DOM TRACE: Found alternative container: ${selector}`, alt);
        break;
      }
    }
    return;
  }

  let indicator = contextBar.querySelector('.context-indicator');
  console.log('üîç DOM TRACE: Existing context indicator:', indicator);

  if (!indicator && selectedContextItems && selectedContextItems.length > 0) {
    console.log('üîç DOM TRACE: Creating new context indicator...');
    indicator = document.createElement('div');
    indicator.className = 'context-indicator';
    contextBar.appendChild(indicator);
    console.log('üîç DOM TRACE: Context indicator created and added to DOM');
  }

  if (!selectedContextItems || selectedContextItems.length === 0) {
    console.log('üîç DOM TRACE: No context items, removing indicator if exists');
    if (indicator) {
      indicator.remove();
      console.log('üîç DOM TRACE: Context indicator removed');
    }
    return;
  }

  const count = selectedContextItems.length;
  console.log(`üîç DOM TRACE: Updating indicator with ${count} items`);

  // Log items being displayed
  const displayItems = selectedContextItems.slice(-3);
  console.log('üîç DOM TRACE: Items for chips display:', displayItems);

  const indicatorHTML = `
    <span class="context-count">${count} context${count !== 1 ? 's' : ''}</span>
    <div class="context-chips">
      ${displayItems
        .map((item, index) => {
          const icon = getContextIcon(item.type);
          const chipIndex = selectedContextItems.length - 3 + index;
          console.log(
            `üîç DOM TRACE: Creating chip ${index} for item:`,
            item.type,
            'at index',
            chipIndex
          );
          return `
          <span class="context-chip" data-type="${item.type}">
            ${icon}
            <span class="chip-close" data-index="${chipIndex}">√ó</span>
          </span>
        `;
        })
        .join('')}
    </div>
  `;

  console.log('üîç DOM TRACE: Setting indicator innerHTML...');
  indicator.innerHTML = indicatorHTML;
  console.log('üîç DOM TRACE: Indicator HTML updated');

  // Setup chip close handlers with debugging
  const closeButtons = indicator.querySelectorAll('.chip-close');
  console.log('üîç DOM TRACE: Found', closeButtons.length, 'close buttons for chips');

  closeButtons.forEach((closeBtn, btnIndex) => {
    console.log(`üîç DOM TRACE: Setting up close handler for button ${btnIndex}`);
    closeBtn.addEventListener('click', e => {
      console.log('üîç DOM TRACE: Chip close button clicked:', closeBtn);
      console.log('üîç DOM TRACE: Close button index:', closeBtn.dataset.index);
      e.stopPropagation();
      const index = parseInt(closeBtn.dataset.index);
      console.log('üîç DOM TRACE: Removing item at index:', index);
      console.log('üîç DOM TRACE: Items before removal:', selectedContextItems.length);
      selectedContextItems.splice(index, 1);
      console.log('üîç DOM TRACE: Items after removal:', selectedContextItems.length);
      updateContextIndicator();
    });
  });

  console.log('üîç DOM TRACE: Context indicator update complete');
}

function getContextIcon(type) {
  const icons = {
    file: 'üìÑ',
    screenshot: 'üì∑',
    planning: 'üìã',
    error: 'üî¥',
    guidance: 'üìù',
    terminal: 'üíª',
  };
  return icons[type] || 'üìé';
}

// Show suggestion dropdown
function showSuggestionDropdown(suggestions, inputEl) {
  const existingDropdown = document.querySelector('.terminal-suggestions-dropdown');
  if (existingDropdown) existingDropdown.remove();

  const dropdown = document.createElement('div');
  dropdown.className = 'terminal-suggestions-dropdown cursor-dropdown';
  dropdown.innerHTML = suggestions
    .map(
      suggestion =>
        `<div class="suggestion-item" data-suggestion="${suggestion}">
      <span class="suggestion-text">${suggestion}</span>
    </div>`
    )
    .join('');

  inputEl.parentElement.appendChild(dropdown);

  // Position dropdown
  const rect = inputEl.getBoundingClientRect();
  dropdown.style.position = 'absolute';
  dropdown.style.top = `${rect.bottom + 2}px`;
  dropdown.style.left = `${rect.left}px`;
  dropdown.style.width = `${rect.width}px`;

  // Add click handlers
  dropdown.querySelectorAll('.suggestion-item').forEach(item => {
    item.addEventListener('click', () => {
      inputEl.value = item.dataset.suggestion + ' ';
      dropdown.remove();
      inputEl.focus();
    });
  });

  // Remove on outside click
  setTimeout(() => {
    document.addEventListener(
      'click',
      e => {
        if (!dropdown.contains(e.target)) {
          dropdown.remove();
        }
      },
      { once: true }
    );
  }, 100);
}

// OLD showFileContextDropdown function removed - replaced by unified @ system

// Get uploaded files from global app state
function getUploadedFiles() {
  // This would integrate with your file upload system
  // For now, return mock uploaded files
  return [
    {
      name: 'project-plan.md',
      path: 'uploads/project-plan.md',
      type: 'markdown',
      source: 'uploaded',
    },
    {
      name: 'requirements.txt',
      path: 'uploads/requirements.txt',
      type: 'text',
      source: 'uploaded',
    },
    { name: 'config.json', path: 'uploads/config.json', type: 'json', source: 'uploaded' },
    {
      name: 'trace-report.md',
      path: 'uploads/trace-report.md',
      type: 'markdown',
      source: 'uploaded',
    },
  ];
}

// Filter files based on search and category
function filterFiles(fileList, allFiles, searchTerm, category) {
  let filteredFiles = allFiles;

  // Filter by category
  if (category !== 'all') {
    filteredFiles = filteredFiles.filter(file => file.source === category);
  }

  // Filter by search term
  if (searchTerm) {
    filteredFiles = filteredFiles.filter(
      file =>
        file.name.toLowerCase().includes(searchTerm) || file.path.toLowerCase().includes(searchTerm)
    );
  }

  // Update file list display
  fileList.innerHTML = filteredFiles
    .map(
      file =>
        `<div class="file-context-item" data-filepath="${file.path}" data-category="${file.source}">
      <span class="file-icon">${getFileIcon(file.type)}</span>
      <div class="file-info">
        <div class="file-name">${file.name}</div>
        <div class="file-path">${file.path}</div>
        <div class="file-source">${file.source}</div>
      </div>
    </div>`
    )
    .join('');

  // Re-add click handlers
  fileList.querySelectorAll('.file-context-item').forEach(item => {
    item.addEventListener('click', () => {
      const filepath = item.dataset.filepath;
      const filename = item.querySelector('.file-name').textContent;
      const inputEl = document.getElementById('terminal-input');
      if (inputEl) {
        inputEl.value += `@${filename}(${filepath}) `;
        document.querySelector('.terminal-file-context-dropdown').remove();
        inputEl.focus();
      }
    });
  });

  // Update file count
  const fileCount = fileList.parentElement.querySelector('.file-count');
  if (fileCount) {
    fileCount.textContent = `${filteredFiles.length} files available`;
  }
}

// Update advanced terminal context (fixed function name)
function updateAdvancedTerminalContext(command) {
  // Update context files based on command
  const contextFileItems = document.querySelectorAll('.context-file-item');

  if (command.includes('grep') || command.includes('find')) {
    // Highlight relevant files in context
    contextFileItems.forEach(item => {
      item.classList.add('context-highlight');
      setTimeout(() => item.classList.remove('context-highlight'), 2000);
    });
  }

  // Update context description based on command
  const contextDescription = document.querySelector('.context-description');
  if (contextDescription && command.trim()) {
    contextDescription.textContent = `Last command: ${command} - analyzing file dependencies and trace patterns`;
  }
}

// Get file icon based on type
function getFileIcon(type) {
  const icons = {
    javascript: 'js',
    css: 'css',
    html: 'htm',
    json: 'json',
    markdown: 'md',
    text: 'txt',
    default: 'file',
  };
  return icons[type] || icons.default;
}

// Show error context dropdown
function showErrorContextDropdown(inputEl) {
  const errorLogs = Array.from(document.querySelectorAll('.error-item')).slice(0, 5);

  const dropdown = document.createElement('div');
  dropdown.className = 'terminal-error-context-dropdown cursor-dropdown';
  dropdown.innerHTML = `
    <div class="dropdown-header">Error Context</div>
    ${errorLogs
      .map(
        (error, index) =>
          `<div class="error-context-item" data-error="${index}">
        <span class="error-icon">!</span>
        <div class="error-info">
          <div class="error-message">${error.querySelector('.error-message')?.textContent || 'Unknown error'}</div>
          <div class="error-source">${error.querySelector('.error-source')?.textContent || 'Unknown source'}</div>
        </div>
      </div>`
      )
      .join('')}
  `;

  inputEl.parentElement.appendChild(dropdown);

  // Position dropdown
  const rect = inputEl.getBoundingClientRect();
  dropdown.style.position = 'absolute';
  dropdown.style.top = `${rect.bottom + 2}px`;
  dropdown.style.left = `${rect.left}px`;
  dropdown.style.width = '400px';

  // Add click handlers
  dropdown.querySelectorAll('.error-context-item').forEach(item => {
    item.addEventListener('click', () => {
      const errorMessage = item.querySelector('.error-message')?.textContent;
      inputEl.value += `#error:"${errorMessage}" `;
      dropdown.remove();
      inputEl.focus();
    });
  });

  // Remove on outside click
  setTimeout(() => {
    document.addEventListener(
      'click',
      e => {
        if (!dropdown.contains(e.target)) {
          dropdown.remove();
        }
      },
      { once: true }
    );
  }, 100);
}

// Show command history dropdown
function showCommandHistoryDropdown(inputEl) {
  const commandHistory = ['npm start', 'git status', 'ls -la', 'grep -r "terminal"', 'cd src'];

  const dropdown = document.createElement('div');
  dropdown.className = 'terminal-history-dropdown cursor-dropdown';
  dropdown.innerHTML = `
    <div class="dropdown-header">Command History</div>
    ${commandHistory
      .reverse()
      .map(
        command =>
          `<div class="history-item" data-command="${command}">
        <span class="history-icon">></span>
        <span class="history-command">${command}</span>
      </div>`
      )
      .join('')}
  `;

  inputEl.parentElement.appendChild(dropdown);

  // Position dropdown
  const rect = inputEl.getBoundingClientRect();
  dropdown.style.position = 'absolute';
  dropdown.style.top = `${rect.bottom + 2}px`;
  dropdown.style.left = `${rect.left}px`;
  dropdown.style.width = '300px';

  // Add click handlers
  dropdown.querySelectorAll('.history-item').forEach(item => {
    item.addEventListener('click', () => {
      inputEl.value = item.dataset.command;
      dropdown.remove();
      inputEl.focus();
    });
  });

  // Remove on outside click
  setTimeout(() => {
    document.addEventListener(
      'click',
      e => {
        if (!dropdown.contains(e.target)) {
          dropdown.remove();
        }
      },
      { once: true }
    );
  }, 100);
}

// Show context add dialog
function showContextAddDialog() {
  const existingDialog = document.querySelector('.terminal-context-dialog');
  if (existingDialog) existingDialog.remove();

  const dialog = document.createElement('div');
  dialog.className = 'terminal-context-dialog cursor-dialog';
  dialog.innerHTML = `
    <div class="dialog-content">
      <div class="dialog-header">
        <span class="dialog-title">Add Context</span>
        <button class="dialog-close">x</button>
      </div>
      <div class="dialog-body">
        <div class="context-type-selector">
          <button class="context-type-btn active" data-type="file">Files</button>
          <button class="context-type-btn" data-type="error">Errors</button>
          <button class="context-type-btn" data-type="plan">Plans</button>
          <button class="context-type-btn" data-type="live">Live Data</button>
        </div>
        <div class="context-content-area">
          <textarea class="context-textarea" placeholder="Add context information..."></textarea>
        </div>
        <div class="dialog-actions">
          <button class="btn-cancel">Cancel</button>
          <button class="btn-add">Add Context</button>
        </div>
      </div>
    </div>
    <div class="dialog-overlay"></div>
  `;

  document.body.appendChild(dialog);

  // Add event handlers
  dialog.querySelector('.dialog-close').addEventListener('click', () => dialog.remove());
  dialog.querySelector('.btn-cancel').addEventListener('click', () => dialog.remove());
  dialog.querySelector('.dialog-overlay').addEventListener('click', () => dialog.remove());
}

