/* =========================================================================
   PHASE BUTTON STYLING - SESSION 2 IMPLEMENTATION
   Based on MVP/sources with GTST compliance
   Chicago Time: 2025-08-13 15:58:00 CST
   ========================================================================= */

/* Phase button specific styling extending btn-41 */
#phase-btn.tiny-btn {
  min-width: 32px;
  height: 32px;
  padding: 4px 8px;
  font-size: 14px;
  border-radius: 6px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border: 1px solid rgba(102, 126, 234, 0.3);
  color: white;
  position: relative;
  overflow: hidden;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

#phase-btn.tiny-btn:hover {
  transform: translateY(-1px) scale(1.05);
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
  background: linear-gradient(135deg, #7c8ef0 0%, #8458b3 100%);
}

#phase-btn.tiny-btn:active {
  transform: translateY(0) scale(0.98);
  box-shadow: 0 2px 6px rgba(102, 126, 234, 0.2);
}

/* Phase button executing state */
#phase-btn.executing {
  animation: phaseButtonPulse 1.5s infinite;
  background: linear-gradient(135deg, #9b59b6 0%, #8e44ad 100%);
  opacity: 0.8;
}

@keyframes phaseButtonPulse {
  0% { 
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(155, 89, 182, 0.7);
  }
  50% { 
    transform: scale(1.05);
    box-shadow: 0 0 0 8px rgba(155, 89, 182, 0.1);
  }
  100% { 
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(155, 89, 182, 0);
  }
}

/* Phase selection modal styling */
.phase-selection-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: 1000;
  display: flex;
  align-items: center;
  justify-content: center;
}

.phase-selection-modal .modal-overlay {
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.7);
  backdrop-filter: blur(4px);
  display: flex;
  align-items: center;
  justify-content: center;
}

.phase-selection-modal .modal-content {
  background: var(--background-color);
  border-radius: 12px;
  padding: 24px;
  min-width: 480px;
  max-width: 600px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.phase-selection-modal .modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
  padding-bottom: 12px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.phase-selection-modal .modal-header h3 {
  margin: 0;
  font-size: 18px;
  font-weight: 600;
  color: var(--text-color);
}

.phase-selection-modal .close-btn {
  background: none;
  border: none;
  color: var(--text-color);
  font-size: 24px;
  cursor: pointer;
  padding: 4px;
  border-radius: 4px;
  transition: background-color 0.2s;
}

.phase-selection-modal .close-btn:hover {
  background: rgba(255, 255, 255, 0.1);
}

/* Phase templates grid */
.phase-templates {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 16px;
  margin-top: 20px;
}

.phase-template {
  display: flex;
  align-items: center;
  padding: 16px;
  border: 2px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  background: rgba(255, 255, 255, 0.02);
  position: relative;
  overflow: hidden;
}

.phase-template::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
  opacity: 0;
  transition: opacity 0.2s;
  z-index: 0;
}

.phase-template:hover::before {
  opacity: 1;
}

.phase-template:hover {
  border-color: rgba(102, 126, 234, 0.5);
  transform: translateY(-2px);
  box-shadow: 0 4px 16px rgba(102, 126, 234, 0.2);
}

.phase-template:active {
  transform: translateY(0);
  box-shadow: 0 2px 8px rgba(102, 126, 234, 0.1);
}

.phase-template i {
  margin-right: 12px;
  font-size: 20px;
  color: var(--accent-color);
  z-index: 1;
  position: relative;
}

.phase-template span {
  font-size: 14px;
  font-weight: 500;
  color: var(--text-color);
  z-index: 1;
  position: relative;
}

/* Phase execution output styling */
.terminal-output.phase-output {
  border-left: 3px solid #667eea;
  padding-left: 12px;
  margin: 4px 0;
  background: rgba(102, 126, 234, 0.05);
}

.terminal-output.phase-output .timestamp {
  color: rgba(255, 255, 255, 0.6);
  font-size: 11px;
}

.terminal-output.phase-output .source {
  color: #667eea;
  font-weight: 500;
  font-size: 11px;
}

.terminal-output.phase-output .message {
  color: var(--text-color);
  margin-left: 8px;
}

/* Phase screenshot styling in planning tab */
.screenshot-item.phase-screenshot {
  border: 1px solid rgba(102, 126, 234, 0.3);
  background: rgba(102, 126, 234, 0.05);
}

.screenshot-item.phase-screenshot .screenshot-type {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 2px 8px;
  border-radius: 12px;
  font-size: 11px;
  font-weight: 500;
}

/* Full screen screenshot modal */
.screenshot-fullscreen-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: 10000;
}

.fullscreen-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(2px);
}

.fullscreen-content {
  max-width: 90vw;
  max-height: 90vh;
  position: relative;
}

.close-fullscreen {
  position: absolute;
  top: 20px;
  right: 20px;
  background: rgba(255, 255, 255, 0.2);
  border: none;
  color: white;
  font-size: 24px;
  cursor: pointer;
  padding: 12px;
  border-radius: 6px;
  transition: background-color 0.2s;
}

.close-fullscreen:hover {
  background: rgba(255, 255, 255, 0.3);
}

/* Phase button integration with existing status indicators */
#phase-btn.tiny-btn + .status-indicators {
  margin-left: 4px;
}

/* Responsive adjustments for phase button */
@media (max-width: 768px) {
  .phase-templates {
    grid-template-columns: 1fr;
  }

  .phase-selection-modal .modal-content {
    min-width: 90vw;
    margin: 20px;
  }

  #phase-btn.tiny-btn {
    min-width: 28px;
    height: 28px;
    padding: 3px 6px;
  }
}

