/* Ensure all buttons across the app use consistent styling */
button:not(.btn-41) {
  font-family:
    'SF Mono', 'Monaco', 'Inconsolata', 'Fira Code', 'Consolas', 'Liberation Mono', monospace;
}

/* Tab button specific styling */
.btn-41.tab-btn {
  font-size: 11px;
  padding: 5px 8px;
  height: 26px;
}

.btn-41.tab-btn.active {
  background: linear-gradient(180deg, #8a8a9d, #7c7c8a);
  border-color: #6e6e7b;
}

/* Filter button specific styling */
.btn-41.filter-btn {
  font-size: 10px;
  padding: 4px 6px;
  height: 24px;
}

.btn-41.filter-btn.active {
  background: linear-gradient(180deg, #8a8a9d, #7c7c8a);
  border-color: #6e6e7b;
}


/* UNIFIED BTN-41 VARIANTS */

/* Tiny variant - for compact spaces */
.btn-41.tiny {
  font-size: 10px;
  padding: 2px 6px;
  min-height: 20px;
  gap: 3px;
}

.btn-41.tiny i {
  font-size: 10px;
  opacity: 0.8;
}

/* Muted variant - for subtle actions */
.btn-41.muted {
  color: rgba(255, 255, 255, 0.5);
  background: rgba(255, 255, 255, 0.02);
  border-color: rgba(255, 255, 255, 0.05);
}

.btn-41.muted:hover {
  color: rgba(255, 255, 255, 0.7);
  background: rgba(255, 255, 255, 0.05);
  border-color: rgba(255, 255, 255, 0.1);
}
/* REMOVED: Old terminal control button styling - replaced with Cursor-style implementation */

/* Footer toggle button styling */
.btn-41.footer-toggle {
  font-size: 10px;
  padding: 4px 6px;
  height: 22px;
  min-width: 22px;
}

/* Cursor-like Status Button Styling */
.btn-41.status-btn {
  font-size: var(--cursor-font-tiny);
  font-family: var(--cursor-font-family);
  padding: 2px 6px;
  height: 20px;
  border-radius: var(--am-border-radius-small);
  background: var(--am-bg-tertiary);
  border: 1px solid var(--am-border-color);
  color: var(--am-text-secondary);
  display: inline-flex;
  align-items: center;
  gap: 4px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.btn-41.status-btn:hover {
  background: var(--am-hover-bg);
  border-color: var(--am-accent-blue);
  color: var(--am-text-primary);
}

.btn-41.status-btn .status-icon {
  font-size: var(--cursor-font-xs);
  line-height: 1;
}

.btn-41.status-btn .status-text {
  font-size: var(--cursor-font-tiny);
  font-weight: 400;
  letter-spacing: 0.01em;
}

/* Footer status buttons - even tinier */
.btn-41.status-btn.footer-status {
  font-size: var(--cursor-font-tiny);
  padding: 1px 4px;
  height: 18px;
  gap: 2px;
}

.btn-41.status-btn.footer-status .status-icon,
.btn-41.status-btn.footer-status .connection-icon {
  font-size: 9px;
}

.btn-41.status-btn.footer-status .status-text,
.btn-41.status-btn.footer-status .connection-count,
.btn-41.status-btn.footer-status .connection-status {
  font-size: 9px;
}

/* Modal close button styling */
.btn-41.modal-close {
  font-size: 12px;
  padding: 4px 6px;
  height: 24px;
  min-width: 24px;
  border-radius: 4px;
}

/* Webview Browser Interface Styling */
.webview-browser-interface {
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  background: var(--am-bg-secondary);
  border-radius: 8px;
  overflow: hidden;
}

/* =========================== */
/* Cursor-like Toolbar Refinements (Unified) */
/* Applies to Strike Errors & Files (left) and Tools & Terminal (right) */
/* Goal: fewer visible borders, consistent spacing, muted fonts */

#leftToolbar .toolbar-content,
#rightToolbar .toolbar-content {
  display: flex;
  flex-direction: column;
  gap: var(--am-spacing-md);
}

#leftToolbar .toolbar-toggle,
#rightToolbar .toolbar-toggle {
  display: grid;
  grid-template-columns: auto 1fr auto;
  align-items: center;
  gap: var(--am-spacing-sm);
  padding: 6px 8px;
  background: var(--am-bg-secondary);
  border: 1px solid var(--am-border-color);
  border-radius: var(--am-border-radius);
}

.left-toolbar,
.right-toolbar {
  background: var(--am-bg-secondary);
  color: var(--am-text-secondary);
  padding: var(--am-spacing-md);
  border-radius: var(--am-border-radius);
  /* Single subtle outline; remove interior pane dividers */
  border: 1px solid var(--am-border-color);
}

/* Headings muted but readable */
#leftToolbar .section-header h3,
#rightToolbar .section-header h3 {
  color: var(--am-text-primary);
  font-weight: 500;
  letter-spacing: 0.3px;
  margin: 0 0 var(--am-spacing-sm) 0;
}

/* Remove extra borders inside toolbars */
#leftToolbar .section-subtitle,
#rightToolbar .section-subtitle,
#leftToolbar .mermaid-section,
#rightToolbar .mermaid-section,
#leftToolbar .session-list,
#rightToolbar .session-list,
#leftToolbar .suggestions-list,
#rightToolbar .suggestions-list {
  border: none;
  background: var(--am-bg-secondary);
}

/* Button spacing in toolbars */
#leftToolbar .btn-41,
#rightToolbar .btn-41 {
  margin: 0;
}
#leftToolbar .btn-41 + .btn-41,
#rightToolbar .btn-41 + .btn-41 {
  margin-left: var(--am-spacing-sm);
}

/* Footer-style micro status buttons inside toolbars stay tiny and muted */
#leftToolbar .status-btn .status-icon,
#rightToolbar .status-btn .status-icon {
  color: var(--am-text-secondary);
}

/* No green policy: use muted text for "success" states */
.btn-41.status-btn .status-icon.success {
  color: var(--am-text-secondary);
}
.btn-41.status-btn .status-icon.danger {
  color: var(--am-accent-red);
}

/* Override any green dots used by generic status utilities inside toolbars */
#leftToolbar .status-idle.active::before,
#rightToolbar .status-idle.active::before,
#leftToolbar .status-panel.active::before,
#rightToolbar .status-panel.active::before,
#leftToolbar .status-text.active::before,
#rightToolbar .status-text.active::before {
  color: var(--am-text-secondary);
}

/* Reduce visible borders for list/containers within toolbars */
#leftToolbar .error-log-container,
#rightToolbar .error-log-container,
#leftToolbar .screenshots-container,
#rightToolbar .screenshots-container {
  border-color: var(--am-border-color);
}

/* Muted general text inside both toolbars */
#leftToolbar,
#rightToolbar {
  color: var(--am-text-secondary);
}

/* Icon sizing utilities for justified tiny icons */
.icon-xxs {
  font-size: 10px;
  line-height: 1;
}
.icon-xs {
  font-size: 12px;
  line-height: 1;
}

/* Right toolbar tab navigation: compact, justified, non-overlapping */
#rightToolbar .tab-navigation {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(88px, 1fr));
  gap: 6px;
}

#rightToolbar .tab-navigation .tab-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 6px;
  height: 22px;
  padding: 4px 6px;
  font-size: var(--cursor-font-tiny);
  border: 1px solid var(--am-border-color);
  background: var(--am-bg-tertiary);
  color: var(--am-text-secondary);
  border-radius: var(--am-border-radius);
  overflow: hidden;
}

#rightToolbar .tab-navigation .tab-btn i {
  font-size: 12px;
}
#rightToolbar .tab-navigation .tab-btn span {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

/* When viewport is narrow, switch to icons-only tab buttons */
@media (max-width: 1280px) {
  #rightToolbar .tab-navigation .tab-btn span {
    display: none;
  }
}

/* Left toolbar analysis/status rows: prevent overlap and justify spacing */
#leftToolbar .status-indicator-panel,
#leftToolbar .quick-actions,
#leftToolbar .progress-container,
#leftToolbar .score-grid,
#leftToolbar .diagram-controls,
#rightToolbar .screenshot-controls,
#rightToolbar .error-controls {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--am-spacing-sm);
  flex-wrap: wrap;
}

/* Left tabs (Strike / Files / Diagram) */
.left-tabs {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 6px;
  margin-bottom: var(--am-spacing-sm);
}
.left-tabs .tab-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 6px;
  height: 22px;
  padding: 4px 6px;
  font-size: var(--cursor-font-tiny);
  border: 1px solid var(--am-border-color);
  background: var(--am-bg-tertiary);
  color: var(--am-text-secondary);
  border-radius: var(--am-border-radius);
}
.left-tabs .tab-btn.active {
  background: var(--am-bg-secondary);
  color: var(--am-text-primary);
}

/* Left tab content visibility */
.left-tab-content {
  display: none;
}
.left-tab-content.active {
  display: block;
}
.files-diagram {
  margin-top: 8px;
  padding: 6px;
  background: var(--am-bg-secondary);
  border: 1px solid var(--am-border-color);
  border-radius: var(--am-border-radius);
  max-height: 240px;
  overflow: auto;
}

/* Diagram tab controls layout */
.diagram-tabs {
  display: grid;
  grid-template-columns: repeat(4, minmax(72px, 1fr));
  gap: 6px;
}
.diagram-tabs .tab-btn {
  height: 22px;
  padding: 4px 6px;
}
.diagram-actions {
  display: flex;
  gap: 6px;
  align-items: center;
}

/* Tiny badges inside Files tab */
.badge-xxs {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 14px;
  min-width: 18px;
  padding: 0 4px;
  margin-left: 4px;
  border: 1px solid var(--am-border-color);
  border-radius: 10px;
  background: var(--am-bg-tertiary);
  color: var(--am-text-secondary);
  font-size: 10px;
  line-height: 1;
}
.badge-muted-xxs {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 14px;
  padding: 0 6px;
  margin-left: 4px;
  border: 1px dashed var(--am-border-color);
  border-radius: 10px;
  background: var(--am-bg-secondary);
  color: var(--am-text-secondary);
  font-size: 10px;
  line-height: 1;
}

/* Status row above tabs */
.left-toolbar-status-row {
  display: grid;
  grid-template-columns: auto 1fr auto;
  align-items: center;
  gap: var(--am-spacing-sm);
  margin-bottom: var(--am-spacing-sm);
}
.left-toolbar-status-row .status-badges {
  display: inline-flex;
  gap: 6px;
  align-items: center;
}
.left-toolbar-status-row .left-actions {
  display: inline-flex;
  gap: 6px;
  align-items: center;
}

/* Generic tiny status dot (emoji-free) */
.status-dot-tiny {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: var(--am-text-secondary);
  display: inline-block;
}

/* =========================== */

.webview-browser-controls {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 8px 12px;
  background: var(--am-bg-primary);
  border-bottom: 1px solid var(--am-border-color);
  font-family:
    'SF Mono', 'Monaco', 'Inconsolata', 'Fira Code', 'Consolas', 'Liberation Mono', monospace;
}

.browser-navigation {
  display: flex;
  gap: 4px;
}

.address-bar-container {
  flex: 1;
  display: flex;
  gap: 8px;
  align-items: center;
  max-width: 600px;
}

.address-bar {
  flex: 1;
  padding: 6px 12px;
  border: 1px solid var(--am-border-color);
  border-radius: 6px;
  background: var(--am-bg-tertiary);
  color: var(--am-text-primary);
  font-family:
    'SF Mono', 'Monaco', 'Inconsolata', 'Fira Code', 'Consolas', 'Liberation Mono', monospace;
  font-size: 13px;
}

.address-bar:focus {
  outline: 2px solid var(--am-accent-blue);
  border-color: var(--am-accent-blue);
}

.webview-tab-controls {
  display: flex;
  gap: 8px;
  align-items: center;
}

.webview-tabs {
  display: flex;
  gap: 2px;
  max-width: 300px;
  overflow-x: auto;
}

.webview-tab {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 6px 10px;
  background: var(--am-bg-tertiary);
  border: 1px solid var(--am-border-color);
  border-radius: 6px 6px 0 0;
  cursor: pointer;
  font-size: 12px;
  max-width: 150px;
  font-family:
    'SF Mono', 'Monaco', 'Inconsolata', 'Fira Code', 'Consolas', 'Liberation Mono', monospace;
}

.webview-tab.active {
  background: var(--am-bg-secondary);
  border-bottom-color: var(--am-bg-secondary);
}

.webview-tab .tab-title {
  flex: 1;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.webview-tab .tab-close {
  background: none;
  border: none;
  color: var(--am-text-secondary);
  cursor: pointer;
  padding: 0;
  width: 16px;
  height: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 3px;
  font-size: 14px;
}

.webview-tab .tab-close:hover {
  background: var(--am-bg-primary);
  color: var(--am-text-primary);
}

.webview-container {
  flex: 1;
  position: relative;
  background: var(--am-bg-secondary);
}

.webview-fallback {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: white;
}

.webview-fallback.hidden {
  display: none;
}

/* .chrome-container and its states consolidated above for CSS specificity ordering */

.chrome-placeholder {
  position: absolute;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--am-bg-primary);
}

.placeholder-content {
  text-align: center;
  color: #9b9a97;
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
}

.chrome-webview {
  width: 100%;
  height: 100%;
  border-radius: 8px;
  flex: 1;
  border: none;
  background: var(--am-bg-primary);
}

.chrome-info {
  margin-top: 12px;
  font-size: 0.875rem;
  color: #9b9a97;
  font-weight: 500;
}

/* Side Panel consolidated above for CSS specificity ordering */

.side-panel:hover {
  box-shadow: -2px 0 8px rgb(0 0 0 / 8%);
}

/* Reset text direction for the content inside the right sidebar */
.side-panel > * {
  direction: ltr;
}

/* Tab Navigation */
.tab-navigation {
  display: flex;
  background: var(--am-sidebar-bg);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
  border-bottom: 1px solid var(--am-border-color);
  padding: 0;
}

.tab-btn {
  flex: 1;
  padding: var(--am-spacing-md) var(--am-spacing-sm);
  border: none;
  background: transparent;
  color: var(--am-text-muted);
  font-size: 13px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.15s ease;
  border-bottom: 2px solid transparent;
  position: relative;
  z-index: 10;
  pointer-events: auto;
  user-select: none;
  font-family: -apple-system, BlinkMacSystemFont, 'SF Pro Text', system-ui, sans-serif;
  letter-spacing: -0.01em;
}

.tab-btn:hover {
  background: var(--am-hover-bg);
  color: var(--am-text-primary);
}

.tab-btn.active {
  color: var(--am-accent-blue);
  border-bottom-color: var(--am-accent-blue);
  background: var(--am-selected-bg);
}

.tab-btn.active::after {
  content: '';
  position: absolute;
  bottom: -1px;
  left: 0;
  right: 0;
  height: 1px;
  background: var(--am-sidebar-bg);
}

/* Tab Content */
.tab-content {
  flex: 1;
  display: none;
  flex-direction: column;
  overflow: hidden;
  height: 100%;
  width: 100%;
  position: relative;
}

.tab-content.active {
  display: flex !important;
  visibility: visible;
  opacity: 1;
}

/* Enhanced scrolling for tab content */
.tab-content > * {
  overflow-y: auto;
  scrollbar-width: thin;
  scrollbar-color: #d3d1cb transparent;
}

.tab-content > *::-webkit-scrollbar {
  width: 6px;
}

.tab-content > *::-webkit-scrollbar-track {
  background: transparent;
}

.tab-content > *::-webkit-scrollbar-thumb {
  background: #d3d1cb;
  border-radius: 3px;
  transition: background 0.15s ease;
}

.tab-content > *::-webkit-scrollbar-thumb:hover {
  background: #9b9a97;
}

/* Planning Guidance Section */
.planning-section {
  flex: 1;
  display: flex;
  flex-direction: column;
  border-bottom: 1px solid var(--am-border-color);
  background: rgba(30, 30, 30, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  margin-bottom: 16px;
}

.file-import {
  padding: 12px;
  background: rgba(255, 255, 255, 0.02);
  border: none;
  border-radius: 6px;
  margin: 12px;
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
  align-items: center;
}

.planning-section .section-header {
  padding: 12px 12px 8px 12px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  margin-bottom: 0;
}

.planning-section .section-header h3 {
  font-size: 13px;
  font-weight: 600;
  color: rgba(0, 120, 212, 1);
  margin: 0;
}

.imported-files {
  margin-top: 16px;
  max-height: 150px;
  overflow-y: auto;
}

.file-item {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 10px 12px;
  background: rgba(40, 40, 40, 0.8);
  border: 1px solid #e9e9e7;
  border-radius: 6px;
  margin-bottom: 8px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.file-item:hover {
  background: rgba(50, 50, 50, 0.9);
  border-color: #d3d1cb;
}

.file-item.template {
  border-color: #0f70b8;
  background: rgb(15 112 184 / 5%);
}

.file-icon {
  font-size: 1rem;
  opacity: 0.7;
}

.file-name {
  flex: 1;
  font-size: 0.875rem;
  font-weight: 500;
  color: #37352f;
}

.file-status {
  font-size: 0.875rem;
}

.guidance-preview {
  flex: 1;
  padding: 12px;
  background: transparent;
  margin: 0 12px 12px 12px;
}

.guidance-preview textarea {
  width: 100%;
  height: 120px;
  background: rgba(40, 40, 40, 0.8);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 6px;
  padding: 12px;
  color: rgba(255, 255, 255, 0.9);
  font-family: 'SF Mono', Monaco, 'Cascadia Code', monospace;
  font-size: 12px;
  line-height: 1.5;
  resize: vertical;
  resize: none;
  transition: border-color 0.15s ease;
}

/* Standardized form elements */
input,
textarea,
select {
  background: var(--am-bg-secondary);
  border: 1px solid var(--am-border-color);
  border-radius: var(--am-border-radius-small);
  color: var(--am-text-primary);
  font-family: -apple-system, BlinkMacSystemFont, 'SF Pro Text', system-ui, sans-serif;
  font-size: 13px;
  padding: var(--am-spacing-sm) var(--am-spacing-md);
  transition: all 0.15s ease;
}

input:focus,
textarea:focus,
button:focus,
select:focus {
  outline: none;
  border-color: var(--am-accent-blue);
  box-shadow: 0 0 0 2px rgba(0, 122, 255, 0.2);
}

.message-compose textarea {
  width: 100%;
  background: var(--am-bg-secondary);
  border: 1px solid var(--am-border-color);
  border-radius: var(--am-border-radius);
  padding: var(--am-spacing-lg);
  color: var(--am-text-primary);
  font-family: inherit;
  font-size: 13px;
  line-height: 1.5;
  resize: vertical;
  margin-bottom: var(--am-spacing-md);
  transition: border-color 0.15s ease;
}

.guidance-preview textarea:focus {
  outline: none;
  border-color: #0f70b8;
  box-shadow: 0 0 0 3px rgb(15 112 184 / 10%);
}

/* User Input Section */
.user-input-section {
  flex: 1;
  display: flex;
  flex-direction: column;
  background: var(--am-bg-primary);
}

.message-compose {
  padding: 20px;
  border-bottom: 1px solid #e9e9e7;
}

/* .message-compose textarea consolidated above for CSS specificity ordering */

.message-compose textarea:focus {
  outline: none;
  border-color: #0f70b8;
  box-shadow: 0 0 0 3px rgb(15 112 184 / 10%);
}

.message-compose textarea::placeholder {
  color: #9b9a97;
}

.message-controls {
  display: flex;
  gap: 8px;
}

.message-queue {
  flex: 1;
  padding: 20px;
  overflow-y: auto;
  scrollbar-width: thin;
  scrollbar-color: #d3d1cb transparent;
  max-height: calc(100vh - 300px);
}

.message-queue::-webkit-scrollbar {
  width: 6px;
}

.message-queue::-webkit-scrollbar-track {
  background: transparent;
}

.message-queue::-webkit-scrollbar-thumb {
  background: #d3d1cb;
  border-radius: 3px;
  transition: background 0.15s ease;
}

.message-queue::-webkit-scrollbar-thumb:hover {
  background: #9b9a97;
}

/* .message-queue h4 consolidated above for CSS specificity ordering */

.queued-messages {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.message-item:hover {
  background: rgba(50, 50, 50, 0.9);
  border-left-color: #0d5fa3;
}

.message-item.urgent {
  border: 1px solid #fecaca;
  border-left-color: #ef4444;
  background: #fef2f2;
}

.message-item.urgent:hover {
  background: #fde8e8;
}

/* Bottom Panel - DISABLED (conflicts with status-footer) */
.bottom-panel {
  /* grid-area: footer; - REMOVED to prevent conflicts */
