      'header header'
      'viewport sidebar'
      'footer footer';
  }

  .performance-toolbar {
    display: none;
  }
}

@media (width <= 600px) {
  .container {
    grid-template:
      'header' auto
      'viewport' 1fr
      'footer' auto /
      1fr;
    grid-template-areas:
      'header'
      'viewport'
      'footer';
  }

  .side-panel {
    display: none;
  }

  .header {
    flex-direction: column;
    gap: 8px;
    align-items: flex-start;
  }

  .status-indicators {
    flex-direction: column;
    gap: 4px;
    align-items: flex-start;
    width: 100%;
  }

  #header-screenshot-btn {
    align-self: flex-end;
    margin-left: 0 !important;
  }
}

/* Command History Indicator */
.command-history-indicator {
  position: absolute;
  right: 16px;
  top: 50%;
  transform: translateY(-50%);
  color: #6a9955;
  font-size: 11px;
  opacity: 0.7;
}

/* Screenshots Section Styles */
.screenshots-section {
  padding: 12px;
  background: rgba(30, 30, 30, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  margin: 16px 12px;
}

.screenshots-section .section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 12px;
  padding-bottom: 8px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.screenshots-section .section-header h3 {
  font-size: 13px;
  font-weight: 600;
  color: rgba(0, 120, 212, 1);
  margin: 0;
}

.screenshot-controls {
  display: flex;
  align-items: center;
  gap: 10px;
}

.screenshot-count {
  font-size: 11px;
  color: rgba(255, 255, 255, 0.7);
  background: rgba(0, 120, 212, 0.2);
  padding: 3px 8px;
  border-radius: 12px;
  font-weight: 500;
}

.screenshots-container {
  max-height: 300px;
  overflow-y: auto;
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 6px;
  background: rgba(40, 40, 40, 0.5);
  padding: 8px;
}

.screenshots-list {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(80px, 1fr));
  gap: 8px;
}

.screenshot-thumbnail {
  width: 80px;
  height: 60px;
  object-fit: cover;
  border-radius: 4px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  cursor: pointer;
  transition:
    transform 0.2s,
    border-color 0.2s;
}

.screenshot-thumbnail:hover {
  transform: scale(1.05);
  border-color: rgba(0, 120, 212, 0.5);
}

.no-screenshots {
  text-align: center;
  padding: 30px 20px;
  color: rgba(255, 255, 255, 0.5);
  font-size: 12px;
}

.no-screenshots-icon {
  font-size: 32px;
  display: block;
  margin-bottom: 10px;
  opacity: 0.6;
}

.no-screenshots p {
  margin: 0 0 5px;
  font-size: 14px;
  color: #666;
}

.no-screenshots small {
  font-size: 12px;
  opacity: 0.7;
}

.screenshot-item:hover {
  background: var(--am-bg-secondary);
}

.screenshot-item:last-child {
  border-bottom: none;
}

.screenshot-header:hover {
  background: rgb(0 0 0 / 2%);
}

.screenshot-toggle.expanded {
  transform: rotate(90deg);
}

.screenshot-content.expanded {
  display: block;
}

@keyframes slide-down {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.screenshot-image:hover {
  transform: scale(1.02);
  box-shadow: 0 4px 20px rgb(0 0 0 / 12%);
}

.screenshot-path code {
  background: rgb(0 0 0 / 10%);
  padding: 2px 6px;
  border-radius: 3px;
  font-family: 'SF Mono', Monaco, monospace;
  font-size: 11px;
  color: #06c;
  cursor: pointer;
  transition: all 0.2s ease;
  user-select: all;
}

.screenshot-path code:hover {
  background: rgb(0 102 204 / 10%);
  color: #049;
}

.screenshot-btn:hover {
  background: #e9ecef;
  border-color: #ced4da;
  color: #343a40;
}

.screenshot-btn:active {
  background: #dee2e6;
  transform: translateY(1px);
}

.screenshot-content img {
  max-width: 100%;
  height: auto;
  border-radius: 6px;
  border: 1px solid #e9e9e7;
  box-shadow: 0 2px 12px rgb(0 0 0 / 8%);
  transition: transform 0.2s ease;
  display: block;
}

.screenshot-content img:hover {
  transform: scale(1.02);
  box-shadow: 0 4px 20px rgb(0 102 204 / 15%);
}

.btn-sm {
  padding: 6px 12px;
  font-size: 11px;
  border-radius: 4px;
  font-weight: 500;
}

/* .file-import merged with earlier definition */

/* ===== STRIKE ERRORS EXPANDABLE TOOLBAR ===== */

.strike-errors-toolbar:not(.collapsed) {
  width: 280px;
}

.strike-errors-toolbar.collapsed .toolbar-content {
  opacity: 0;
  visibility: hidden;
}

.strike-errors-toolbar:not(.collapsed) .toolbar-content {
  opacity: 1;
  visibility: visible;
  transition:
    opacity 0.3s ease 0.1s,
    visibility 0.3s ease 0.1s;
}

.toolbar-toggle {
  display: flex;
  align-items: center;
  height: 40px;
  gap: 8px;
  padding: 0 8px;
  cursor: pointer;
  color: white;
  transition: all 0.2s ease;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.toolbar-toggle:hover {
  background: rgba(255, 255, 255, 0.1);
}

.toggle-icon {
  font-size: 20px;
  margin-right: 10px;
  min-width: 24px;
  text-align: center;
}

.toggle-text {
  font-weight: 600;
  font-size: 14px;
  white-space: nowrap;
}

.toggle-arrow {
  margin-left: auto;
  font-size: 12px;
  transition: transform 0.3s ease;
}

.strike-errors-toolbar:not(.collapsed) .toggle-arrow {
  transform: rotate(90deg);
}

.toolbar-content {
  padding: 20px;
  height: calc(100% - 70px);
  overflow-y: auto;
  scrollbar-width: thin;
  scrollbar-color: rgba(255, 255, 255, 0.2) transparent;
}

.toolbar-header {
  margin-bottom: 25px;
  text-align: center;
  color: white;
}

.toolbar-header h3 {
  font-size: 18px;
  font-weight: 700;
  margin-bottom: 5px;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
}

.toolbar-subtitle {
  font-size: 12px;
  opacity: 0.8;
  font-weight: 400;
}

/* Strike Errors Sections */
.quick-actions,
.analysis-status,
.health-scores,
.recent-results,
.status-header,
.score-header,
.results-header,
.config-header {
  margin-bottom: 12px;
}

.status-header h4,
.score-header h4,
.results-header h4,
.config-header h4 {
  color: white;
  font-size: 14px;
  font-weight: 600;
  margin: 0;
}

/* Strike Buttons */
.strike-btn {
  width: 100%;
  padding: 10px 15px;
  border: none;
  border-radius: 6px;
  font-weight: 600;
  font-size: 13px;
  cursor: pointer;
  transition: all 0.2s ease;
  margin-bottom: 8px;
  text-align: center;
}

.strike-btn.primary {
  background: linear-gradient(45deg, #dc3545, #e74c3c);
  color: white;
  box-shadow: 0 2px 8px rgba(220, 53, 69, 0.3);
}

.strike-btn.primary:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(220, 53, 69, 0.4);
}

.strike-btn.secondary {
  background: rgba(255, 255, 255, 0.2);
  color: white;
  border: 1px solid rgba(255, 255, 255, 0.3);
}

.strike-btn.secondary:hover {
  background: rgba(255, 255, 255, 0.3);
  transform: translateY(-1px);
}

/* Status Indicators */
.status-indicator-panel {
  color: white;
}

.status-dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  margin-right: 8px;
  background: #6c757d;
  transition: background 0.3s ease;
}

.status-dot.idle {
  background: #6c757d;
}
.status-dot .status-dot .status-dot.complete {
  background: #8a8a9d;
}
.status-dot.error {
  background: #dc3545;
}

@keyframes pulse {
  0% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
  100% {
    opacity: 1;
  }
}

.progress-container {
  margin-top: 10px;
}

.progress-bar {
  height: 4px;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 2px;
  overflow: hidden;
  margin-bottom: 5px;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #007bff, #0056b3);
  width: 0%;
  transition: width 0.3s ease;
}

.progress-text {
  font-size: 11px;
  color: rgba(255, 255, 255, 0.8);
  text-align: right;
}

/* Health Scores Grid */
.score-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 10px;
}

.score-item {
  text-align: center;
  padding: 8px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 4px;
}

.score-label {
  font-size: 10px;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 4px;
}

.score-value {
  font-size: 16px;
  font-weight: 700;
  color: white;
}

/* Results List */
.results-list {
  color: white;
}

.no-results {
  text-align: center;
  padding: 15px;
  color: rgba(255, 255, 255, 0.7);
}

.no-results-icon {
  font-size: 24px;
  display: block;
  margin-bottom: 8px;
}

.no-results p {
  font-size: 13px;
  margin-bottom: 4px;
}

.no-results small {
  font-size: 11px;
  opacity: 0.8;
}

/* Configuration Options */
.config-options {
  color: white;
}

.config-item {
  margin-bottom: 12px;
}

.config-item label {
  display: flex;
  align-items: center;
  font-size: 12px;
  cursor: pointer;
}

.config-item input[type='checkbox'] {
  margin-right: 8px;
  width: 14px;
  height: 14px;
}

.config-item input[type='number'] {
  width: 60px;
  padding: 4px 6px;
  border: 1px solid rgba(255, 255, 255, 0.3);
  background: rgba(255, 255, 255, 0.1);
  color: white;
  border-radius: 3px;
  font-size: 11px;
  margin-left: 8px;
}

/* Main content adjustment for toolbar */
.main-content {
  margin-left: 50px;
  transition: margin-left 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.strike-errors-toolbar:not(.collapsed) + .main-content {
  margin-left: 280px;
}

/* ===== THIN STATUS FOOTER ===== */
.status-footer {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 30px;
  background: #2c2c2c;
  border-top: 1px solid #3e3e3e;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 15px;
  font-size: 11px;
  color: #d4d4d4;
  z-index: 1001;
  font-family:
    'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace;
}

.status-left,
.status-center,
.status-right {
  display: flex;
  align-items: center;
  gap: 15px;
}

.status-left {
  flex: 1;
}

.status-center {
  flex: 0;
}

.status-right {
  flex: 1;
  justify-content: flex-end;
}

/* Compact Metrics */
.metric-compact {
  display: flex;
  align-items: center;
  gap: 4px;
}

.metric-compact .metric-icon {
  font-size: 12px;
}

.metric-compact .metric-label {
  font-weight: 500;
  color: #9d9d9d;
}

.metric-compact .metric-value {
  font-weight: 600;
  color: #d4d4d4;
  min-width: 35px;
  text-align: right;
}

.metric-bar-mini {
  width: 30px;
  height: 3px;
  background: #3e3e3e;
  border-radius: 1px;
  overflow: hidden;
  margin-left: 4px;
}

.metric-progress-mini {
  height: 100%;
  background: linear-gradient(90deg, #007acc, #005a9e);
  width: 0%;
  transition: width 0.3s ease;
}

.connection-icon {
  font-size: 12px;
}

.connection-count,
.connection-status {
  font-weight: 500;
  color: #d4d4d4;
}

.footer-toggle {
  background: transparent;
  border: 1px solid #3e3e3e;
  color: #d4d4d4;
  padding: 2px 6px;
  border-radius: 3px;
  cursor: pointer;
  font-size: 12px;
  transition: all 0.2s ease;
}

.footer-toggle:hover {
  background: #3e3e3e;
  border-color: #5a5a5a;
}

/* Modal Adjustments */
.modal.hidden {
  display: none;
}

.modal-content.large {
  max-width: 80vw;
  max-height: 80vh;
}

/* Ensure content doesn't go under footer */
body {
  padding-bottom: 30px;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .strike-errors-toolbar:not(.collapsed) {
    width: 260px;
  }

  .strike-errors-toolbar:not(.collapsed) + .main-content {
    margin-left: 260px;
  }
}

@media (max-width: 768px) {
  .status-footer {
    font-size: 10px;
    padding: 0 10px;
  }

  .status-left,
  .status-center,
  .status-right {
    gap: 10px;
  }

  .metric-compact .metric-label {
    display: none;
  }

  .strike-errors-toolbar:not(.collapsed) {
    width: 240px;
  }

  .strike-errors-toolbar:not(.collapsed) + .main-content {
    margin-left: 240px;
  }
}
