/* Unified Cursor-style Terminal Container */
.cursor-unified-terminal {
  display: flex;
  flex-direction: column;
  background: rgba(30, 30, 30, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  backdrop-filter: blur(10px);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.3);
  overflow: hidden;
  margin: 0;
}

.cursor-unified-terminal .terminal-input-bar {
  background: transparent;
  border: none;
  margin: 0;
  padding: 8px 12px;
  border-radius: 0;
}

.cursor-unified-terminal .terminal-input {
  background: transparent;
  border: none;
  border-radius: 0;
  margin: 0;
  padding: 8px 0;
  color: #ffffff;
}

.cursor-unified-terminal .terminal-input::placeholder {
  color: rgba(255, 255, 255, 0.5);
}

.cursor-unified-terminal .btn-41 {
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  color: #ffffff;
}

/* Cursor-style Terminal Input Wrapper with Inline Send Button */
.terminal-input-wrapper.cursor-style {
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
}

.terminal-input-wrapper.cursor-style .terminal-input {
  flex: 1;
  padding-right: 32px; /* Space for send button */
  background: transparent;
  border: none;
  color: #ffffff;
  font-size: 14px;
}

.terminal-send-btn.cursor-tiny {
  position: absolute;
  right: 6px;
  top: 50%;
  transform: translateY(-50%);
  width: 20px;
  height: 20px;
  padding: 0;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 4px;
  color: rgba(255, 255, 255, 0.7);
  font-size: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s ease;
}

.terminal-send-btn.cursor-tiny:hover {
  background: rgba(255, 255, 255, 0.2);
  border-color: rgba(255, 255, 255, 0.4);
  color: #ffffff;
}

.terminal-send-btn.cursor-tiny .codicon {
  font-size: 10px;
}

/* Terminal Toolbar - Cursor-style Tiny Skinnier Buttons */
.vscode-terminal-header {
  background: rgba(30, 30, 30, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px 8px 0 0;
  padding: 6px 12px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  backdrop-filter: blur(10px);
}

.terminal-controls {
  display: flex;
  align-items: center;
  gap: 4px;
}

.terminal-control-btn.btn-41 {
  width: 18px;
  height: 16px;
  padding: 0;
  background: rgba(255, 255, 255, 0.08);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 3px;
  color: rgba(255, 255, 255, 0.6);
  font-size: 9px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.15s ease;
  min-width: 18px;
}

.terminal-control-btn.btn-41:hover {
  background: rgba(255, 255, 255, 0.15);
  border-color: rgba(255, 255, 255, 0.3);
  color: rgba(255, 255, 255, 0.9);
  transform: translateY(-1px);
}

.terminal-control-btn.btn-41:active {
  transform: translateY(0);
  background: rgba(255, 255, 255, 0.2);
}

.terminal-control-btn.btn-41 .codicon {
  font-size: 9px;
  line-height: 1;
}

.terminal-tab-bar {
  flex: 1;
  display: flex;
  align-items: center;
  gap: 4px;
  min-height: 16px;
}

.terminal-status-indicator {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 11px;
  color: rgba(255, 255, 255, 0.7);
}

.terminal-status-dot {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  display: inline-block;
  transition: all 0.3s ease;
}

.terminal-status-dot.connected {
  background: #4caf50;
  animation: pulse-gentle 2s infinite;
  box-shadow: 0 0 4px rgba(76, 175, 80, 0.5);
}

.terminal-status-dot.ready {
  background: #ff9800;
  animation: none;
}

.terminal-status-dot.loading {
  background: #2196f3;
  animation: pulse-fast 1s infinite;
}

.terminal-status-dot.error {
  background: #f44336;
  animation: none;
}

@keyframes pulse-fast {
  0%,
  100% {
    opacity: 0.3;
    transform: scale(1);
  }
  50% {
    opacity: 1;
    transform: scale(1.1);
  }
}

.terminal-status-text {
  font-weight: 500;
  letter-spacing: 0.3px;
}

@keyframes pulse-gentle {
  0%,
  100% {
    opacity: 0.7;
  }
  50% {
    opacity: 1;
  }
}

