  justify-content: center;
}

.tab-close:hover {
  background: rgba(255, 80, 80, 0.8);
  border-color: rgba(255, 80, 80, 1);
  color: rgba(255, 255, 255, 1);
  background: rgba(255, 255, 255, 0.1);
}

.terminal-content-area {
