  font-size: 13px;
  backdrop-filter: blur(8px);
}

.terminal-dropdown-menu .dropdown-item {
  display: flex;
  align-items: center;
  padding: 8px 12px;
  cursor: pointer;
  color: rgba(255, 255, 255, 0.9);
  border-radius: 2px;
  transition: background-color 0.2s;
  gap: 8px;
}

.terminal-dropdown-menu .dropdown-item:hover {
  background: rgba(0, 120, 212, 0.2);
  color: rgba(255, 255, 255, 1);
}

.terminal-dropdown-menu .dropdown-icon {
  width: 16px;
  text-align: center;
  font-weight: bold;
  opacity: 0.8;
}

.terminal-dropdown-menu .dropdown-text {
  flex: 1;
  font-weight: 500;
}

.terminal-dropdown-menu .dropdown-shortcut {
  font-size: 11px;
  opacity: 0.6;
