   * Close phase selection modal
   */
  closeModal() {
    if (this.modal) {
      document.body.removeChild(this.modal);
      this.modal = null;
      document.removeEventListener('keydown', this.handleEscapeKey.bind(this));
    }
  }

  /**
   * Execute selected phase through controller
   * Integrates with FastMCP python bridge
   */
  async executePhase(phaseType) {
    try {
      this.setExecutingState(true);

      // Create phase data object
      const phaseData = {
        type: phaseType,
        timestamp: Date.now(),
        source: 'phase-button',
        parameters: this.getDefaultParameters(phaseType)
      };

      // Execute through controller
      const result = await this.controller.executePhase(phaseData);

      // Stream results to planning tab
      await this.streamResultsToPlanningTab(result);

      // Update status indicators
      this.updateStatusIndicators('success');

      // Trace successful execution
      if (window.electronAPI?.traceDebug) {
        await window.electronAPI.traceDebug('ui:phase-button:execute-success', 'Phase executed successfully', {
          phaseType,
          result,
          timestamp: Date.now()
        });
      }

    } catch (error) {
      console.error('Phase execution error:', error);
      this.handleError(error);
      this.updateStatusIndicators('error');
    } finally {
      this.setExecutingState(false);
    }
  }

  /**
   * Get default parameters for phase type
   */
  getDefaultParameters(phaseType) {
    const parameterDefaults = {
      'cursor-movement': {
        algorithm: 'bezier',
        speed: 'normal',
        variation: 0.1
      },
      'element-interaction': {
        detection_method: 'ai',
        interaction_type: 'click',
        wait_time: 1000
      },
      'screenshot-analysis': {
        analysis_type: 'elements',
        ai_processing: true,
        save_to_planning: true
      },
      'gesture-emulation': {
        gesture_type: 'two-finger-scroll',
        duration: 2000,
        natural_variation: true
      }
    };

    return parameterDefaults[phaseType] || {};
  }

  /**
   * Stream execution results to planning tab
   * Includes screenshots integration
   */
  async streamResultsToPlanningTab(result) {
    try {
      // Switch to planning tab
      const planningTab = document.querySelector('[data-tab="planning"]');
      if (planningTab && !planningTab.classList.contains('active')) {
        planningTab.click();
      }

      // Stream to planning guidance content
      const guidanceContent = document.getElementById('guidance-content');
      if (guidanceContent) {
        const phaseReport = this.formatPhaseReport(result);
        guidanceContent.value = phaseReport;
      }

      // Add screenshot to planning tab if available
      if (result.screenshot) {
        await this.addScreenshotToPlanning(result.screenshot, result.timestamp);
      }

      // Update planning tab with phase data
      await this.updatePlanningTabData(result);

    } catch (error) {
      console.error('Error streaming to planning tab:', error);
    }
  }

  /**
   * Add screenshot to planning tab screenshots section
   * Chicago Time: 2025-08-13 15:40:00 CST
   */
  async addScreenshotToPlanning(screenshotData, timestamp) {
    try {
      const screenshotsList = document.getElementById('screenshots-list');
      const noScreenshots = screenshotsList.querySelector('.no-screenshots');

      // Remove "no screenshots" message if present
      if (noScreenshots) {
        noScreenshots.style.display = 'none';
      }

      // Create screenshot element
      const screenshotElement = document.createElement('div');
      screenshotElement.className = 'screenshot-item phase-screenshot';
      screenshotElement.innerHTML = `
        <div class="screenshot-header">
          <span class="screenshot-title">Phase Execution - ${new Date(timestamp).toLocaleTimeString()}</span>
          <span class="screenshot-type">Phase Result</span>
        </div>
        <div class="screenshot-image-container">
          <img src="${screenshotData}" alt="Phase execution screenshot" class="screenshot-image"/>
        </div>
        <div class="screenshot-metadata">
          <span class="timestamp">${new Date(timestamp).toLocaleString()}</span>
          <button class="btn-41 xs screenshot-action" data-action="view-full">
            <i class="codicon codicon-zoom-in"></i>
          </button>
        </div>
      `;

      // Add to screenshots list
      screenshotsList.appendChild(screenshotElement);

      // Update screenshot count
      const countElement = document.getElementById('screenshot-count');
      if (countElement) {
        const count = screenshotsList.querySelectorAll('.screenshot-item').length;
        countElement.textContent = `${count} images`;
      }

      // Setup screenshot interaction handlers
      this.setupScreenshotHandlers(screenshotElement);

    } catch (error) {
      console.error('Error adding screenshot to planning tab:', error);
    }
  }

  /**
   * Setup screenshot interaction handlers
   */
  setupScreenshotHandlers(screenshotElement) {
    const viewButton = screenshotElement.querySelector('[data-action="view-full"]');
    if (viewButton) {
      viewButton.addEventListener('click', () => {
        const img = screenshotElement.querySelector('.screenshot-image');
        if (img) {
          // Create full-size view modal
          this.showFullScreenshot(img.src);
        }
      });
    }
  }

  /**
   * Show full-size screenshot in modal
   */
  showFullScreenshot(imageSrc) {
    const fullScreenModal = document.createElement('div');
    fullScreenModal.className = 'screenshot-fullscreen-modal';
    fullScreenModal.innerHTML = `
      <div class="fullscreen-overlay" style="position: fixed; top: 0; left: 0; width: 100vw; height: 100vh; background: rgba(0,0,0,0.9); z-index: 10000; display: flex; align-items: center; justify-content: center;">
        <div class="fullscreen-content" style="max-width: 90vw; max-height: 90vh;">
          <img src="${imageSrc}" style="max-width: 100%; max-height: 100%; object-fit: contain;" />
          <button class="close-fullscreen" style="position: absolute; top: 20px; right: 20px; background: rgba(255,255,255,0.2); border: none; color: white; font-size: 24px; cursor: pointer; padding: 10px;">&times;</button>
        </div>
      </div>
    `;

    document.body.appendChild(fullScreenModal);

    // Close handlers
    const closeBtn = fullScreenModal.querySelector('.close-fullscreen');
    const overlay = fullScreenModal.querySelector('.fullscreen-overlay');

    const closeModal = () => {
      document.body.removeChild(fullScreenModal);
    };

    closeBtn.addEventListener('click', closeModal);
    overlay.addEventListener('click', (e) => {
      if (e.target === overlay) closeModal();
    });
  }

  /**
   * Format phase execution report for planning tab
   */
  formatPhaseReport(result) {
    const timestamp = new Date().toLocaleString();
    return `Phase Execution Report - ${timestamp}

Phase Type: ${result.phaseType}
Status: ${result.status}
Duration: ${result.duration}ms

Execution Details:
${JSON.stringify(result.details, null, 2)}

Performance Metrics:
- Response Time: ${result.performance?.responseTime || 'N/A'}ms
- Success Rate: ${result.performance?.successRate || 'N/A'}%
- Error Count: ${result.performance?.errorCount || 0}

Next Steps:
${result.nextSteps || 'Phase execution complete. Review results and continue with workflow.'}

Generated: ${timestamp} CST
`;
  }

  /**
   * Update planning tab with detailed phase data
   */
  async updatePlanningTabData(result) {
    // This will be expanded in later sessions
    // For now, ensure basic data streaming works
    console.log('Planning tab updated with phase data:', result);
  }

  /**
   * Set executing state with visual feedback
   */
  setExecutingState(executing) {
    this.isExecuting = executing;

    if (executing) {
      this.element.classList.add('executing');
      this.element.style.backgroundColor = '#9b59b6';
      this.element.disabled = true;
    } else {
      this.element.classList.remove('executing');
      this.element.style.backgroundColor = '';
      this.element.disabled = false;
    }
  }

  /**
   * Setup state management for visual feedback
   */
  setupStateManagement() {
    // Add CSS class for executing state
    const style = document.createElement('style');
    style.textContent = `
      #phase-btn.executing {
        animation: pulse 1.5s infinite;
        opacity: 0.8;
      }

      @keyframes pulse {
        0% { transform: scale(1); }
        50% { transform: scale(1.05); }
        100% { transform: scale(1); }
      }

      .phase-selection-modal {
        position: fixed;
        top: 0;
        left: 0;
        width: 100vw;
        height: 100vh;
        z-index: 1000;
      }

      .phase-selection-modal .modal-overlay {
        width: 100%;
        height: 100%;
        background: rgba(0, 0, 0, 0.7);
        display: flex;
        align-items: center;
        justify-content: center;
      }

      .phase-selection-modal .modal-content {
        background: var(--background-color);
        border-radius: 8px;
        padding: 20px;
        min-width: 400px;
        max-width: 600px;
        box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
      }

      .phase-templates {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        gap: 15px;
        margin-top: 20px;
      }

      .phase-template {
        display: flex;
        align-items: center;
        padding: 15px;
        border: 2px solid var(--border-color);
        border-radius: 6px;
        cursor: pointer;
        transition: all 0.2s ease;
      }

      .phase-template:hover {
        border-color: var(--accent-color);
        background: var(--hover-background);
      }

      .phase-template i {
        margin-right: 10px;
        font-size: 18px;
      }
    `;
    document.head.appendChild(style);
  }

  /**
   * Update status indicators in header
   */
  updateStatusIndicators(status) {
    const chromeStatus = document.getElementById('chrome-status');
    if (chromeStatus) {
      const statusText = chromeStatus.querySelector('.status-text');
      const statusIcon = chromeStatus.querySelector('.status-icon');

      if (status === 'success') {
        statusText.textContent = 'Chrome: Phase Complete';
        statusIcon.classList.add('success');
      } else if (status === 'error') {
        statusText.textContent = 'Chrome: Phase Error';
        statusIcon.classList.add('danger');
      }
    }
  }

  /**
   * Handle errors with planning tab integration
   */
  handleError(error) {
    console.error('Phase button error:', error);

    // Stream error to planning tab
    const guidanceContent = document.getElementById('guidance-content');
    if (guidanceContent) {
      const errorReport = `Phase Execution Error - ${new Date().toLocaleString()}

Error: ${error.message}
Stack: ${error.stack}

Recommended Actions:
1. Check Chrome Container connection
2. Verify WebView accessibility
3. Review phase parameters
4. Check Python MCP bridge status

Generated: ${new Date().toLocaleString()} CST
`;
      guidanceContent.value = errorReport;
    }

    // Also stream to error log tab if needed
    if (window.electronAPI?.traceDebug) {
      window.electronAPI.traceDebug('ui:phase-button:error', 'Phase execution error', {
        error: error.message,
        stack: error.stack,
        timestamp: Date.now()
      });
    }
  }
}

// Export for use in app.js
window.PhaseButton = PhaseButton;

