/**
 * Phase Button Component - Tiny header button for phase execution
 * Based on MVP/sources/downloaded/github/claude-code-hooks-mastery/ component patterns
 * Chicago Time: 2025-08-13 15:35:00 CST
 */

class PhaseButton {
  constructor(elementId) {
    this.elementId = elementId;
    this.element = null;
    this.controller = null;
    this.isExecuting = false;
    this.currentPhaseIndex = 0;

    // Phase execution cycle - NO POPUP
    this.phaseTypes = [
      'cursor-movement',
      'element-interaction', 
      'screenshot-analysis',
      'gesture-emulation'
    ];

    // Initialize with MVP source patterns from claude-code-hooks
    this.initializeComponent();
  }

  /**
   * Initialize phase button component following claude-code-hooks patterns
   * Source: MVP/sources/downloaded/github/claude-code-hooks-mastery/
   */
  initializeComponent() {
    this.element = document.getElementById(this.elementId);
    if (!this.element) {
      console.error(`Phase button element ${this.elementId} not found`);
      return;
    }

    // Setup event handlers
    this.attachEventHandlers();

    // Initialize controller
    this.controller = new PhaseController();

    // Setup visual state management
    this.setupStateManagement();
  }

  /**
   * Attach event handlers for direct MCP phase execution
   * NO POPUP - Direct connection to Python MCP server
   */
  attachEventHandlers() {
    this.element.addEventListener('click', async (event) => {
      event.preventDefault();

      if (this.isExecuting) {
        return; // Prevent multiple executions
      }

      try {
        // DIRECT EXECUTION - No popup modal
        await this.executeNextPhase();

        // Trace the interaction
        if (window.electronAPI?.traceDebug) {
          await window.electronAPI.traceDebug('ui:phase-button:direct-execute', 'Phase button direct execution', {
            timestamp: Date.now(),
            executing: this.isExecuting
          });
        }
      } catch (error) {
        console.error('Phase button execution error:', error);
        this.handleError(error);
      }
    });

    // Add hover effects
    this.element.addEventListener('mouseenter', () => {
      if (!this.isExecuting) {
        this.element.style.transform = 'scale(1.05)';
      }
    });

    this.element.addEventListener('mouseleave', () => {
      if (!this.isExecuting) {
        this.element.style.transform = 'scale(1.0)';
      }
    });
  }

  /**
   * Execute next phase in cycle - Direct MCP connection
   * Cycles through all 4 phase types automatically
   */
  async executeNextPhase() {
    try {
      const currentPhaseType = this.phaseTypes[this.currentPhaseIndex];

      console.log(`üöÄ Executing phase: ${currentPhaseType} (${this.currentPhaseIndex + 1}/4)`);

      // Update button text to show current phase
      this.updateButtonDisplay(currentPhaseType);

      // Execute the current phase
      await this.executePhase(currentPhaseType);

      // Move to next phase in cycle
      this.currentPhaseIndex = (this.currentPhaseIndex + 1) % this.phaseTypes.length;

      console.log(`‚úÖ Phase ${currentPhaseType} completed. Next: ${this.phaseTypes[this.currentPhaseIndex]}`);

    } catch (error) {
      console.error('Error executing next phase:', error);
      this.handleError(error);
    }
  }

  /**
   * Update button display to show current phase
   */
  updateButtonDisplay(phaseType) {
    const phaseIcons = {
      'cursor-movement': 'codicon-target',
      'element-interaction': 'codicon-hand', 
      'screenshot-analysis': 'codicon-device-camera',
      'gesture-emulation': 'codicon-gesture'
    };

    const icon = this.element.querySelector('i');
    if (icon && phaseIcons[phaseType]) {
      icon.className = `codicon ${phaseIcons[phaseType]}`;
    }

    // Update title to show current phase
    this.element.title = `Execute ${phaseType.replace('-', ' ')}`;
  }

  /**
   * Execute phase through direct MCP connection
   * Immediate Python script execution via FastMCP
   */
  async executePhase(phaseType) {
    try {
      this.setExecutingState(true);

      // Stream immediate execution notice to terminal
      if (window.streamToTerminal) {
        window.streamToTerminal(`Starting ${phaseType} execution via MCP...`, 'info', 'mcp-bridge');
      }

      // Create phase package for MCP Python execution
      const phasePackage = {
        type: phaseType,
        timestamp: Date.now(),
        source: 'direct-mcp-execution',
        mcpCommand: this.getMCPCommand(phaseType),
        parameters: this.getPhaseParameters(phaseType),
        executionMode: 'immediate'
      };

      // Direct execution through controller - No delays
      const result = await this.controller.executePhase(phasePackage);

      // Immediate results streaming
      await this.streamResultsImmediately(result);

      // Update status indicators
      this.updateStatusIndicators('success');

      // Stream completion to terminal
      if (window.streamToTerminal) {
        window.streamToTerminal(`‚úÖ ${phaseType} execution completed`, 'info', 'mcp-bridge');
      }

      // Trace successful execution
      if (window.electronAPI?.traceDebug) {
        await window.electronAPI.traceDebug('mcp:phase:execute-success', 'Direct MCP phase executed', {
          phaseType,
          result,
          timestamp: Date.now()
        });
      }

    } catch (error) {
      console.error('Direct MCP phase execution error:', error);
      this.handleError(error);
      this.updateStatusIndicators('error');

      // Stream error to terminal
      if (window.streamToTerminal) {
        window.streamToTerminal(`‚ùå ${phaseType} execution failed: ${error.message}`, 'error', 'mcp-bridge');
      }
    } finally {
      this.setExecutingState(false);
    }
  }

  /**
   * Get MCP command for direct Python execution
   */
  getMCPCommand(phaseType) {
    const mcpCommands = {
      'cursor-movement': 'execute_cursor_movement_phase',
      'element-interaction': 'execute_element_interaction_phase',
      'screenshot-analysis': 'execute_screenshot_analysis_phase',
      'gesture-emulation': 'execute_gesture_emulation_phase'
    };

    return mcpCommands[phaseType] || 'execute_generic_phase';
  }

  /**
   * Get phase parameters for MCP Python execution
   */
  getPhaseParameters(phaseType) {
    const phaseParameters = {
      'cursor-movement': {
        algorithm: 'mathematical_bezier',
        speed: 'human_natural',
        variation: 0.15,
        target_detection: 'ai_powered'
      },
      'element-interaction': {
        detection_method: 'ai_visual',
        interaction_type: 'intelligent_click',
        wait_time: 'dynamic',
        verification: 'screenshot_diff'
      },
      'screenshot-analysis': {
        analysis_type: 'comprehensive_ai',
        ai_processing: 'claude_vision',
        save_to_planning: true,
        element_detection: true
      },
      'gesture-emulation': {
        gesture_type: 'natural_human_gestures',
        duration: 'variable',
        natural_variation: true,
        touch_simulation: 'precise'
      }
    };

    return phaseParameters[phaseType] || {};
  }

  /**
   * Stream results immediately - No delays for AI agent efficiency
   * Direct streaming to both planning and terminal tabs
   */
  async streamResultsImmediately(result) {
    try {
      // Immediate terminal output
      if (window.streamToTerminal) {
        window.streamToTerminal(`MCP Execution Result: ${result.status}`, 'info', 'mcp-result');
        if (result.actions) {
          result.actions.forEach(action => {
            window.streamToTerminal(`‚Üí ${action.type}: ${action.result}`, 'info', 'mcp-action');
          });
        }
      }

      // Immediate planning tab update - No tab switching delays
      if (window.streamToPlanningTab) {
        const mcpReport = this.formatMCPReport(result);
        window.streamToPlanningTab(mcpReport);
      }

      // Immediate screenshot if available
      if (result.screenshot && window.addScreenshotToPlanning) {
        await window.addScreenshotToPlanning(result.screenshot, result.timestamp);
      }

      console.log('‚úÖ Results streamed immediately to all tabs');

    } catch (error) {
      console.error('Error in immediate result streaming:', error);
    }
  }

  /**
   * Add screenshot to planning tab screenshots section
   * Chicago Time: 2025-08-13 15:40:00 CST
   */
  async addScreenshotToPlanning(screenshotData, timestamp) {
    try {
      const screenshotsList = document.getElementById('screenshots-list');
      const noScreenshots = screenshotsList.querySelector('.no-screenshots');

      // Remove "no screenshots" message if present
      if (noScreenshots) {
        noScreenshots.style.display = 'none';
      }

      // Create screenshot element
      const screenshotElement = document.createElement('div');
      screenshotElement.className = 'screenshot-item phase-screenshot';
      screenshotElement.innerHTML = `
        <div class="screenshot-header">
          <span class="screenshot-title">Phase Execution - ${new Date(timestamp).toLocaleTimeString()}</span>
          <span class="screenshot-type">Phase Result</span>
        </div>
        <div class="screenshot-image-container">
          <img src="${screenshotData}" alt="Phase execution screenshot" class="screenshot-image"/>
        </div>
        <div class="screenshot-metadata">
          <span class="timestamp">${new Date(timestamp).toLocaleString()}</span>
          <button class="btn-41 xs screenshot-action" data-action="view-full">
            <i class="codicon codicon-zoom-in"></i>
          </button>
        </div>
      `;

      // Add to screenshots list
      screenshotsList.appendChild(screenshotElement);

      // Update screenshot count
      const countElement = document.getElementById('screenshot-count');
      if (countElement) {
        const count = screenshotsList.querySelectorAll('.screenshot-item').length;
        countElement.textContent = `${count} images`;
      }

      // Setup screenshot interaction handlers
      this.setupScreenshotHandlers(screenshotElement);

    } catch (error) {
      console.error('Error adding screenshot to planning tab:', error);
    }
  }

  /**
   * Setup screenshot interaction handlers
   */
  setupScreenshotHandlers(screenshotElement) {
    const viewButton = screenshotElement.querySelector('[data-action="view-full"]');
    if (viewButton) {
      viewButton.addEventListener('click', () => {
        const img = screenshotElement.querySelector('.screenshot-image');
        if (img) {
          // Create full-size view modal
          this.showFullScreenshot(img.src);
        }
      });
    }
  }

  /**
   * Show full-size screenshot in modal
   */
  showFullScreenshot(imageSrc) {
    const fullScreenModal = document.createElement('div');
    fullScreenModal.className = 'screenshot-fullscreen-modal';
