 * No modal CSS - Direct execution only
 */
setupStateManagement() {
  // Add CSS for executing state and phase cycling
  const style = document.createElement('style');
  style.textContent = `
    #phase-btn.executing {
      animation: mcpExecutionPulse 1.2s infinite;
      opacity: 0.9;
      transform: scale(1.02);
    }

    @keyframes mcpExecutionPulse {
      0% { 
        transform: scale(1.02);
        box-shadow: 0 0 0 0 rgba(102, 126, 234, 0.8);
      }
      50% { 
        transform: scale(1.06);
        box-shadow: 0 0 0 6px rgba(102, 126, 234, 0.2);
      }
      100% { 
        transform: scale(1.02);
        box-shadow: 0 0 0 0 rgba(102, 126, 234, 0);
      }
    }

    /* Phase cycling indicator */
    #phase-btn::after {
      content: attr(data-phase-count);
      position: absolute;
      top: -4px;
      right: -4px;
      background: #667eea;
      color: white;
      border-radius: 50%;
      width: 14px;
      height: 14px;
      font-size: 9px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: bold;
    }
  `;
  document.head.appendChild(style);

  // Set initial phase indicator
  this.element.setAttribute('data-phase-count', '1');
}

