    if (window.streamToTerminal) {
      window.streamToTerminal(`üöÄ PHASE EXECUTION: ${phaseType.toUpperCase()}`, 'info', 'phase-execution');
      window.streamToTerminal(`Terminal connected to WebView for direct control`, 'info', 'webview-bridge');
    }

    // Create phase package for sequential MCP execution
    const phasePackage = {
      type: phaseType,
      timestamp: Date.now(),
      source: 'terminal-webview-mcp',
      mcpCommand: this.getMCPCommand(phaseType),
      parameters: this.getPhaseParameters(phaseType),
      webviewTarget: 'chrome-viewport',
      terminalIntegration: true,
      sequentialMode: true
    };

    // Execute through controller with terminal-webview bridge
    const result = await this.controller.executePhaseWithTerminalBridge(phasePackage);

    // Stream results to both terminal and planning tab
    await this.streamSequentialResults(result, phaseType);

    // Update status indicators
    this.updateStatusIndicators('success');

    // Final terminal output
    if (window.streamToTerminal) {
      window.streamToTerminal(`‚úÖ ${phaseType} COMPLETE - Python pointer control active in WebView`, 'info', 'phase-complete');
    }

    // Trace successful execution
    if (window.electronAPI?.traceDebug) {
      await window.electronAPI.traceDebug('phase:terminal-webview:success', 'Terminal-WebView-MCP execution complete', {
        phaseType,
        result,
        webviewControl: true,
        timestamp: Date.now()
      });
    }

  } catch (error) {
    console.error('Terminal-WebView-MCP execution error:', error);
    this.handleError(error);
    this.updateStatusIndicators('error');

    // Stream error to terminal
    if (window.streamToTerminal) {
      window.streamToTerminal(`‚ùå ${phaseType} FAILED: ${error.message}`, 'error', 'phase-error');
    }
  } finally {
    this.setExecutingState(false);
  }
}

/**
 * Connect terminal directly to webview for immediate control
 */
async connectTerminalToWebview() {
  const webview = document.getElementById('chrome-viewport');
  const terminalContent = document.getElementById('terminal-content');

  if (webview && terminalContent) {
    // Establish direct terminal-webview communication
    if (window.streamToTerminal) {
      window.streamToTerminal(`Establishing terminal ‚Üí webview bridge...`, 'info', 'bridge-setup');
    }

    // Set webview as active target for Python pointer control
    webview.setAttribute('data-terminal-controlled', 'true');
    webview.setAttribute('data-python-target', 'active');

    console.log('‚úÖ Terminal-WebView bridge established');
  }
}

/**
 * Inject command directly into terminal for immediate execution
 */
async injectTerminalCommand(command) {
  const terminalInput = document.getElementById('terminal-input');

  if (terminalInput) {
    // Inject command into terminal
    terminalInput.value = command;

    // Trigger command execution
    const sendButton = document.getElementById('terminal-send');
    if (sendButton) {
      sendButton.click();
    }

    // Stream command injection notice
    if (window.streamToTerminal) {
      window.streamToTerminal(`> ${command}`, 'info', 'command-injection');
      window.streamToTerminal(`Reading planning files and launching MCP...`, 'info', 'mcp-launch');
    }

    console.log(`‚úÖ Terminal command injected: ${command}`);
  }
}

