/**
 * Phase Controller - Main orchestration system for phase execution
 * Based on MVP/sources/downloaded/n8n/workflows.html orchestration patterns
 * Chicago Time: 2025-08-13 15:45:00 CST
 */

class PhaseController {
  constructor() {
    this.dataReader = null;
    this.dataWriter = null;
    this.statusManager = null;
    this.executor = null;
    this.pythonBridge = null;
    this.templateEngine = null;
    this.errorHandler = null;
    this.eventBus = null;
    this.configManager = null;

    // Initialize all subsystems
    this.initializeSubsystems();
  }

  /**
   * Initialize all subsystem components
   * Following n8n workflow orchestration patterns
   */
  initializeSubsystems() {
    try {
      // Initialize data layer modules
      this.dataReader = new PhaseDataReader();
      this.dataWriter = new PhaseDataWriter();
      this.statusManager = new PhaseStatusManager();

      // Initialize execution layer (will be fully implemented in later sessions)
      this.executor = new PhaseExecutor();
      this.pythonBridge = new PhasePythonBridge();

      // Initialize infrastructure (basic setup for now)
      this.eventBus = new PhaseEventBus();
      this.errorHandler = new PhaseErrorHandler();

      console.log('Phase controller subsystems initialized');

    } catch (error) {
      console.error('Error initializing phase controller subsystems:', error);
    }
  }

  /**
   * Main phase execution orchestration with WebView coordinate capture
   * MCP MUST know actual element coordinates before sending first package
   */
  async executePhase(phaseData) {
    const executionId = this.generateExecutionId();
    const startTime = Date.now();

    try {
      // Trace phase execution start
      await this.tracePhaseStart(executionId, phaseData);

      // CRITICAL: Capture WebView element coordinates FIRST
      const webviewCoordinates = await this.captureWebViewElementCoordinates();
      if (!webviewCoordinates || webviewCoordinates.length === 0) {
        throw new Error('Cannot execute phase: No WebView element coordinates captured');
      }

      // Step 1: Read all tab data via dataReader
      const contextData = await this.gatherExecutionContext();
      contextData.webviewCoordinates = webviewCoordinates;

      // Step 2: Validate phase parameters with coordinate data
      const validation = await this.validatePhaseWithCoordinates(phaseData, contextData);
      if (!validation.valid) {
        throw new Error(`Phase validation failed: ${validation.error}`);
      }

      // Step 3: Execute automation with mathematical coordinate templates
      const executionResult = await this.executeAutomationWithCoordinates(phaseData, contextData);

      // Step 4: Stream results via dataWriter
      await this.streamResults(executionResult);

      // Step 5: Update status via statusManager
      await this.updateExecutionStatus('completed', executionResult);

      // Step 6: Handle any errors via errorHandler
      if (executionResult.errors && executionResult.errors.length > 0) {
        await this.handleExecutionErrors(executionResult.errors);
      }

      const endTime = Date.now();
      const duration = endTime - startTime;

      // Create comprehensive result object
      const result = {
        executionId,
        phaseType: phaseData.type,
        status: 'success',
        duration,
        startTime,
        endTime,
        details: executionResult,
        performance: {
          responseTime: duration,
          successRate: this.calculateSuccessRate(executionResult),
          errorCount: executionResult.errors ? executionResult.errors.length : 0
        },
        nextSteps: this.generateNextSteps(executionResult),
        screenshot: executionResult.screenshot || null,
        timestamp: Date.now()
      };

      // Trace successful completion
      await this.tracePhaseComplete(executionId, result);

      return result;

    } catch (error) {
      console.error('Phase execution error:', error);

      // Handle execution error
      const errorResult = await this.handlePhaseError(executionId, error, phaseData);

      // Update status to error
      await this.updateExecutionStatus('error', errorResult);

      // Stream error to planning tab
      await this.streamErrorResults(errorResult);

      throw error; // Re-throw for button handler
    }
  }

  /**
   * Gather execution context from all tabs
   * Reads data from left toolbar tabs
   */
  async gatherExecutionContext() {
    try {
      const context = {
        strike: await this.dataReader.getStrikeErrorsContext(),
        files: await this.dataReader.getFileBrowserContext(),
        diagram: await this.dataReader.getDiagramContext(),
        webview: await this.dataReader.getWebViewContext(),
        timestamp: Date.now()
      };

      console.log('Execution context gathered:', context);
      return context;

    } catch (error) {
      console.error('Error gathering execution context:', error);
      return {
        strike: null,
        files: null,
        diagram: null,
        webview: null,
        error: error.message,
        timestamp: Date.now()
      };
    }
  }

  /**
   * Validate phase data and context
   */
  async validatePhase(phaseData, contextData) {
    try {
      // Basic validation
      if (!phaseData || !phaseData.type) {
        return { valid: false, error: 'Phase type is required' };
      }

      // Check if WebView is accessible
      if (!contextData.webview || contextData.webview.error) {
        return { valid: false, error: 'WebView not accessible' };
      }

      // Validate phase-specific requirements
      const phaseValidation = await this.validatePhaseType(phaseData.type, phaseData.parameters);
      if (!phaseValidation.valid) {
        return phaseValidation;
      }

      return { valid: true };

    } catch (error) {
      return { valid: false, error: `Validation error: ${error.message}` };
    }
  }

  /**
   * Validate phase with captured coordinates
   * Ensures MCP has coordinates before sending to Python
   */
  async validatePhaseWithCoordinates(phaseData, contextData) {
    try {
      // Basic validation
      if (!phaseData || !phaseData.type) {
        return { valid: false, error: 'Phase type is required' };
      }

      // Check if coordinates were captured
      if (!contextData.webviewCoordinates) {
        return { valid: false, error: 'WebView coordinates not captured - MCP cannot proceed' };
      }

      // Validate WebView accessibility
      if (!contextData.webview || contextData.webview.error) {
        return { valid: false, error: 'WebView not accessible for coordinate capture' };
      }

      // Validate coordinate data structure
      const coords = contextData.webviewCoordinates;
      if (!coords.webview || !coords.webview.x || !coords.webview.y) {
        return { valid: false, error: 'Invalid WebView coordinate data - MCP cannot generate mathematical template' };
      }

      return { valid: true, coordinates: coords };

    } catch (error) {
      return { valid: false, error: `Coordinate validation error: ${error.message}` };
    }
  }

  /**
   * Capture WebView element coordinates BEFORE any MCP execution
   * MCP needs actual coordinates to generate mathematical output
   */
  async captureWebViewElementCoordinates() {
    try {
      const webview = document.getElementById('chrome-viewport');
      if (!webview) {
        throw new Error('WebView element not found');
      }

      // Get WebView dimensions and offset
      const webviewRect = webview.getBoundingClientRect();
      const coordinates = {
        webview: {
          x: webviewRect.x,
          y: webviewRect.y,
          width: webviewRect.width,
          height: webviewRect.height,
          centerX: webviewRect.x + webviewRect.width / 2,
          centerY: webviewRect.y + webviewRect.height / 2
        },
        elements: []
      };

      // Capture interactive elements within WebView (if accessible)
      try {
        // This would typically use CDP to get actual page elements
        // For now, provide WebView coordinate template structure
        coordinates.elements.push({
          selector: 'webview-content',
          x: webviewRect.x + 50,
          y: webviewRect.y + 100,
          width: 200,
          height: 50,
          centerX: webviewRect.x + 150,
          centerY: webviewRect.y + 125,
          type: 'clickable',
          confidence: 0.95
        });
      } catch (error) {
        console.warn('Could not capture internal WebView elements:', error);
      }

      console.log('WebView coordinates captured:', coordinates);
      return coordinates;

    } catch (error) {
      console.error('Error capturing WebView coordinates:', error);
      return null;
    }
  }

  /**
   * Execute automation with mathematical coordinate templates
   * Python script receives EXACT template format as MCP mathematical output
   */
  async executeAutomationWithCoordinates(phaseData, contextData) {
    try {
      // Create mathematical template for MCP-Python coordination
      const mathematicalTemplate = this.generateMathematicalTemplate(phaseData, contextData.webviewCoordinates);

      // Stream coordinate data to terminal for verification
      if (window.streamToTerminal) {
        window.streamToTerminal(`üìê WebView coordinates captured: ${contextData.webviewCoordinates.elements.length} elements`, 'info', 'coordinate-capture');
        window.streamToTerminal(`üßÆ Mathematical template generated for Python script`, 'info', 'template-generation');
      }

      const coordinateResult = {
        phaseType: phaseData.type,
        status: 'completed',
        mathematicalTemplate,
        webviewCoordinates: contextData.webviewCoordinates,
        actions: [
          {
            type: 'coordinate-capture',
            result: 'success',
            webviewCoordinates: contextData.webviewCoordinates.webview,
            elementCount: contextData.webviewCoordinates.elements.length,
            timestamp: Date.now()
          },
          {
            type: 'template-generation',
            result: 'success',
            details: `Mathematical template created for ${phaseData.type} phase`,
            template: mathematicalTemplate,
            timestamp: Date.now()
          }
        ],
        screenshot: await this.captureScreenshot(),
        errors: [],
        metadata: {
          coordinatesCaptured: true,
          templateGenerated: true,
          mcpPythonSync: true,
          executionTime: Date.now()
        }
      };

      // Simulate coordinate processing delay
      await new Promise(resolve => setTimeout(resolve, 500));

      return coordinateResult;

    } catch (error) {
      console.error('Error executing automation with coordinates:', error);
      return {
        phaseType: phaseData.type,
        status: 'error',
        error: error.message,
        actions: [],
        errors: [error.message],
        coordinatesCaptured: false,
        timestamp: Date.now()
      };
    }
  }

  /**
   * Generate mathematical template that MCP and Python script both use
   * CRITICAL: Both MCP and Python must use IDENTICAL template format
   */
  generateMathematicalTemplate(phaseData, webviewCoordinates) {
    const template = {
      // Template version for MCP-Python synchronization
      templateVersion: '1.0.0',
      templateType: 'MCP_PYTHON_MATHEMATICAL_COORDINATION',

      // Phase execution data
      phase: {
        type: phaseData.type,
        timestamp: Date.now(),
        executionId: this.generateExecutionId()
      },

      // WebView coordinate system (absolute coordinates)
      coordinateSystem: {
        origin: { x: webviewCoordinates.webview.x, y: webviewCoordinates.webview.y },
        dimensions: {
          width: webviewCoordinates.webview.width,
          height: webviewCoordinates.webview.height
        },
        center: {
          x: webviewCoordinates.webview.centerX,
          y: webviewCoordinates.webview.centerY
        }
      },

      // Target elements with mathematical coordinates
      targets: webviewCoordinates.elements.map((element, index) => ({
        targetId: `element_${index}`,
        selector: element.selector,
        coordinates: {
          absolute: { x: element.x, y: element.y },
          relative: {
            x: element.x - webviewCoordinates.webview.x,
            y: element.y - webviewCoordinates.webview.y
          },
          center: { x: element.centerX, y: element.centerY }
        },
        dimensions: { width: element.width, height: element.height },
        confidence: element.confidence,
        type: element.type
      })),

      // Mathematical algorithms for cursor movement
      algorithms: {
        cursorMovement: {
          type: 'bezier',
          controlPoints: this.generateBezierControlPoints(webviewCoordinates),
          speed: 'natural_human',
          variation: 0.15
        },
        targeting: {
          method: 'mathematical_precision',
          tolerance: 5, // pixels
          verification: 'coordinate_match'
        }
      },

      // Python script input format (IDENTICAL to MCP output)
      pythonInput: {
        action: phaseData.type,
        coordinates: webviewCoordinates,
        mathematical: true,
        template: true,
        mcpSynchronized: true
      }
    };

    return template;
  }

  /**
   * Generate Bezier control points for mathematical cursor movement
   */
  generateBezierControlPoints(webviewCoordinates) {
    const webview = webviewCoordinates.webview;

    return {
      start: { x: webview.centerX, y: webview.centerY },
      control1: { 
        x: webview.centerX + (webview.width * 0.2), 
        y: webview.centerY - (webview.height * 0.1) 
      },
      control2: { 
        x: webview.centerX - (webview.width * 0.1), 
        y: webview.centerY + (webview.height * 0.2) 
      },
      end: { 
        x: webview.centerX + (webview.width * 0.3), 
        y: webview.centerY + (webview.height * 0.1) 
      }
    };
  }

  /**
   * Capture screenshot for phase documentation
   * Integrates with planning tab screenshots
   */
  async captureScreenshot() {
    try {
      // Use existing screenshot functionality if available
      if (window.electronAPI && window.electronAPI.captureScreenshot) {
        const screenshot = await window.electronAPI.captureScreenshot();
        return `data:image/png;base64,${screenshot}`;
      }

      // Fallback: capture WebView via canvas (basic implementation)
      const webview = document.getElementById('chrome-viewport');
      if (webview) {
        // This is a placeholder - actual implementation will use CDP in Session 4
        return 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkYPhfDwAChwGA60e6kgAAAABJRU5ErkJggg=='; // 1x1 transparent PNG
      }

      return null;

    } catch (error) {
      console.error('Error capturing screenshot:', error);
      return null;
    }
  }

  /**
   * Stream results to appropriate tabs
   */
  async streamResults(executionResult) {
    try {
      // Stream to planning tab
      await this.dataWriter.writeToPlanningTab({
        type: 'phase-result',
        data: executionResult,
        timestamp: Date.now()
      });

      // Stream to terminal tab if there's output
      if (executionResult.output) {
        await this.dataWriter.streamOutputToTerminal({
          output: executionResult.output,
          level: 'info',
          source: 'phase-execution',
          timestamp: Date.now()
        });
      }

      console.log('Results streamed to tabs');

    } catch (error) {
      console.error('Error streaming results:', error);
    }
  }

  /**
   * Stream error results to planning and error tabs
   */
  async streamErrorResults(errorResult) {
    try {
      // Stream to planning tab
      await this.dataWriter.writeToPlanningTab({
        type: 'phase-error',
        data: errorResult,
        timestamp: Date.now()
      });

      // Stream to error log tab
      await this.dataWriter.streamErrorsToTab({
        error: errorResult.error,
        context: errorResult.context,
        timestamp: Date.now(),
        source: 'phase-execution'
      });

      // Stream to terminal tab
      await this.dataWriter.streamOutputToTerminal({
        output: `Phase execution error: ${errorResult.error}`,
        level: 'error',
        source: 'phase-execution',
        timestamp: Date.now()
      });

    } catch (error) {
      console.error('Error streaming error results:', error);
    }
  }

  /**
   * Update execution status in status indicators
   */
  async updateExecutionStatus(status, result) {
    try {
      await this.statusManager.updatePhaseStatus({
        status,
        result,
        timestamp: Date.now()
      });
    } catch (error) {
      console.error('Error updating execution status:', error);
    }
  }

  /**
   * Handle execution errors
   */
  async handleExecutionErrors(errors) {
    try {
      for (const error of errors) {
        await this.errorHandler.handleError({
          error,
          context: 'phase-execution',
          timestamp: Date.now()
        });
      }
    } catch (error) {
      console.error('Error handling execution errors:', error);
    }
  }

  /**
   * Handle phase error
   */
  async handlePhaseError(executionId, error, phaseData) {
    const errorResult = {
      executionId,
      phaseType: phaseData.type,
      status: 'error',
      error: error.message,
      stack: error.stack,
      context: {
        phaseData,
        timestamp: Date.now()
      },
      recovery: await this.generateRecoverySteps(error, phaseData),
      timestamp: Date.now()
    };

    // Trace error
    await this.tracePhaseError(executionId, errorResult);

    return errorResult;
  }

  /**
   * Generate recovery steps for errors
   */
  async generateRecoverySteps(error, phaseData) {
    // Basic recovery suggestions for Session 2
    // Will be expanded with AI-powered recovery in Session 7
    const recoverySteps = [
      'Check Chrome Container connection status',
      'Verify WebView accessibility',
      'Review phase parameters for correctness',
      'Try executing phase again with different parameters'
    ];

    if (error.message.includes('WebView')) {
      recoverySteps.unshift('Refresh WebView or restart Chrome integration');
    }

    if (error.message.includes('Python')) {
      recoverySteps.unshift('Check Python MCP bridge connection');
    }

    return recoverySteps;
  }

  /**
   * Calculate success rate from execution result
   */
  calculateSuccessRate(executionResult) {
    if (!executionResult.actions || executionResult.actions.length === 0) {
      return 0;
    }

    const successfulActions = executionResult.actions.filter(action => action.result === 'success');
    return Math.round((successfulActions.length / executionResult.actions.length) * 100);
  }

  /**
   * Generate next steps suggestions
   */
  generateNextSteps(executionResult) {
    const suggestions = [
      'Review execution results in planning tab',
      'Check screenshot for visual verification',
      'Consider chaining additional phases if needed'
    ];

    if (executionResult.errors && executionResult.errors.length > 0) {
      suggestions.unshift('Address any errors shown in error log tab');
    }

    if (executionResult.phaseType === 'screenshot-analysis') {
      suggestions.push('Review AI analysis results for actionable insights');
    }

    return suggestions.join('\n');
  }

  /**
   * Generate unique execution ID
   */
  generateExecutionId() {
    return `phase_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Trace phase execution start
   */
  async tracePhaseStart(executionId, phaseData) {
    if (window.electronAPI?.traceDebug) {
      await window.electronAPI.traceDebug('phase:execution:start', 'Phase execution started', {
        executionId,
        phaseType: phaseData.type,
        parameters: phaseData.parameters,
        timestamp: Date.now()
      });
    }
  }

  /**
   * Trace phase execution completion
   */
  async tracePhaseComplete(executionId, result) {
    if (window.electronAPI?.traceDebug) {
      await window.electronAPI.traceDebug('phase:execution:complete', 'Phase execution completed', {
        executionId,
        result,
        timestamp: Date.now()
      });
    }
  }

  /**
   * Trace phase execution error
   */
  async tracePhaseError(executionId, errorResult) {
    if (window.electronAPI?.traceDebug) {
      await window.electronAPI.traceDebug('phase:execution:error', 'Phase execution error', {
        executionId,
        errorResult,
        timestamp: Date.now()
      });
    }
  }

  /**
   * Integrate with all discovered tab systems
   * Uses switchTab() from app.js:2985
   */
  initializeIntegrations() {
    try {
      // Ensure tab switching functionality is available
      if (typeof switchTab === 'function') {
        this.switchTab = switchTab;
      }

      // Initialize data readers/writers with tab system access
      this.dataReader.setTabSystem(this);
      this.dataWriter.setTabSystem(this);

      console.log('Phase controller integrations initialized');

    } catch (error) {
      console.error('Error initializing integrations:', error);
    }
  }
}

