class PhaseDataWriter {
  setTabSystem(controller) {
    this.controller = controller;
  }

  async writeToPlanningTab(data) {
    // Switch to planning tab if not active
    const planningTab = document.querySelector('[data-tab="planning"]');
    if (planningTab && !planningTab.classList.contains('active')) {
      planningTab.click();
    }

    // Update guidance content
    const guidanceContent = document.getElementById('guidance-content');
    if (guidanceContent && data.type === 'phase-result') {
      const report = this.formatPhaseReport(data.data);
      guidanceContent.value = report;
    }
  }

  async streamOutputToTerminal(data) {
    // Switch to terminal tab
    const terminalTab = document.querySelector('[data-tab="terminal"]');
    if (terminalTab && !terminalTab.classList.contains('active')) {
      terminalTab.click();
    }

    // Add to terminal content
    const terminalContent = document.getElementById('terminal-content');
    if (terminalContent) {
      const outputElement = document.createElement('div');
      outputElement.className = `terminal-output ${data.level}`;
      outputElement.innerHTML = `
        <span class="timestamp">[${new Date(data.timestamp).toLocaleTimeString()}]</span>
        <span class="source">[${data.source}]</span>
        <span class="message">${data.output}</span>
      `;
      terminalContent.appendChild(outputElement);
      terminalContent.scrollTop = terminalContent.scrollHeight;
    }
  }

  async streamErrorsToTab(data) {
    // Switch to error tab
    const errorTab = document.querySelector('[data-tab="errors"]');
    if (errorTab && !errorTab.classList.contains('active')) {
      errorTab.click();
    }

    // Add to error log
    const errorLog = document.getElementById('error-log');
    if (errorLog) {
      const errorElement = document.createElement('div');
      errorElement.className = 'error-entry phase-error';
      errorElement.innerHTML = `
        <div class="error-header">
          <span class="error-timestamp">${new Date(data.timestamp).toLocaleTimeString()}</span>
          <span class="error-source">${data.source}</span>
        </div>
        <div class="error-message">${data.error}</div>
        ${data.context ? `<div class="error-context">${JSON.stringify(data.context, null, 2)}</div>` : ''}
      `;
      errorLog.appendChild(errorElement);
    }
  }

  formatPhaseReport(executionResult) {
    const timestamp = new Date().toLocaleString();
    return `Phase Execution Report - ${timestamp}

Phase Type: ${executionResult.phaseType}
Status: ${executionResult.status}
Actions Executed: ${executionResult.actions ? executionResult.actions.length : 0}

${executionResult.actions ? executionResult.actions.map(action => 
  `- ${action.type}: ${action.result}${action.details ? ` (${action.details})` : ''}`
).join('\n') : ''}

${executionResult.errors && executionResult.errors.length > 0 ? 
  `\nErrors:\n${executionResult.errors.map(error => `- ${error}`).join('\n')}` : ''}

Generated: ${timestamp} CST
`;
  }
}

class PhaseStatusManager {
  async updatePhaseStatus(data) {
    // Update chrome status indicator
    const chromeStatus = document.getElementById('chrome-status');
    if (chromeStatus) {
      const statusText = chromeStatus.querySelector('.status-text');
      const statusIcon = chromeStatus.querySelector('.status-icon');

      if (data.status === 'completed') {
        statusText.textContent = 'Chrome: Phase Complete';
        statusIcon.className = 'codicon codicon-primitive-dot status-icon success';
      } else if (data.status === 'error') {
        statusText.textContent = 'Chrome: Phase Error';
        statusIcon.className = 'codicon codicon-primitive-dot status-icon danger';
      } else {
        statusText.textContent = 'Chrome: Phase Executing';
        statusIcon.className = 'codicon codicon-primitive-dot status-icon warning';
      }
    }
  }
}

