/**
 * Generate sequential steps - UNIVERSAL TEMPLATE for ANY gesture/combination
 * MCP server calculates multiple steps per phase, each step can be ANY combination
 */
generateSequentialSteps(phaseType, webviewCoordinates) {
  // MCP server determines how many steps needed for this phase
  const totalSteps = this.calculateMCPStepsForPhase(phaseType);

  // Generate UNIVERSAL template steps that can handle ANY gesture/combination
  const universalSteps = [];

  for (let i = 1; i <= totalSteps; i++) {
    universalSteps.push({
      stepId: i,
      stepTemplate: 'UNIVERSAL_GESTURE_TEMPLATE',
      executionMode: 'ONE_STEP_AT_A_TIME',

      // UNIVERSAL GESTURE POSSIBILITIES - ANY COMBINATION ALLOWED
      gestureCapabilities: {
        cursorMovement: {
          pathTypes: ['bezier_curve', 'linear', 'arc', 'spiral', 'zigzag'],
          startCoordinates: webviewCoordinates.webview.center,
          endCoordinates: webviewCoordinates.elements[0]?.center || webviewCoordinates.webview.center,
          speed: ['slow', 'normal', 'fast', 'natural_human'],
          variation: [0.1, 0.15, 0.2, 0.25]
        },
        clickActions: {
          types: ['left_click', 'right_click', 'double_click', 'middle_click'],
          duration: [50, 100, 150, 200],
          coordinates: 'calculated_or_specified'
        },
        gestureActions: {
          types: ['hover', 'drag', 'pinch', 'zoom', 'scroll', 'swipe', 'tap', 'long_press'],
          multiTouch: [1, 2, 3, 4, 5], // up to 5 fingers
          duration: [100, 500, 1000, 2000, 5000],
          simultaneous: true
        },
        keyboardActions: {
          types: ['key_press', 'key_hold', 'key_combination', 'text_input'],
          keys: 'any_key_combination'
        }
      },

      // ACTUAL STEP EXECUTION - Determined by MCP at runtime
      stepExecution: {
        primaryAction: this.getMCPStepAction(phaseType, i),
        coordinates: this.getMCPStepCoordinates(phaseType, i, webviewCoordinates),
        gestureCombination: this.getMCPGestureCombination(phaseType, i),
        pathCalculation: this.getMCPPathCalculation(phaseType, i, webviewCoordinates)
      },

      // PYTHON INSTRUCTION - Same template for every step
      pythonInstruction: 'Execute this step with specified gesture combination, then proceed to next step',

      // QUEUE CONTINUATION - Only for last step
      queueContinuation: (i === totalSteps) ? {
        isLastStep: true,
        action: 'continue_queue_to_next_package',
        trigger: 'MCP_DESIGNATED_COMPLETION',
        finalScriptLine: `MCP_PACKAGE_COMPLETE_${this.generateExecutionId()}`,
        nextPackageReady: true
      } : {
        isLastStep: false,
        action: 'continue_to_next_step',
        nextStepReady: true
      }
    });
  }

  return universalSteps;
}

/**
 * MCP server calculates how many steps needed for this phase
 */
calculateMCPStepsForPhase(phaseType) {
  // MCP determines step count based on complexity and requirements
  const mcpCalculatedSteps = {
    'cursor-movement': 3,        // MCP calculates: Path, Move, Action
    'element-interaction': 4,     // MCP calculates: Detect, Move, Gesture, Verify  
    'screenshot-analysis': 5,     // MCP calculates: Capture, Analyze, Coordinate, Move, Save
    'gesture-emulation': 6        // MCP calculates: Init, Calculate, Position, Execute, Verify, Action
  };

  return mcpCalculatedSteps[phaseType] || 3;
}

/**
 * MCP determines what action each step should perform
 */
getMCPStepAction(phaseType, stepId) {
  const mcpActionMatrix = {
    'cursor-movement': {
      1: 'calculate_bezier_path',
      2: 'execute_cursor_movement', 
      3: 'execute_right_click_gesture'
    },
    'element-interaction': {
      1: 'detect_and_analyze_element',
      2: 'move_cursor_to_element',
      3: 'execute_hover_then_left_click',
      4: 'verify_element_response'
    },
    'screenshot-analysis': {
      1: 'capture_webview_screenshot',
      2: 'ai_analyze_screenshot_elements',
      3: 'calculate_new_element_coordinates',
      4: 'move_cursor_to_analyzed_element',
      5: 'save_screenshot_and_analysis'
    },
    'gesture-emulation': {
      1: 'initialize_multi_touch_system',
      2: 'calculate_pinch_gesture_paths',
      3: 'position_two_finger_cursors',
      4: 'execute_pinch_zoom_gesture',
      5: 'verify_zoom_effect',
      6: 'execute_right_click_context_menu'
    }
  };

  return mcpActionMatrix[phaseType]?.[stepId] || 'execute_generic_action';
}

/**
 * MCP calculates coordinates for each step
 */
getMCPStepCoordinates(phaseType, stepId, webviewCoordinates) {
  // MCP calculates precise coordinates for each step
  return {
    primary: webviewCoordinates.elements[0]?.center || webviewCoordinates.webview.center,
    secondary: webviewCoordinates.webview.center,
    relative: {
      x: (webviewCoordinates.elements[0]?.x || webviewCoordinates.webview.x) - webviewCoordinates.webview.x,
      y: (webviewCoordinates.elements[0]?.y || webviewCoordinates.webview.y) - webviewCoordinates.webview.y
    }
  };
}

/**
 * MCP determines gesture combination for each step
 */
getMCPGestureCombination(phaseType, stepId) {
  const gestureCombinations = {
    'cursor-movement': {
      1: ['bezier_path_calculation'],
      2: ['smooth_cursor_movement'],
      3: ['right_click', 'context_menu']
    },
    'element-interaction': {
      1: ['element_detection', 'coordinate_analysis'],
      2: ['bezier_cursor_movement'],
      3: ['hover_gesture', 'left_click'],
      4: ['visual_verification']
    },
    'screenshot-analysis': {
      1: ['screenshot_capture'],
      2: ['ai_processing', 'element_detection'],
      3: ['coordinate_calculation'],
      4: ['bezier_cursor_movement'],
      5: ['data_saving', 'tab_update']
    },
    'gesture-emulation': {
      1: ['multi_touch_init'],
      2: ['dual_path_calculation'],
      3: ['simultaneous_cursor_positioning'],
      4: ['pinch_gesture', 'zoom_in'],
      5: ['effect_verification'],
      6: ['right_click', 'context_menu']
    }
  };

  return gestureCombinations[phaseType]?.[stepId] || ['generic_gesture'];
}

/**
 * MCP calculates path for each step  
 */
getMCPPathCalculation(phaseType, stepId, webviewCoordinates) {
  return {
    pathType: 'bezier_curve',
    controlPoints: this.generateBezierControlPoints(webviewCoordinates),
    startPoint: webviewCoordinates.webview.center,
    endPoint: webviewCoordinates.elements[0]?.center || webviewCoordinates.webview.center,
    variation: 0.15
  };
}

