      },
      {
        stepId: 6,
        stepType: 'MCP_COMPLETION',
        action: 'mcp_package_complete',
        executionMode: 'FINAL_STEP',
        trigger: 'MCP_DESIGNATED_COMPLETION',
        finalScriptLine: 'MCP_PACKAGE_COMPLETE',
        pythonInstruction: 'Print completion signal, advance queue to next package'
      }
    ];

    baseSteps['gesture-emulation'] = [
      {
        stepId: 1,
        stepType: 'GESTURE_INITIALIZATION',
        action: 'initialize_multi_touch_system',
        executionMode: 'ONE_STEP_AT_A_TIME',
        gestureType: 'two_finger_pinch',
        touchPoints: 2,
        initialSpread: 200,
        pythonInstruction: 'Initialize two-finger touch system, then proceed to next step'
      },
      {
        stepId: 2,
        stepType: 'COORDINATE_CALCULATION',
        action: 'calculate_gesture_coordinates',
        executionMode: 'ONE_STEP_AT_A_TIME',
        startPoints: [
          { x: webviewCoordinates.webview.centerX - 100, y: webviewCoordinates.webview.centerY },
          { x: webviewCoordinates.webview.centerX + 100, y: webviewCoordinates.webview.centerY }
        ],
        endPoints: [
          { x: webviewCoordinates.webview.centerX - 50, y: webviewCoordinates.webview.centerY },
          { x: webviewCoordinates.webview.centerX + 50, y: webviewCoordinates.webview.centerY }
        ],
        gestureType: 'pinch_zoom_in',
        pythonInstruction: 'Calculate pinch gesture coordinates, then continue to next step'
      },
      {
