        stepType: 'GESTURE_EXECUTION',
        action: 'execute_pinch_gesture',
        executionMode: 'ONE_STEP_AT_A_TIME',
        gestureType: 'pinch_zoom_in',
        duration: 1000,
        naturalVariation: true,
        simultaneousMovement: true,
        pythonInstruction: 'Execute pinch gesture with both fingers simultaneously, then continue to next step'
      },
      {
        stepId: 5,
        stepType: 'VERIFICATION',
        action: 'verify_zoom_effect',
        executionMode: 'ONE_STEP_AT_A_TIME',
        expectedChange: 'zoom_level_increased',
        timeoutMs: 2000,
        pythonInstruction: 'Verify zoom effect occurred, then proceed to next step'
      },
      {
        stepId: 6,
        stepType: 'RIGHT_CLICK_ACTION',
        action: 'execute_context_menu',
        executionMode: 'ONE_STEP_AT_A_TIME',
        coordinates: webviewCoordinates.webview.center,
        clickType: 'right_click',
        duration: 150,
        pythonInstruction: 'Execute right click for context menu, then continue to next step'
      },
      {
        stepId: 7,
        stepType: 'MCP_COMPLETION',
        action: 'mcp_package_complete',
