// src/mcp/phase-controller-server.js
class PhaseControllerMCP {
    constructor() {
        this.activePhases = new Map();
        this.templates = new Map();
        this.serverConnections = new Map();
    }

    async loadTemplate(templatePath) {
        // Load GTST-compliant phase template
        const template = await this.parseTemplate(templatePath);
        this.validateTemplate(template);
        this.templates.set(template.id, template);
    }

    async executePhase(phaseId, context) {
        const template = this.templates.get(phaseId);
        const phase = new PhaseExecution(template, context);

        // Coordinate sub-servers
        for (const step of template.steps) {
            const server = this.getServerForStep(step);
            const result = await server.execute(step, phase.context);
            phase.recordResult(step.id, result);

            // Send to N8N for analysis
            await this.notifyN8N(phase, step, result);
        }

        return phase.complete();
    }
}
```

#### **Server 2: Terminal Executor MCP**
```javascript
// src/mcp/terminal-executor-server.js
class TerminalExecutorMCP {
    constructor(ptyManager) {
        this.ptyManager = ptyManager;
        this.commandQueue = [];
        this.executionHistory = [];
    }

    async executeCommand(command, options = {}) {
        const execution = {
            id: generateId(),
            command,
            terminal: options.terminalId || 'primary',
            timestamp: Date.now(),
            context: options.context
        };

        // Pre-execution screenshot
        if (options.captureScreenshot) {
            execution.preScreenshot = await this.captureTerminalState();
        }

        // Execute command
        const result = await this.ptyManager.writeToTerminal(
            execution.terminal,
            command + '\r'
        );

        // Wait for output
        const output = await this.waitForOutput(execution.terminal, options.timeout);
        execution.output = output;

        // Post-execution screenshot
        if (options.captureScreenshot) {
            execution.postScreenshot = await this.captureTerminalState();
        }

        this.executionHistory.push(execution);
        return execution;
    }
}
```

