        for (const step of template.steps) {
            if (step.target.type === 'coordinate') {
                path.points.push([step.target.x, step.target.y]);
                path.timing.push(step.duration || 0.5);
                path.actions.push(step.action);
                path.metadata[`expected_element_${path.points.length - 1}`] = step.expected_element;
            } else if (step.target.type === 'ui') {
                // Convert UI selector to coordinates
                const element = document.querySelector(step.target.selector);
                if (element) {
                    const rect = element.getBoundingClientRect();
                    const x = rect.left + rect.width / 2;
                    const y = rect.top + rect.height / 2;
                    path.points.push([x, y]);
                    path.timing.push(step.duration || 0.5);
                    path.actions.push(step.action);
                }
            }
        }

        return path;
    }
}
