# src/automation/universal_pointer_controller.py
import time
from typing import List, Tuple, Dict, Optional
from dataclasses import dataclass
from selenium.webdriver.common.action_chains import ActionChains

@dataclass
class CoordinatePath:
    """Represents a planned path of (x,y) coordinates"""
    points: List[Tuple[int, int]]
    timing: List[float]  # Time to spend at each point
    actions: List[str]   # 'move', 'click', 'drag', 'hover'
    metadata: Dict[str, any]

class UniversalPointerController:
    """Controls pointer movement with visual feedback"""

    def __init__(self, driver, mcp_connection, visual_settings):
        self.driver = driver
        self.mcp = mcp_connection
        self.visual = visual_settings
        self.current_position = (0, 0)
        self.path_history = []

    async def execute_path(self, path: CoordinatePath) -> Dict:
        """Execute a planned coordinate path with visual feedback"""
        execution_result = {
            'id': str(uuid.uuid4()),
            'start_time': time.time(),
            'screenshots': [],
            'actual_path': [],
            'deviations': []
        }

        # Inject visual cursor
        await self.inject_visual_cursor()

        for i, (point, duration, action) in enumerate(
            zip(path.points, path.timing, path.actions)
        ):
            # Move to point with easing
            await self.move_to_point(point, duration)

            # Capture screenshot at this point
            screenshot = await self.capture_screenshot()
            execution_result['screenshots'].append({
                'index': i,
                'point': point,
                'screenshot': screenshot,
                'timestamp': time.time()
            })

            # Execute action
            if action == 'click':
                await self.click_at_point(point)
            elif action == 'drag':
                if i + 1 < len(path.points):
                    await self.drag_to_point(path.points[i + 1])

            # Report to MCP
            await self.mcp.report_position(point, screenshot)

            # Check for deviations
            actual_element = await self.get_element_at_point(point)
            if actual_element != path.metadata.get(f'expected_element_{i}'):
                execution_result['deviations'].append({
                    'index': i,
                    'expected': path.metadata.get(f'expected_element_{i}'),
                    'actual': actual_element
                })

        execution_result['end_time'] = time.time()
        execution_result['duration'] = execution_result['end_time'] - execution_result['start_time']

        return execution_result

    async def inject_visual_cursor(self):
        """Inject purple visual cursor with spell effects"""
        cursor_css = """
        .universal-cursor {
            position: fixed;
            width: 24px;
            height: 24px;
            border-radius: 50%;
            background: radial-gradient(circle, #9b59b6 0%, #8e44ad 50%, transparent 70%);
            box-shadow: 0 0 20px #9b59b6, 0 0 40px #8e44ad;
            pointer-events: none;
            z-index: 999999;
            transform: translate(-50%, -50%);
            transition: all 0.1s ease-out;
        }

        .universal-cursor::before {
            content: '';
            position: absolute;
            top: -10px;
            left: -10px;
            right: -10px;
            bottom: -10px;
            border-radius: 50%;
            border: 2px solid #9b59b6;
            animation: pulse 1s infinite;
        }

        @keyframes pulse {
            0% { transform: scale(1); opacity: 1; }
            100% { transform: scale(1.5); opacity: 0; }
        }

        .spell-circle {
            position: fixed;
            width: 100px;
            height: 100px;
            border-radius: 50%;
            border: 3px solid #9b59b6;
            opacity: 0;
            pointer-events: none;
            z-index: 999998;
            transform: translate(-50%, -50%);
            animation: spell-expand 0.5s ease-out;
        }

        @keyframes spell-expand {
            0% { transform: translate(-50%, -50%) scale(0.1); opacity: 1; }
            100% { transform: translate(-50%, -50%) scale(1); opacity: 0; }
        }
        """

        cursor_js = """
        (function() {
            // Remove existing cursor
            const existing = document.querySelector('.universal-cursor');
            if (existing) existing.remove();

            // Add styles
            const style = document.createElement('style');
            style.textContent = arguments[0];
            document.head.appendChild(style);

            // Create cursor element
            const cursor = document.createElement('div');
            cursor.className = 'universal-cursor';
            document.body.appendChild(cursor);

            // Track cursor globally
            window.universalCursor = cursor;

            // Update cursor position function
            window.updateUniversalCursor = function(x, y) {
                cursor.style.left = x + 'px';
                cursor.style.top = y + 'px';
            };

            // Create spell effect function
            window.createSpellEffect = function(x, y) {
                const spell = document.createElement('div');
                spell.className = 'spell-circle';
                spell.style.left = x + 'px';
                spell.style.top = y + 'px';
                document.body.appendChild(spell);
                setTimeout(() => spell.remove(), 500);
            };
        })(arguments[0]);
        """

        self.driver.execute_script(cursor_js, cursor_css)
