        if (selected) {
            await this.executePhase(selected);
        }
    }

    async executePhase(template) {
        this.activePhase = {
            template,
            startTime: Date.now(),
            steps: [],
            screenshots: []
        };

        // Update UI
        this.updatePhaseButton('executing', template.name);

        try {
            // Generate coordinate path
            const path = await this.generatePathForPhase(template);

            // Execute via Python
            const result = await this.python.executePath(path);

            // Analyze results
            await this.analyzeExecution(result);

            // Report success
            this.updatePhaseButton('success', 'Phase Complete');

        } catch (error) {
            // Handle errors
            await this.handlePhaseError(error);
            this.updatePhaseButton('error', 'Phase Failed');
        }
    }

    async generatePathForPhase(template) {
        const path = {
            points: [],
            timing: [],
            actions: [],
            metadata: {}
        };

        for (const step of template.steps) {
            if (step.target.type === 'coordinate') {
                path.points.push([step.target.x, step.target.y]);
                path.timing.push(step.duration || 0.5);
                path.actions.push(step.action);
                path.metadata[`expected_element_${path.points.length - 1}`] = step.expected_element;
            } else if (step.target.type === 'ui') {
                // Convert UI selector to coordinates
                const element = document.querySelector(step.target.selector);
                if (element) {
                    const rect = element.getBoundingClientRect();
                    const x = rect.left + rect.width / 2;
                    const y = rect.top + rect.height / 2;
                    path.points.push([x, y]);
                    path.timing.push(step.duration || 0.5);
                    path.actions.push(step.action);
                }
            }
        }

        return path;
    }
}
