#### **Server 1: Phase Controller MCP (MVP Enhanced)**
```javascript
// src/mcp/phase-controller-server.js
class PhaseControllerMCP {
    constructor() {
        this.activePhases = new Map();
        this.templates = new Map();
        this.serverConnections = new Map();
        this.webviewBoundaries = null;
        this.pythonScriptState = 'NOT_INITIALIZED';
        this.mvpConfig = MVP_MCP_CONFIG;
    }

    // MVP CRITICAL: Initialize with webview awareness
    async initializeForMVP(webviewElement) {
        // Get webview boundaries
        const rect = webviewElement.getBoundingClientRect();
        this.webviewBoundaries = {
            x: rect.left,
            y: rect.top,
            width: rect.width,
            height: rect.height,
            element: webviewElement
        };

        // Validate Python script can access webview
        const pythonReady = await this.validatePythonWebviewAccess();
        if (!pythonReady) {
            throw new Error('MVP BLOCKER: Python script cannot access webview');
        }

        this.pythonScriptState = 'WEBVIEW_READY';
    }

    async loadTemplate(templatePath) {
        const template = await this.parseTemplate(templatePath);

        // MVP VALIDATION: Ensure all coordinates are webview-relative
        for (const step of template.steps) {
            if (step.target.type === 'coordinate') {
                this.validateWebviewCoordinates(step.target.coordinates);
            }
        }

        this.templates.set(template.id, template);
    }

    async executePhase(phaseId, context) {
        // MVP PRE-CHECK: Ensure webview is active
        if (!await this.isWebviewActive()) {
            throw new Error('MVP VIOLATION: Webview not active for phase execution');
        }

        const template = this.templates.get(phaseId);
        const phase = new PhaseExecution(template, {
            ...context,
            webviewBoundaries: this.webviewBoundaries,
            coordinateMode: 'WEBVIEW_RELATIVE'
        });

        // Execute with webview constraints
        for (const step of template.steps) {
            // Convert any screen coordinates to webview-relative
            if (step.target.coordinates) {
                step.target.coordinates = this.toWebviewCoordinates(step.target.coordinates);
            }

            const server = this.getServerForStep(step);
            const result = await server.execute(step, phase.context);

            // MVP: Validate execution stayed within webview
            if (result.executionPath) {
                this.validateExecutionPath(result.executionPath);
            }

            phase.recordResult(step.id, result);
            await this.notifyN8N(phase, step, result);
        }

        return phase.complete();
    }

    validateWebviewCoordinates(coords) {
        if (coords.x < 0 || coords.x > this.webviewBoundaries.width ||
            coords.y < 0 || coords.y > this.webviewBoundaries.height) {
            throw new Error(`MVP VIOLATION: Coordinates (${coords.x}, ${coords.y}) outside webview boundaries`);
        }
    }

    toWebviewCoordinates(screenCoords) {
        return {
            x: screenCoords.x - this.webviewBoundaries.x,
            y: screenCoords.y - this.webviewBoundaries.y
        };
    }
}
```

