/**
 * Comprehensive Canvas State Diagnostics System
 * 
 * Captures ALL system states as defined in diagnostic-framework.md
 * Implements real-time state monitoring and analysis
 * 
 * Date: 2025-07-25
 * Priority: P0 CRITICAL
 */

class CanvasStateDiagnostics {
    constructor() {
        this.stateHistory = [];
        this.maxHistorySize = 100;
        this.monitoringActive = false;
        this.errorLog = [];
        this.performanceMetrics = {};

        // Initialize diagnostic dashboard
        this.createDiagnosticDashboard();

        console.log('ğŸ” Canvas State Diagnostics System initialized');
    }

    /**
     * Capture complete system state as per diagnostic framework requirements
     */
    captureCompleteSystemState() {
        const timestamp = new Date().toISOString();

        try {
            const state = {
                timestamp,
                editor: this.captureEditorState(),
                tools: this.captureToolState(),
                canvas: this.captureCanvasState(),
                shapes: this.captureShapeState(),
                input: this.captureInputState(),
                errors: this.captureErrorState(),
                performance: this.capturePerformanceState()
            };

            // Add to history
            this.stateHistory.push(state);
            if (this.stateHistory.length > this.maxHistorySize) {
                this.stateHistory.shift();
            }

            // Update visual dashboard
            this.updateDashboard(state);

            return state;
        } catch (error) {
            console.error('âŒ Failed to capture system state:', error);
            this.logError('STATE_CAPTURE_FAILED', error);
            return null;
        }
    }

    /**
     * 1. Editor State Diagnosis
     */
    captureEditorState() {
        const editorState = {
            initialized: false,
            available: false,
            canvas: null,
            store: null,
            currentTool: null,
            apiMethods: {}
        };

        try {
            // Check if editor exists
            if (typeof window !== 'undefined' && window.tlEditor) {
                editorState.available = true;
                editorState.initialized = window.tlEditor.initialized || false;

                // Canvas properties
                if (window.tlEditor.canvas) {
                    editorState.canvas = {
                        exists: true,
                        width: window.tlEditor.canvas.width,
                        height: window.tlEditor.canvas.height,
                        style: window.tlEditor.canvas.style?.display || 'unknown'
                    };
                }

                // Store state
                if (window.tlEditor.store) {
                    editorState.store = {
                        exists: true,
                        hasShapes: window.tlEditor.store.allRecords?.length > 0,
                        recordCount: window.tlEditor.store.allRecords?.length || 0
                    };
                }

                // Current tool
                try {
                    editorState.currentTool = window.tlEditor.getCurrentTool?.() || 'unknown';
                } catch (e) {
                    editorState.currentTool = `error: ${e.message}`;
                }

                // API method availability
                editorState.apiMethods = {
                    setCurrentTool: typeof window.tlEditor.setCurrentTool === 'function',
                    createShapes: typeof window.tlEditor.createShapes === 'function',
                    getCurrentTool: typeof window.tlEditor.getCurrentTool === 'function',
                    getShapes: typeof window.tlEditor.getShapes === 'function'
                };
            }
        } catch (error) {
            editorState.error = error.message;
        }

        return editorState;
    }

    /**
     * 2. Tool System State Diagnosis
     */
    captureToolState() {
        const toolState = {
            availableTools: [],
            currentActiveTool: null,
            toolButtonStates: {},
            stateMachine: null,
            eventHandlers: {}
        };

        try {
            // Available tools from UI
            const toolButtons = document.querySelectorAll('[data-tool]');
            toolButtons.forEach(button => {
                const toolName = button.dataset.tool;
                toolState.availableTools.push(toolName);
                toolState.toolButtonStates[toolName] = {
                    exists: true,
                    visible: button.style.display !== 'none',
                    active: button.classList.contains('active'),
                    enabled: !button.disabled
                };
            });

            // Check for StateChart tool system
            if (typeof window.rootState !== 'undefined') {
                toolState.stateMachine = {
                    exists: true,
                    currentState: window.rootState.currentState || 'unknown',
                    hasSetTool: typeof window.rootState.setTool === 'function'
                };
            }

            // Universal Input System
            if (typeof window.UniversalInputSystem !== 'undefined') {
                toolState.universalInput = {
                    exists: true,
                    initialized: window.UniversalInputSystem.initialized || false,
                    conflictsWith: 'StateChart system present'
                };
            }

        } catch (error) {
            toolState.error = error.message;
        }

        return toolState;
    }

    /**
     * 3. Canvas State Diagnosis
     */
    captureCanvasState() {
        const canvasState = {
            domElement: null,
            position: {},
            dimensions: {},
            eventListeners: {},
            rendering: {},
            coordinates: {}
        };

        try {
            // Find canvas element
            const canvas = document.querySelector('canvas') || 
                         document.querySelector('.tl-canvas') ||
                         document.querySelector('#canvas');

            if (canvas) {
                canvasState.domElement = {
                    exists: true,
                    tagName: canvas.tagName,
                    id: canvas.id,
                    classes: Array.from(canvas.classList)
                };

                // Position and dimensions
                const rect = canvas.getBoundingClientRect();
                canvasState.position = {
                    x: rect.x,
                    y: rect.y,
                    left: rect.left,
                    top: rect.top
                };

                canvasState.dimensions = {
                    width: rect.width,
                    height: rect.height,
                    clientWidth: canvas.clientWidth,
                    clientHeight: canvas.clientHeight
                };

                // Event listeners (approximate count)
                canvasState.eventListeners = {
                    hasClickListener: canvas.onclick !== null,
                    hasMouseListeners: canvas.onmousedown !== null || canvas.onmousemove !== null,
                    hasTouchListeners: canvas.ontouchstart !== null || canvas.ontouchmove !== null
                };
            }

            // Check for camera/viewport
            if (window.tlEditor?.camera) {
                canvasState.coordinates = {
                    camera: {
                        x: window.tlEditor.camera.x,
                        y: window.tlEditor.camera.y,
                        z: window.tlEditor.camera.z
                    },
                    hasScreenToPage: typeof window.tlEditor.camera.screenToPage === 'function'
                };
            }

        } catch (error) {
            canvasState.error = error.message;
        }

        return canvasState;
    }

    /**
     * 4. Shape System State Diagnosis
     */
    captureShapeState() {
        const shapeState = {
            currentCount: 0,
            creationMethods: {},
            dataModel: {},
            renderingPipeline: {},
            storeSync: {}
        };

        try {
            // Current shape count
            if (window.tlEditor?.store?.allRecords) {
                const shapes = window.tlEditor.store.allRecords.filter(r => r.typeName === 'shape');
                shapeState.currentCount = shapes.length;
                shapeState.dataModel = {
                    hasShapes: shapes.length > 0,
                    shapeTypes: [...new Set(shapes.map(s => s.type))]
                };
            }

            // Shape creation methods
            shapeState.creationMethods = {
                editorCreateShapes: typeof window.tlEditor?.createShapes === 'function',
                editorCreateShape: typeof window.tlEditor?.createShape === 'function',
                shapeUtilRegistry: typeof window.ShapeUtilRegistry !== 'undefined'
            };

            // Check for shape utilities
            if (typeof window.ShapeUtilRegistry !== 'undefined') {
                shapeState.renderingPipeline = {
                    registryExists: true,
                    hasRectangleUtil: window.ShapeUtilRegistry.has?.('rectangle') || false,
                    hasEllipseUtil: window.ShapeUtilRegistry.has?.('ellipse') || false
                };
            }

        } catch (error) {
            shapeState.error = error.message;
        }

        return shapeState;
    }

    /**
     * 5. Input System State Diagnosis
     */
    captureInputState() {
        const inputState = {
            mousePointer: {},
            touchEvents: {},
            threeFingerGesture: {},
            eventPropagation: {},
            conflicts: {}
        };

        try {
            // Mouse/pointer event registration
            const canvas = document.querySelector('canvas');
            if (canvas) {
                inputState.mousePointer = {
                    canvasHasEvents: !!(canvas.onmousedown || canvas.onclick),
                    documentHasEvents: !!(document.onmousedown || document.onclick),
                    pointerEventsStyle: canvas.style.pointerEvents || 'auto'
                };

                // Touch event handlers
                inputState.touchEvents = {
                    canvasHasTouch: !!(canvas.ontouchstart || canvas.ontouchmove),
                    documentHasTouch: !!(document.ontouchstart || document.ontouchmove),
                    touchActionStyle: canvas.style.touchAction || 'auto'
                };
            }

            // Three-finger gesture detection
            if (typeof window.UniversalInputSystem !== 'undefined') {
                inputState.threeFingerGesture = {
                    systemExists: true,
                    hasGestureHandler: typeof window.UniversalInputSystem.handleThreeFingerDrag === 'function',
                    isActive: window.UniversalInputSystem.isActive || false
                };
            }

            // Check for input method conflicts
            const systemCount = [
                typeof window.UniversalInputSystem !== 'undefined',
                typeof window.rootState !== 'undefined',
                typeof window.tlEditor?.inputManager !== 'undefined'
            ].filter(Boolean).length;

            inputState.conflicts = {
                multipleInputSystems: systemCount > 1,
                systemCount,
                systems: {
                    universal: typeof window.UniversalInputSystem !== 'undefined',
                    stateChart: typeof window.rootState !== 'undefined',
                    editorInput: typeof window.tlEditor?.inputManager !== 'undefined'
                }
            };

        } catch (error) {
            inputState.error = error.message;
        }

        return inputState;
    }

    /**
     * 6. Error State Diagnosis
     */
    captureErrorState() {
        return {
            recentErrors: this.errorLog.slice(-10),
            errorCount: this.errorLog.length,
            lastError: this.errorLog[this.errorLog.length - 1] || null
        };
    }

    /**
     * 7. Performance State Diagnosis
     */
    capturePerformanceState() {
        return {
            timestamp: performance.now(),
            memory: performance.memory ? {
                used: performance.memory.usedJSHeapSize,
                total: performance.memory.totalJSHeapSize,
                limit: performance.memory.jsHeapSizeLimit
            } : null,
            timing: performance.timing ? {
                loadComplete: performance.timing.loadEventEnd > 0,
                domReady: performance.timing.domContentLoadedEventEnd > 0
            } : null
        };
    }

    /**
     * Create visual diagnostic dashboard
     */
    createDiagnosticDashboard() {
        const dashboard = document.createElement('div');
        dashboard.id = 'diagnostic-dashboard';
        dashboard.style.cssText = `
            position: fixed;
            top: 10px;
            right: 10px;
            width: 300px;
            max-height: 80vh;
            background: rgba(0, 0, 0, 0.9);
            color: #00ff00;
            font-family: monospace;
            font-size: 12px;
            padding: 10px;
            border-radius: 5px;
            z-index: 10000;
            overflow-y: auto;
            border: 1px solid #00ff00;
        `;

        dashboard.innerHTML = `
            <div style="border-bottom: 1px solid #00ff00; margin-bottom: 10px; padding-bottom: 5px;">
                <strong>ğŸ” Canvas State Diagnostics</strong>
                <button id="toggle-monitoring" style="float: right; background: #000; color: #00ff00; border: 1px solid #00ff00;">Start</button>
            </div>
            <div id="state-display">Initializing...</div>
        `;

        document.body.appendChild(dashboard);

        // Toggle monitoring
        document.getElementById('toggle-monitoring').onclick = () => {
            this.toggleMonitoring();
        };
    }

    /**
     * Update visual dashboard with current state
     */
    updateDashboard(state) {
        const display = document.getElementById('state-display');
        if (!display) return;

        const html = `
            <div><strong>ğŸ“Š System Status:</strong></div>
            <div>Editor: ${state.editor.available ? 'âœ…' : 'âŒ'} (${state.editor.initialized ? 'init' : 'not-init'})</div>
            <div>Canvas: ${state.canvas.domElement?.exists ? 'âœ…' : 'âŒ'}</div>
            <div>Tools: ${state.tools.availableTools.length} available</div>
            <div>Shapes: ${state.shapes.currentCount} total</div>
            <div>Current Tool: ${state.tools.currentActiveTool || state.editor.currentTool || 'none'}</div>

            <div style="margin-top: 10px;"><strong>ğŸ”§ Input Systems:</strong></div>
            <div>Universal: ${state.input.conflicts.systems.universal ? 'âœ…' : 'âŒ'}</div>
            <div>StateChart: ${state.input.conflicts.systems.stateChart ? 'âœ…' : 'âŒ'}</div>
            <div>Conflicts: ${state.input.conflicts.multipleInputSystems ? 'âš ï¸' : 'âœ…'}</div>

            <div style="margin-top: 10px;"><strong>âš¡ Performance:</strong></div>
            <div>Memory: ${state.performance.memory ? Math.round(state.performance.memory.used / 1024 / 1024) + 'MB' : 'N/A'}</div>
            <div>Errors: ${state.errors.errorCount}</div>

            <div style="margin-top: 10px; font-size: 10px;">
                Last update: ${new Date(state.timestamp).toLocaleTimeString()}
            </div>
        `;

        display.innerHTML = html;
    }

    /**
     * Toggle real-time monitoring
     */
    toggleMonitoring() {
        const button = document.getElementById('toggle-monitoring');

        if (this.monitoringActive) {
            clearInterval(this.monitoringInterval);
            this.monitoringActive = false;
            button.textContent = 'Start';
            button.style.background = '#000';
        } else {
            this.monitoringInterval = setInterval(() => {
                this.captureCompleteSystemState();
            }, 1000);
            this.monitoringActive = true;
            button.textContent = 'Stop';
            button.style.background = '#003300';
        }
    }

    /**
     * Log errors for state diagnosis
     */
    logError(type, error) {
        this.errorLog.push({
            timestamp: new Date().toISOString(),
            type,
            message: error.message || error,
            stack: error.stack
        });
    }

    /**
     * Monitor state changes during user interactions
     */
    monitorUserInteraction(actionName, beforeCallback, afterCallback) {
        const beforeState = this.captureCompleteSystemState();
        console.log(`ğŸ¯ Before ${actionName}:`, beforeState);

        try {
            if (beforeCallback) beforeCallback();

            // Wait a moment for state to update
            setTimeout(() => {
                const afterState = this.captureCompleteSystemState();
                console.log(`ğŸ¯ After ${actionName}:`, afterState);

                // Compare states
                this.compareStates(beforeState, afterState, actionName);

                if (afterCallback) afterCallback(beforeState, afterState);
            }, 100);

        } catch (error) {
            this.logError(`INTERACTION_${actionName.toUpperCase()}`, error);
        }
    }

    /**
     * Compare two states and highlight changes
     */
    compareStates(before, after, actionName) {
        const changes = {};

        // Compare key metrics
        if (before.editor.currentTool !== after.editor.currentTool) {
            changes.toolChanged = {
                from: before.editor.currentTool,
                to: after.editor.currentTool
            };
        }

        if (before.shapes.currentCount !== after.shapes.currentCount) {
            changes.shapeCountChanged = {
                from: before.shapes.currentCount,
                to: after.shapes.currentCount
            };
        }

        if (Object.keys(changes).length > 0) {
            console.log(`ğŸ”„ State changes after ${actionName}:`, changes);
        }

        return changes;
    }

    /**
     * Export complete diagnostic report
     */
    exportDiagnosticReport() {
        const currentState = this.captureCompleteSystemState();
        const report = {
            timestamp: new Date().toISOString(),
            currentState,
            stateHistory: this.stateHistory,
            errorLog: this.errorLog,
            summary: this.generateSummary(currentState)
        };

        console.log('ğŸ“Š Complete Diagnostic Report:', report);
        return report;
    }

    /**
     * Generate summary of current system health
     */
    generateSummary(state) {
        const issues = [];
        const successes = [];

        // Check for issues
        if (!state.editor.available) issues.push('Editor not available');
        if (!state.editor.initialized) issues.push('Editor not initialized');
        if (!state.canvas.domElement?.exists) issues.push('Canvas element missing');
        if (state.input.conflicts.multipleInputSystems) issues.push('Multiple input systems detected');
        if (state.shapes.currentCount === 0 && state.tools.availableTools.length > 0) issues.push('No shapes despite tools available');

        // Check for successes
        if (state.editor.available && state.editor.initialized) successes.push('Editor properly initialized');
        if (state.canvas.domElement?.exists) successes.push('Canvas element present');
        if (state.tools.availableTools.length > 0) successes.push(`${state.tools.availableTools.length} tools available`);
        if (!state.input.conflicts.multipleInputSystems) successes.push('No input system conflicts');

        return {
            overallHealth: issues.length === 0 ? 'HEALTHY' : issues.length < 3 ? 'WARNING' : 'CRITICAL',
            issues,
            successes,
            issueCount: issues.length,
            successCount: successes.length
        };
    }
}

// Global diagnostic instance
window.CanvasStateDiagnostics = CanvasStateDiagnostics;

// Auto-initialize when DOM is ready
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', () => {
        window.canvasStateDiag = new CanvasStateDiagnostics();
    });
} else {
    window.canvasStateDiag = new CanvasStateDiagnostics();
}

console.log('âœ… Comprehensive State Diagnostics System loaded');

