                print(f"\nğŸ”§ {len(failed_tests)} ISSUES FOUND - CODE EDITING REQUIRED")

            # Wait for code editing or exit
            if not self.wait_for_code_editing():
                break

    def generate_dez_validation_report(self, results):
        """Generate report for DEZ validation"""
        print("\n" + "="*60)
        print("ğŸ“‹ DEZ VALIDATION REPORT")
        print("="*60)

        print("ğŸ¯ TRIPLE VERIFICATION RESULTS:")
        print("   All tests passed Visual + Backend + State verification")
        print("   Ready for human validation")

        print("\nâœ… CONFIRMED WORKING:")
        for test, passed in results.items():
            if passed:
                print(f"   âœ… {test.replace('_', ' ').title()}")

        print("\nğŸ” TEST METHODOLOGY:")
        print("   1. Visual verification: UI responds correctly")
        print("   2. Backend verification: Functions work properly") 
        print("   3. State verification: Data model updates correctly")

        print("\nğŸš¨ IMPORTANT:")
        print("   These are automated test results")
        print("   DEZ validation still required to confirm user experience")
        print("   If DEZ reports issues, diagnostic tools need improvement")

        # Export detailed results
        report_file = f"dez_validation_report_{int(time.time())}.json"
        with open(report_file, 'w') as f:
            json.dump({
                'timestamp': time.time(),
                'results': results,
                'verification_log': self.verification_log,
                'state_snapshots': self.state_snapshots[-10:]  # Last 10 snapshots
            }, f, indent=2)

        print(f"\nğŸ“„ Detailed report saved: {report_file}")

    def cleanup(self):
        """Clean up browser session"""
        if self.driver:
            print("\nğŸ”„ Cleaning up browser session...")
            self.driver.quit()
            self.session_active = False

def main():
    diagnostic = TripleVerificationDiagnostic()

    try:
        diagnostic.initialize_persistent_session()
        diagnostic.run_comprehensive_test_cycle()
    except KeyboardInterrupt:
        print("\nâ¹ï¸  Test interrupted by user")
    except Exception as e:
        print(f"\nâŒ Error during testing: {e}")
    finally:
        diagnostic.cleanup()

if __name__ == "__main__":
    main()

