#!/usr/bin/env python3
"""
Enhanced Visual Diagnostic Runner with Triple Verification
Implements: Visual + Backend + State monitoring with persistent session

Requirements:
- Keep browser open during entire testing session
- Capture comprehensive state data before/after each action
- Enable live code editing and re-testing
- Triple verification: Visual + Backend + State data
- Report findings for DEZ validation only after all verifications pass

Date: 2025-07-25
Priority: P0 CRITICAL
"""

import time
import json
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException

class TripleVerificationDiagnostic:
    def __init__(self):
        self.driver = None
        self.wait = None
        self.session_active = False
        self.test_results = []
        self.state_snapshots = []
        self.verification_log = []

        print("ğŸ” Triple Verification Diagnostic System Starting...")
        print("ğŸ“‹ Workflow: Visual â†’ Backend â†’ State â†’ Code Edit â†’ Re-verify")

    def initialize_persistent_session(self):
        """Initialize browser session that stays open for entire testing workflow"""
        chrome_options = Options()
        chrome_options.add_argument("--disable-web-security")
        chrome_options.add_argument("--disable-features=VizDisplayCompositor")
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_experimental_option("useAutomationExtension", False)
        chrome_options.add_experimental_option("excludeSwitches", ["enable-automation"])

        self.driver = webdriver.Chrome(options=chrome_options)
        self.wait = WebDriverWait(self.driver, 10)
        self.session_active = True

        # Navigate to canvas
        print("ğŸŒ Opening canvas page...")
        self.driver.get("http://localhost:8080/public/canvas.html")

        # Wait for page to fully load
        print("â³ Waiting for page to load completely...")
        self.wait.until(EC.presence_of_element_located((By.TAG_NAME, "body")))

        # Wait for toolbar buttons to appear
        try:
            self.wait.until(EC.presence_of_element_located((By.CSS_SELECTOR, "[data-tool]")))
            print("âœ… Toolbar buttons detected")
        except TimeoutException:
            print("âš ï¸ Toolbar buttons not found, continuing anyway...")

        # Additional wait for JavaScript initialization
        time.sleep(5)
        self.inject_comprehensive_diagnostics()

        print("âœ… Persistent session initialized - browser will stay open")
        print("ğŸ’¡ You can edit code and re-run tests in this same session")

    def inject_comprehensive_diagnostics(self):
        """Inject the comprehensive state diagnostics system"""
        print("ğŸ’‰ Injecting comprehensive state diagnostics...")

        try:
            # Load the comprehensive diagnostics script
            with open('/Users/dezmondhollins/my-web-app/public/js/comprehensive-state-diagnostics.js', 'r') as f:
                diagnostics_script = f.read()

            # Inject it into the page
            self.driver.execute_script(diagnostics_script)

            # Wait for initialization
            time.sleep(2)

            # Verify injection worked
            diagnostics_available = self.driver.execute_script("""
                return typeof window.CanvasStateDiagnostics !== 'undefined' && 
                       typeof window.canvasStateDiag !== 'undefined';
            """)

            if diagnostics_available:
                print("âœ… Comprehensive diagnostics injected successfully")
                # Start monitoring
                self.driver.execute_script("window.canvasStateDiag.toggleMonitoring();")
                print("ğŸ”„ Real-time state monitoring activated")
            else:
                print("âŒ Failed to inject diagnostics")

        except Exception as e:
            print(f"âŒ Error injecting diagnostics: {e}")

    def capture_triple_state(self, action_name):
        """Capture Visual + Backend + State data for triple verification"""
        print(f"\nğŸ¯ TRIPLE VERIFICATION: {action_name}")

        verification_data = {
            'timestamp': time.time(),
            'action': action_name,
            'visual': self.capture_visual_state(),
            'backend': self.capture_backend_state(),
            'state': self.capture_comprehensive_state()
        }

        self.state_snapshots.append(verification_data)
        return verification_data

    def capture_visual_state(self):
        """Capture what user can visually see"""
        try:
            # Take screenshot
            screenshot_path = f"screenshot_{int(time.time())}.png"
            self.driver.save_screenshot(screenshot_path)

            # Check visual elements
            visual_state = {
                'screenshot': screenshot_path,
                'tool_buttons_visible': [],
                'canvas_visible': False,
                'shapes_visible': 0,
                'error_messages_visible': []
            }

            # Check tool buttons
            tool_buttons = self.driver.find_elements(By.CSS_SELECTOR, "[data-tool]")
            for button in tool_buttons:
                if button.is_displayed():
                    visual_state['tool_buttons_visible'].append({
                        'tool': button.get_attribute('data-tool'),
                        'active': 'active' in button.get_attribute('class'),
                        'enabled': button.is_enabled()
                    })

            # Check canvas
            canvas_elements = self.driver.find_elements(By.TAG_NAME, "canvas") + \
                            self.driver.find_elements(By.CSS_SELECTOR, ".tl-canvas")
            visual_state['canvas_visible'] = len(canvas_elements) > 0

            # Check for visible shapes (SVG elements typically)
            shapes = self.driver.find_elements(By.CSS_SELECTOR, "svg rect, svg ellipse, svg path")
            visual_state['shapes_visible'] = len(shapes)

            # Check for error messages
            error_elements = self.driver.find_elements(By.CSS_SELECTOR, ".error, [class*='error']")
            for error in error_elements:
                if error.is_displayed() and error.text.strip():
                    visual_state['error_messages_visible'].append(error.text)

            return visual_state

        except Exception as e:
            return {'error': str(e)}

    def capture_backend_state(self):
        """Capture backend function and API state"""
        try:
            backend_state = self.driver.execute_script("""
                const state = {
                    editor_available: typeof window.tlEditor !== 'undefined',
                    editor_initialized: false,
                    functions_available: {},
                    console_errors: [],
                    api_responses: {}
                };

                // Check editor
                if (window.tlEditor) {
                    state.editor_initialized = window.tlEditor.initialized || false;

                    // Test key functions
                    state.functions_available = {
                        setCurrentTool: typeof window.tlEditor.setCurrentTool === 'function',
                        getCurrentTool: typeof window.tlEditor.getCurrentTool === 'function',
                        createShapes: typeof window.tlEditor.createShapes === 'function',
                        createShape: typeof window.tlEditor.createShape === 'function'
                    };

                    // Test API calls
                    try {
                        state.api_responses.getCurrentTool = window.tlEditor.getCurrentTool();
                    } catch (e) {
                        state.api_responses.getCurrentTool = 'ERROR: ' + e.message;
                    }
                }

                // Check for console errors (simplified)
                if (window.lastConsoleErrors) {
                    state.console_errors = window.lastConsoleErrors.slice(-5);
                }

                return state;
            """)

            return backend_state

        except Exception as e:
            return {'error': str(e)}

    def capture_comprehensive_state(self):
        """Capture complete system state via diagnostics system"""
        try:
            state_data = self.driver.execute_script("""
                if (window.canvasStateDiag) {
                    return window.canvasStateDiag.captureCompleteSystemState();
                } else {
                    return { error: 'Diagnostics system not available' };
                }
            """)

            return state_data

        except Exception as e:
            return {'error': str(e)}

    def test_tool_selection_workflow(self, tool_name):
        """Test complete tool selection with triple verification"""
        print(f"\nğŸ§ª TESTING TOOL SELECTION: {tool_name}")

        # Debug: Check what buttons are available
        available_buttons = self.driver.execute_script("""
            const buttons = document.querySelectorAll('[data-tool]');
            return Array.from(buttons).map(btn => ({
                tool: btn.dataset.tool,
                visible: btn.offsetParent !== null,
                enabled: !btn.disabled,
                text: btn.textContent.trim()
            }));
        """)
        print(f"ğŸ” Available buttons: {available_buttons}")

        # BEFORE state capture
        before_state = self.capture_triple_state(f"BEFORE_SELECT_{tool_name}")

        # Visual action: Click tool button
        try:
            # First try to find the button
            tool_buttons = self.driver.find_elements(By.CSS_SELECTOR, f"[data-tool='{tool_name}']")
            if not tool_buttons:
                print(f"âŒ No button found with data-tool='{tool_name}'")
                return False

            tool_button = tool_buttons[0]

            # Check if button is visible and enabled
            if not tool_button.is_displayed():
                print(f"âŒ Tool button {tool_name} is not visible")
                return False

            if not tool_button.is_enabled():
                print(f"âŒ Tool button {tool_name} is disabled")
                return False

            # Scroll to button if needed
            self.driver.execute_script("arguments[0].scrollIntoView(true);", tool_button)
            time.sleep(0.5)

            # Highlight button visually
            self.driver.execute_script("""
                arguments[0].style.border = '3px solid red';
                arguments[0].style.boxShadow = '0 0 10px red';
                arguments[0].style.backgroundColor = 'rgba(255,0,0,0.2)';
            """, tool_button)

            # Click with visual feedback
            ActionChains(self.driver).move_to_element(tool_button).click().perform()
            print(f"ğŸ‘† Clicked {tool_name} tool button")

            time.sleep(1)  # Allow state to update

        except Exception as e:
            print(f"âŒ Error clicking tool button {tool_name}: {e}")
            return False

        # AFTER state capture
        after_state = self.capture_triple_state(f"AFTER_SELECT_{tool_name}")

        # Triple verification
        verification_result = self.verify_tool_selection(before_state, after_state, tool_name)

        return verification_result

    def test_shape_creation_workflow(self, tool_name):
        """Test shape creation with pointer drag"""
        print(f"\nğŸ¨ TESTING SHAPE CREATION: {tool_name}")

        # Ensure tool is selected first
        if not self.test_tool_selection_workflow(tool_name):
            print(f"âŒ Cannot test shape creation - tool selection failed")
            return False

        # BEFORE creation state
        before_state = self.capture_triple_state(f"BEFORE_CREATE_{tool_name}")

        # Visual action: Drag to create shape
        try:
            canvas = self.driver.find_element(By.TAG_NAME, "canvas")

            # Perform drag gesture
            start_x, start_y = 100, 100
            end_x, end_y = 200, 200

            actions = ActionChains(self.driver)
            actions.move_to_element_with_offset(canvas, start_x, start_y)
            actions.click_and_hold()
            actions.move_by_offset(end_x - start_x, end_y - start_y)
            actions.release()
            actions.perform()

            print(f"ğŸ–±ï¸ Performed drag from ({start_x},{start_y}) to ({end_x},{end_y})")

            time.sleep(2)  # Allow shape creation to complete

        except NoSuchElementException:
            print("âŒ Canvas not found for shape creation")
            return False

        # AFTER creation state
        after_state = self.capture_triple_state(f"AFTER_CREATE_{tool_name}")

        # Triple verification
        verification_result = self.verify_shape_creation(before_state, after_state, tool_name)

        return verification_result

    def verify_tool_selection(self, before, after, tool_name):
        """Triple verification for tool selection"""
        print(f"ğŸ” TRIPLE VERIFICATION: Tool Selection for {tool_name}")

        verifications = {
            'visual': False,
            'backend': False,
            'state': False,
            'overall': False
        }

        # Visual verification
        try:
            after_buttons = after['visual']['tool_buttons_visible']
            target_button = next((b for b in after_buttons if b['tool'] == tool_name), None)

            if target_button and target_button['active']:
                verifications['visual'] = True
                print(f"  âœ… VISUAL: {tool_name} button shows as active")
            else:
                print(f"  âŒ VISUAL: {tool_name} button not active or not found")

        except Exception as e:
            print(f"  âŒ VISUAL: Error checking button state: {e}")

        # Backend verification
        try:
            current_tool = after['backend'].get('api_responses', {}).get('getCurrentTool')
            if current_tool == tool_name:
                verifications['backend'] = True
                print(f"  âœ… BACKEND: getCurrentTool() returns '{tool_name}'")
            else:
                print(f"  âŒ BACKEND: getCurrentTool() returns '{current_tool}', expected '{tool_name}'")

        except Exception as e:
            print(f"  âŒ BACKEND: Error checking current tool: {e}")

        # State verification
        try:
            state_tool = after['state'].get('editor', {}).get('currentTool')
            if state_tool == tool_name:
                verifications['state'] = True
                print(f"  âœ… STATE: Editor state shows current tool as '{tool_name}'")
            else:
                print(f"  âŒ STATE: Editor state shows '{state_tool}', expected '{tool_name}'")

        except Exception as e:
            print(f"  âŒ STATE: Error checking editor state: {e}")

        # Overall verification
        verifications['overall'] = all([verifications['visual'], verifications['backend'], verifications['state']])

        if verifications['overall']:
            print(f"  ğŸ‰ TRIPLE VERIFICATION PASSED: {tool_name} tool selection working correctly")
        else:
            print(f"  ğŸš¨ TRIPLE VERIFICATION FAILED: {tool_name} tool selection has issues")

        self.verification_log.append({
            'action': f'tool_selection_{tool_name}',
            'timestamp': time.time(),
            'verifications': verifications,
            'before_state': before,
            'after_state': after
        })

        return verifications['overall']

    def verify_shape_creation(self, before, after, tool_name):
        """Triple verification for shape creation"""
        print(f"ğŸ” TRIPLE VERIFICATION: Shape Creation with {tool_name}")

        verifications = {
            'visual': False,
            'backend': False,
            'state': False,
            'overall': False
        }

        # Visual verification
        try:
            before_shapes = before['visual']['shapes_visible']
            after_shapes = after['visual']['shapes_visible']

            if after_shapes > before_shapes:
                verifications['visual'] = True
                print(f"  âœ… VISUAL: Shape count increased from {before_shapes} to {after_shapes}")
            else:
                print(f"  âŒ VISUAL: No new shapes visible (before: {before_shapes}, after: {after_shapes})")

        except Exception as e:
            print(f"  âŒ VISUAL: Error checking shape count: {e}")

        # Backend verification
        try:
            # Check if createShapes function is available
            create_available = after['backend'].get('functions_available', {}).get('createShapes', False)
            if create_available:
                verifications['backend'] = True
                print(f"  âœ… BACKEND: createShapes function is available")
            else:
                print(f"  âŒ BACKEND: createShapes function not available")

        except Exception as e:
            print(f"  âŒ BACKEND: Error checking shape creation functions: {e}")

        # State verification
        try:
            before_count = before['state'].get('shapes', {}).get('currentCount', 0)
            after_count = after['state'].get('shapes', {}).get('currentCount', 0)

            if after_count > before_count:
                verifications['state'] = True
                print(f"  âœ… STATE: Shape count in store increased from {before_count} to {after_count}")
            else:
                print(f"  âŒ STATE: No increase in shape store (before: {before_count}, after: {after_count})")

        except Exception as e:
            print(f"  âŒ STATE: Error checking shape state: {e}")

        # Overall verification
        verifications['overall'] = all([verifications['visual'], verifications['backend'], verifications['state']])

        if verifications['overall']:
            print(f"  ğŸ‰ TRIPLE VERIFICATION PASSED: {tool_name} shape creation working correctly")
        else:
            print(f"  ğŸš¨ TRIPLE VERIFICATION FAILED: {tool_name} shape creation has issues")

        self.verification_log.append({
            'action': f'shape_creation_{tool_name}',
            'timestamp': time.time(),
            'verifications': verifications,
            'before_state': before,
            'after_state': after
        })

        return verifications['overall']

    def wait_for_code_editing(self):
        """Pause for manual code editing and re-testing"""
        print("\nâ¸ï¸  PAUSING FOR CODE EDITING")
        print("ğŸ’» You can now:")
        print("   1. Edit JavaScript files")
        print("   2. Fix any issues found")
        print("   3. Save changes")
        print("   4. Press ENTER to re-run tests")
        print("   5. Type 'quit' to exit")

        while True:
            user_input = input("\nğŸ”„ Press ENTER to re-test or 'quit' to exit: ").strip().lower()

            if user_input == 'quit':
                return False
            elif user_input == '':
                # Refresh page to reload code changes
                print("ğŸ”„ Refreshing page to load code changes...")
                self.driver.refresh()
                time.sleep(3)
                self.inject_comprehensive_diagnostics()
                return True
            else:
                print("â“ Please press ENTER to continue or type 'quit'")

    def run_comprehensive_test_cycle(self):
        """Run complete test cycle with ability to edit and re-test"""
        tools_to_test = ['select', 'rectangle', 'ellipse', 'eraser']

        while self.session_active:
            print("\n" + "="*60)
            print("ğŸš€ STARTING COMPREHENSIVE TEST CYCLE")
            print("="*60)

            cycle_results = {}

            # Test each tool
            for tool in tools_to_test:
                print(f"\nğŸ“‹ Testing {tool} tool...")

                # Test tool selection
                selection_result = self.test_tool_selection_workflow(tool)
                cycle_results[f'{tool}_selection'] = selection_result

                # Test shape creation (for creation tools)
                if tool in ['rectangle', 'ellipse']:
                    creation_result = self.test_shape_creation_workflow(tool)
                    cycle_results[f'{tool}_creation'] = creation_result

            # Summary
            print("\n" + "="*60)
            print("ğŸ“Š TEST CYCLE SUMMARY")
            print("="*60)

            passed_tests = [k for k, v in cycle_results.items() if v]
            failed_tests = [k for k, v in cycle_results.items() if not v]

            print(f"âœ… PASSED: {len(passed_tests)} tests")
            for test in passed_tests:
                print(f"   âœ… {test}")

            print(f"\nâŒ FAILED: {len(failed_tests)} tests")
            for test in failed_tests:
                print(f"   âŒ {test}")

            # Generate DEZ validation report
            if len(failed_tests) == 0:
                print("\nğŸ‰ ALL TESTS PASSED - READY FOR DEZ VALIDATION")
                self.generate_dez_validation_report(cycle_results)
            else:
                print(f"\nğŸ”§ {len(failed_tests)} ISSUES FOUND - CODE EDITING REQUIRED")

            # Wait for code editing or exit
            if not self.wait_for_code_editing():
                break

    def generate_dez_validation_report(self, results):
        """Generate report for DEZ validation"""
        print("\n" + "="*60)
        print("ğŸ“‹ DEZ VALIDATION REPORT")
        print("="*60)

        print("ğŸ¯ TRIPLE VERIFICATION RESULTS:")
        print("   All tests passed Visual + Backend + State verification")
        print("   Ready for human validation")

        print("\nâœ… CONFIRMED WORKING:")
        for test, passed in results.items():
            if passed:
                print(f"   âœ… {test.replace('_', ' ').title()}")

        print("\nğŸ” TEST METHODOLOGY:")
        print("   1. Visual verification: UI responds correctly")
        print("   2. Backend verification: Functions work properly") 
        print("   3. State verification: Data model updates correctly")

        print("\nğŸš¨ IMPORTANT:")
        print("   These are automated test results")
        print("   DEZ validation still required to confirm user experience")
        print("   If DEZ reports issues, diagnostic tools need improvement")

        # Export detailed results
        report_file = f"dez_validation_report_{int(time.time())}.json"
        with open(report_file, 'w') as f:
            json.dump({
                'timestamp': time.time(),
                'results': results,
                'verification_log': self.verification_log,
                'state_snapshots': self.state_snapshots[-10:]  # Last 10 snapshots
            }, f, indent=2)

        print(f"\nğŸ“„ Detailed report saved: {report_file}")

    def cleanup(self):
        """Clean up browser session"""
        if self.driver:
            print("\nğŸ”„ Cleaning up browser session...")
            self.driver.quit()
            self.session_active = False

def main():
    diagnostic = TripleVerificationDiagnostic()

    try:
        diagnostic.initialize_persistent_session()
        diagnostic.run_comprehensive_test_cycle()
    except KeyboardInterrupt:
        print("\nâ¹ï¸  Test interrupted by user")
    except Exception as e:
        print(f"\nâŒ Error during testing: {e}")
    finally:
        diagnostic.cleanup()

if __name__ == "__main__":
    main()

#!/usr/bin/env python3
"""
Enhanced Visual Diagnostic Runner with Triple Verification
Implements: Visual + Backend + State monitoring with persistent session

Requirements:
- Keep browser open during entire testing session
- Capture comprehensive state data before/after each action
- Enable live code editing and re-testing
- Triple verification: Visual + Backend + State data
- Report findings for DEZ validation only after all verifications pass

Date: 2025-07-25
Priority: P0 CRITICAL
"""

import time
import json
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException

class TripleVerificationDiagnostic:
    def __init__(self):
        self.driver = None
        self.wait = None
        self.session_active = False
        self.test_results = []
        self.state_snapshots = []
        self.verification_log = []

        print("ğŸ” Triple Verification Diagnostic System Starting...")
        print("ğŸ“‹ Workflow: Visual â†’ Backend â†’ State â†’ Code Edit â†’ Re-verify")

    def initialize_persistent_session(self):
        """Initialize browser session that stays open for entire testing workflow"""
        chrome_options = Options()
        chrome_options.add_argument("--disable-web-security")
        chrome_options.add_argument("--disable-features=VizDisplayCompositor")
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_experimental_option("useAutomationExtension", False)
        chrome_options.add_experimental_option("excludeSwitches", ["enable-automation"])

        self.driver = webdriver.Chrome(options=chrome_options)
        self.wait = WebDriverWait(self.driver, 10)
        self.session_active = True

        # Navigate to canvas
        print("ğŸŒ Opening canvas page...")
        self.driver.get("http://localhost:8080/public/canvas.html")

        # Wait for page to fully load
        print("â³ Waiting for page to load completely...")
        self.wait.until(EC.presence_of_element_located((By.TAG_NAME, "body")))

        # Wait for toolbar buttons to appear
        try:
            self.wait.until(EC.presence_of_element_located((By.CSS_SELECTOR, "[data-tool]")))
            print("âœ… Toolbar buttons detected")
        except TimeoutException:
            print("âš ï¸ Toolbar buttons not found, continuing anyway...")

        # Additional wait for JavaScript initialization
        time.sleep(5)
        self.inject_comprehensive_diagnostics()

        print("âœ… Persistent session initialized - browser will stay open")
        print("ğŸ’¡ You can edit code and re-run tests in this same session")

    def inject_comprehensive_diagnostics(self):
        """Inject the comprehensive state diagnostics system"""
        print("ğŸ’‰ Injecting comprehensive state diagnostics...")

        try:
            # Load the comprehensive diagnostics script
            with open('/Users/dezmondhollins/my-web-app/public/js/comprehensive-state-diagnostics.js', 'r') as f:
                diagnostics_script = f.read()

            # Inject it into the page
            self.driver.execute_script(diagnostics_script)

            # Wait for initialization
            time.sleep(2)

            # Verify injection worked
            diagnostics_available = self.driver.execute_script("""
                return typeof window.CanvasStateDiagnostics !== 'undefined' && 
                       typeof window.canvasStateDiag !== 'undefined';
            """)

            if diagnostics_available:
                print("âœ… Comprehensive diagnostics injected successfully")
                # Start monitoring
                self.driver.execute_script("window.canvasStateDiag.toggleMonitoring();")
                print("ğŸ”„ Real-time state monitoring activated")
            else:
                print("âŒ Failed to inject diagnostics")

        except Exception as e:
            print(f"âŒ Error injecting diagnostics: {e}")

    def capture_triple_state(self, action_name):
        """Capture Visual + Backend + State data for triple verification"""
        print(f"\nğŸ¯ TRIPLE VERIFICATION: {action_name}")

        verification_data = {
            'timestamp': time.time(),
            'action': action_name,
            'visual': self.capture_visual_state(),
            'backend': self.capture_backend_state(),
            'state': self.capture_comprehensive_state()
        }

        self.state_snapshots.append(verification_data)
        return verification_data

    def capture_visual_state(self):
        """Capture what user can visually see"""
        try:
            # Take screenshot
            screenshot_path = f"screenshot_{int(time.time())}.png"
            self.driver.save_screenshot(screenshot_path)

            # Check visual elements
            visual_state = {
                'screenshot': screenshot_path,
                'tool_buttons_visible': [],
                'canvas_visible': False,
                'shapes_visible': 0,
                'error_messages_visible': []
            }

            # Check tool buttons
            tool_buttons = self.driver.find_elements(By.CSS_SELECTOR, "[data-tool]")
            for button in tool_buttons:
                if button.is_displayed():
                    visual_state['tool_buttons_visible'].append({
                        'tool': button.get_attribute('data-tool'),
                        'active': 'active' in button.get_attribute('class'),
                        'enabled': button.is_enabled()
                    })

            # Check canvas
            canvas_elements = self.driver.find_elements(By.TAG_NAME, "canvas") + \
                            self.driver.find_elements(By.CSS_SELECTOR, ".tl-canvas")
            visual_state['canvas_visible'] = len(canvas_elements) > 0

            # Check for visible shapes (SVG elements typically)
            shapes = self.driver.find_elements(By.CSS_SELECTOR, "svg rect, svg ellipse, svg path")
            visual_state['shapes_visible'] = len(shapes)

            # Check for error messages
            error_elements = self.driver.find_elements(By.CSS_SELECTOR, ".error, [class*='error']")
            for error in error_elements:
                if error.is_displayed() and error.text.strip():
                    visual_state['error_messages_visible'].append(error.text)

            return visual_state

        except Exception as e:
            return {'error': str(e)}

    def capture_backend_state(self):
        """Capture backend function and API state"""
        try:
            backend_state = self.driver.execute_script("""
                const state = {
                    editor_available: typeof window.tlEditor !== 'undefined',
                    editor_initialized: false,
                    functions_available: {},
                    console_errors: [],
                    api_responses: {}
                };

                // Check editor
                if (window.tlEditor) {
                    state.editor_initialized = window.tlEditor.initialized || false;

                    // Test key functions
                    state.functions_available = {
                        setCurrentTool: typeof window.tlEditor.setCurrentTool === 'function',
                        getCurrentTool: typeof window.tlEditor.getCurrentTool === 'function',
                        createShapes: typeof window.tlEditor.createShapes === 'function',
                        createShape: typeof window.tlEditor.createShape === 'function'
                    };

                    // Test API calls
                    try {
                        state.api_responses.getCurrentTool = window.tlEditor.getCurrentTool();
                    } catch (e) {
                        state.api_responses.getCurrentTool = 'ERROR: ' + e.message;
                    }
                }

                // Check for console errors (simplified)
                if (window.lastConsoleErrors) {
                    state.console_errors = window.lastConsoleErrors.slice(-5);
                }

                return state;
            """)

            return backend_state

        except Exception as e:
            return {'error': str(e)}

    def capture_comprehensive_state(self):
        """Capture complete system state via diagnostics system"""
        try:
            state_data = self.driver.execute_script("""
                if (window.canvasStateDiag) {
                    return window.canvasStateDiag.captureCompleteSystemState();
                } else {
                    return { error: 'Diagnostics system not available' };
                }
            """)

            return state_data

        except Exception as e:
            return {'error': str(e)}

    def test_tool_selection_workflow(self, tool_name):
        """Test complete tool selection with triple verification"""
        print(f"\nğŸ§ª TESTING TOOL SELECTION: {tool_name}")

        # Debug: Check what buttons are available
        available_buttons = self.driver.execute_script("""
            const buttons = document.querySelectorAll('[data-tool]');
            return Array.from(buttons).map(btn => ({
                tool: btn.dataset.tool,
                visible: btn.offsetParent !== null,
                enabled: !btn.disabled,
                text: btn.textContent.trim()
            }));
        """)
        print(f"ğŸ” Available buttons: {available_buttons}")

        # BEFORE state capture
        before_state = self.capture_triple_state(f"BEFORE_SELECT_{tool_name}")

        # Visual action: Click tool button
        try:
            # First try to find the button
            tool_buttons = self.driver.find_elements(By.CSS_SELECTOR, f"[data-tool='{tool_name}']")
            if not tool_buttons:
                print(f"âŒ No button found with data-tool='{tool_name}'")
                return False

            tool_button = tool_buttons[0]

            # Check if button is visible and enabled
            if not tool_button.is_displayed():
                print(f"âŒ Tool button {tool_name} is not visible")
                return False

            if not tool_button.is_enabled():
                print(f"âŒ Tool button {tool_name} is disabled")
                return False

            # Scroll to button if needed
            self.driver.execute_script("arguments[0].scrollIntoView(true);", tool_button)
            time.sleep(0.5)

            # Highlight button visually
            self.driver.execute_script("""
                arguments[0].style.border = '3px solid red';
                arguments[0].style.boxShadow = '0 0 10px red';
                arguments[0].style.backgroundColor = 'rgba(255,0,0,0.2)';
            """, tool_button)

            # Click with visual feedback
            ActionChains(self.driver).move_to_element(tool_button).click().perform()
            print(f"ğŸ‘† Clicked {tool_name} tool button")

            time.sleep(1)  # Allow state to update

        except Exception as e:
            print(f"âŒ Error clicking tool button {tool_name}: {e}")
            return False

        # AFTER state capture
        after_state = self.capture_triple_state(f"AFTER_SELECT_{tool_name}")

