class GeoIdle extends StateNode {
    constructor() {
        super('idle');
    }

    onEvent(eventInfo) {
        console.log('GeoIdle: Received event:', eventInfo.type, 'at', eventInfo.currentPagePoint);
        if (eventInfo.type === TLEventType.POINTER_DOWN) {
            console.log('GeoIdle: Transitioning to creating state');
            return TLTransitionResult.transition('parent.creating', {
                origin: eventInfo.currentPagePoint
            });
        }
        return TLTransitionResult.ignored();
    }
}

class GeoCreating extends StateNode {
    constructor() {
        super('creating');
    }

    onEnter(info) {
        this.startPoint = info.origin;
        this.currentShape = this.createGeoShape(this.startPoint);
        this.editor.store.mark('geo-start');
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                this.updateShape(eventInfo.currentPagePoint);
                break;

            case TLEventType.POINTER_UP:
                this.completeShape();
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    createGeoShape(point) {
        const geoType = this.parent.geoType || 'rectangle';
        console.log('GeoCreating: Creating geo shape of type:', geoType, 'at point:', point);

        // CRITICAL FIX: Use correct shape type instead of non-existent TLShapeType.GEO
        const shapeType = geoType === 'ellipse' ? TLShapeType.ELLIPSE : TLShapeType.RECTANGLE;

        const shape = new TLShape(shapeType, null, {
            x: point.x,
            y: point.y,
            props: {
                w: 10,
                h: 10,
                color: '#000000',
                fill: 'none'
            }
        });

        // Add to store AND page (CRITICAL FIX!)
        this.editor.store.put(shape);

        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(shape.id);
            this.editor.store.put(page);
            console.log('GeoCreating: Added shape to page:', shape.id);
        } else {
            console.error('GeoCreating: No current page found!');
        }

        console.log('GeoCreating: Created geo shape:', shape.id);
        return shape;
    }

    updateShape(point) {
        if (!this.currentShape) return;

        const width = Math.abs(point.x - this.startPoint.x);
        const height = Math.abs(point.y - this.startPoint.y);
        const x = Math.min(point.x, this.startPoint.x);
        const y = Math.min(point.y, this.startPoint.y);

        console.log('GeoCreating: updateShape - start:', this.startPoint, 'current:', point);
        console.log('GeoCreating: updateShape - calculated bounds:', { x, y, width, height });

        this.currentShape.x = x;
        this.currentShape.y = y;
        this.currentShape.props.w = Math.max(width, 10);
        this.currentShape.props.h = Math.max(height, 10);

        console.log('GeoCreating: Shape updated to:', {
            id: this.currentShape.id,
            x: this.currentShape.x,
            y: this.currentShape.y,
            w: this.currentShape.props.w,
            h: this.currentShape.props.h
        });

        this.editor.store.put(this.currentShape);
    }

    completeShape() {
        this.editor.store.mark('geo-complete');
    }
}

