class TextEditing extends StateNode {
    constructor() {
        super('editing');
    }

    onEnter(info) {
        this.currentShape = this.createTextShape(info.origin);
        // Start text editing immediately
        this.startTextEditing();
    }

    onEvent(eventInfo) {
        if (eventInfo.type === TLEventType.POINTER_DOWN) {
            this.finishTextEditing();
            return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    startTextEditing() {
        if (!this.currentShape) return;

        // Create text input element
        this.textInput = document.createElement('input');
        this.textInput.type = 'text';
        this.textInput.value = this.currentShape.props.text || '';
        this.textInput.style.cssText = `
            position: absolute;
            left: ${this.currentShape.x}px;
            top: ${this.currentShape.y}px;
            width: ${this.currentShape.props.w || 100}px;
            height: ${this.currentShape.props.h || 24}px;
            font-size: ${this.currentShape.props.fontSize || 16}px;
            color: ${this.currentShape.props.color || '#000000'};
            border: 2px solid #3b82f6;
            background: white;
            z-index: 1000;
            font-family: Arial, sans-serif;
        `;

        // Add to canvas container
        const canvasContainer = this.editor.container;
        canvasContainer.appendChild(this.textInput);

        // Focus and select all text
        this.textInput.focus();
        this.textInput.select();

        // Handle input changes
        this.textInput.addEventListener('input', (e) => {
            this.updateTextContent(e.target.value);
        });

        // Handle enter key to finish editing
        this.textInput.addEventListener('keydown', (e) => {
            if (e.key === 'Enter') {
                this.finishTextEditing();
                this.editor.rootState.setCurrentTool('select');
            }
        });

        console.log('TextEditing: Started text editing interface');
    }

    updateTextContent(newText) {
        if (!this.currentShape) return;

        // Update shape text
        this.currentShape.props.text = newText;
        this.editor.store.put(this.currentShape);
    }

    finishTextEditing() {
        if (this.textInput && this.textInput.parentNode) {
            this.textInput.parentNode.removeChild(this.textInput);
            this.textInput = null;
            console.log('TextEditing: Finished text editing');
        }
    }

    createTextShape(point) {
        console.log('TextEditing: Creating text shape at point:', point);

        const shape = new TLShape(TLShapeType.TEXT, null, {
            x: point.x,
            y: point.y,
            props: {
                text: 'Text',
                fontSize: 16,
                color: '#000000',
                w: 100,
                h: 24
            }
        });

        // Add to store AND page (CRITICAL FIX!)
        this.editor.store.put(shape);

        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(shape.id);
            this.editor.store.put(page);
            console.log('TextEditing: Added text to page:', shape.id);
        } else {
            console.error('TextEditing: No current page found!');
        }

        console.log('TextEditing: Created text shape:', shape.id);
        this.editor.store.mark('text-created');
        return shape;
    }
}

