class HandIdle extends StateNode {
    constructor() {
        super('idle');
    }

    onEvent(eventInfo) {
        if (eventInfo.type === TLEventType.POINTER_DOWN) {
            return TLTransitionResult.transition('parent.panning', {
                origin: eventInfo.currentPagePoint
            });
        }
        return TLTransitionResult.ignored();
    }
}

class HandPanning extends StateNode {
    constructor() {
        super('panning');
    }

    onEnter(info) {
        console.log('HandPanning: onEnter with info:', info);
        this.lastScreenPoint = null; // Will be set on first move
        this.camera = this.editor.store.getCurrentCamera();
        console.log('HandPanning: Got camera:', this.camera);
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                this.updatePan(eventInfo);
                break;

            case TLEventType.POINTER_UP:
                console.log('HandPanning: Pan complete');
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    updatePan(eventInfo) {
        if (!this.camera) return;

        // Use screen coordinates for panning (not page coordinates!)
        const currentScreenPoint = eventInfo.screenPoint;

        if (!this.lastScreenPoint) {
            this.lastScreenPoint = currentScreenPoint;
            return;
        }

        // Calculate screen delta
        const screenDelta = {
            x: currentScreenPoint.x - this.lastScreenPoint.x,
            y: currentScreenPoint.y - this.lastScreenPoint.y
        };

        console.log('HandPanning: Screen delta:', screenDelta, 'Camera z:', this.camera.z);

        // Pan the camera by the screen delta (adjusted for zoom)
        this.camera.x += screenDelta.x / this.camera.z;
        this.camera.y += screenDelta.y / this.camera.z;

        this.editor.store.put(this.camera);
        this.lastScreenPoint = currentScreenPoint;
    }
}

