// Root State - Main state orchestrator for the editor
class RootState extends StateNode {
    constructor(editor) {
        super('root');
        this.editor = editor;
        this.currentTool = 'select';
        this.tools = new Map();
        this.initializeTools();
    }

    initializeTools() {
        // Register all tool states
        this.tools.set('select', new SelectTool(this.editor));
        this.tools.set('draw', new DrawTool(this.editor));
        this.tools.set('rectangle', new GeoTool(this.editor, 'rectangle'));
        this.tools.set('ellipse', new GeoTool(this.editor, 'ellipse'));
        this.tools.set('triangle', new GeoTool(this.editor, 'triangle'));
        this.tools.set('diamond', new GeoTool(this.editor, 'diamond'));
        this.tools.set('star', new GeoTool(this.editor, 'star'));
        this.tools.set('hexagon', new GeoTool(this.editor, 'hexagon'));
        this.tools.set('arrow', new ArrowTool(this.editor));
        this.tools.set('line', new LineTool(this.editor));
        this.tools.set('text', new TextTool(this.editor));
        this.tools.set('hand', new HandTool(this.editor));
        this.tools.set('eraser', new EraserTool(this.editor));

        // Create a reference geoTool for compatibility
        this.geoTool = this.tools.get('rectangle');

        console.log('ğŸ”„ RootState initialized with', this.tools.size, 'tools');
    }

    setCurrentTool(toolId) {
        console.log('ğŸ”§ RootState.setCurrentTool called with:', toolId);

        if (this.tools.has(toolId)) {
            // Exit current tool
            const currentTool = this.tools.get(this.currentTool);
            console.log('ğŸ”§ Current tool:', this.currentTool, currentTool);

            if (currentTool && currentTool.exit) {
                console.log('ğŸ”§ Calling exit on current tool');
                currentTool.exit();
            }

            // Set new tool
            this.currentTool = toolId;

            // Enter new tool
            const newTool = this.tools.get(toolId);
            console.log('ğŸ”§ New tool:', toolId, newTool);
            console.log('ğŸ”§ New tool type:', newTool?.constructor.name);
            console.log('ğŸ”§ New tool has enter:', !!newTool?.enter);
            console.log('ğŸ”§ New tool has handleEvent:', !!newTool?.handleEvent);
            console.log('ğŸ”§ New tool children:', newTool?.children?.size);

            if (newTool && newTool.enter) {
                console.log('ğŸ”§ Calling enter on new tool');
                newTool.enter();
                console.log('ğŸ”§ Active child after enter:', newTool.activeChildId);
            }

            console.log('âœ… Tool changed to:', toolId);
            return true;
        }
        console.warn('Tool not found:', toolId);
        return false;
    }

    // Alias for compatibility with tldraw-editor.js
    setTool(toolId) {
        return this.setCurrentTool(toolId);
    }

    getCurrentTool() {
        return this.tools.get(this.currentTool);
    }

    getCurrentChild() {
        return this.getCurrentTool();
    }

    enter() {
        super.enter();
        // Start with select tool
        this.setCurrentTool('select');
    }

    handleEvent(info) {
        console.log('ğŸ¯ RootState.handleEvent:', info.type);

        // Delegate to current tool
        const currentTool = this.getCurrentTool();
        console.log('ğŸ¯ Current tool:', currentTool?.id || 'none', currentTool?.constructor.name);

        if (currentTool && currentTool.handleEvent) {
            console.log('ğŸ¯ Delegating to tool handleEvent');
            const result = currentTool.handleEvent(info);
            console.log('ğŸ¯ Tool handleEvent result:', result);
            return result;
        }

        console.log('ğŸ¯ No tool handler, ignoring event');
        return { type: 'ignored' };
    }
}

