/**
 * TLDraw-style Editor - Main orchestrator class
 * Combines Store, StateChart, and Rendering like tldraw's Editor
 */

// Tool definitions
const tools = [
    { id: 'select', name: 'Select Tool', icon: 'â†–ï¸', shortcut: 'V' },
    { id: 'draw', name: 'Draw Tool', icon: 'âœï¸', shortcut: 'D' },
    { id: 'hand', name: 'Pan Tool', icon: 'âœ‹', shortcut: 'H' },
    { id: 'rectangle', name: 'Rectangle', icon: 'â¬œ', shortcut: 'R' },
    { id: 'ellipse', name: 'Ellipse', icon: 'â­•', shortcut: 'O' },
    { id: 'arrow', name: 'Arrow', icon: 'â¡ï¸', shortcut: 'A' },
    { id: 'line', name: 'Line', icon: 'ğŸ“', shortcut: 'L' },
    { id: 'text', name: 'Text', icon: 'ğŸ“', shortcut: 'T' },
    { id: 'eraser', name: 'Eraser', icon: 'ğŸ—‘ï¸', shortcut: 'X' }
];

class TLDrawEditor extends EventTarget {
    constructor(container) {
        super();

        this.container = container;
        this.canvas = null;
        this.ctx = null;

        // Core systems
        this.store = null;
        this.rootState = null;
        this.renderer = null;

        // Input handling - use Universal Input System if available
        if (window.UniversalInputSystem) {
            console.log('ğŸ® Using Universal Input System for three-finger drag support');
            this.universalInput = new UniversalInputSystem(this);
            // Keep inputManager reference for compatibility
            this.inputManager = {
                isEnabled: true,
                initialize: () => {},
                destroy: () => this.universalInput.destroy(),
                registerContextMenuSection: () => {},
                unregisterContextMenuSection: () => {},
                showContextMenu: () => {},
                setTool: (toolId) => this.universalInput.setTool(toolId)
            };
        } else {
            console.log('ğŸ“± Using standard Input Manager');
            this.inputManager = new InputManager(this);
        }

        // Performance tracking
        this.renderLoop = null;
        this.isRendering = false;

        this.initialize();
    }

    initialize() {
        console.log('TLDrawEditor: Initializing...');

        // Create canvas
        this.createCanvas();

        // Initialize store
        this.store = new TLStore();
        this.store.addEventListener('change', this.handleStoreChange.bind(this));

        // Initialize shape utilities registry
        this.shapeUtils = new ShapeUtilRegistry();
        console.log('ğŸ“Š Shape utilities registered:', this.shapeUtils.getAllTypes());

        // Initialize state system
        console.log('TLDrawEditor: Creating RootState...');
        this.rootState = new RootState(this);
        console.log('TLDrawEditor: RootState created:', !!this.rootState);
        console.log('TLDrawEditor: geoTool exists:', !!this.rootState.geoTool);

        if (this.rootState.geoTool) {
            console.log('TLDrawEditor: geoTool.setGeoType exists:', !!this.rootState.geoTool.setGeoType);
        }

        this.rootState.enter();
        console.log('TLDrawEditor: RootState entered');

        // Initialize renderer with camera for HTML container positioning
        this.renderer = new TLRenderer(this.canvas, this.store, this.getCamera());

        // Initialize HTML container system for embeds
        this.htmlContainer = new HTMLContainer(this.container, this.getCamera(), this.store);
        this.embedManager = new EmbedManager(this.htmlContainer, this.store);

        // Setup input handling
        this.inputManager.initialize();

        // Start render loop
        this.startRenderLoop();

        console.log('TLDrawEditor: Initialized successfully');
    }

    createCanvas() {
        this.canvas = document.createElement('canvas');
        this.canvas.style.width = '100%';
        this.canvas.style.height = '100%';
        this.canvas.style.display = 'block';
        this.canvas.style.touchAction = 'none';
        this.canvas.style.cursor = 'default';

        // Set actual size
        const rect = this.container.getBoundingClientRect();
        this.canvas.width = rect.width * window.devicePixelRatio;
        this.canvas.height = rect.height * window.devicePixelRatio;
        this.canvas.style.width = rect.width + 'px';
        this.canvas.style.height = rect.height + 'px';

        this.ctx = this.canvas.getContext('2d');
        this.ctx.scale(window.devicePixelRatio, window.devicePixelRatio);

        this.container.appendChild(this.canvas);

        // Handle resize
        window.addEventListener('resize', () => {
            this.handleResize();
        });
    }

    handleResize() {
        const rect = this.container.getBoundingClientRect();
        this.canvas.width = rect.width * window.devicePixelRatio;
        this.canvas.height = rect.height * window.devicePixelRatio;
        this.canvas.style.width = rect.width + 'px';
        this.canvas.style.height = rect.height + 'px';

        this.ctx.scale(window.devicePixelRatio, window.devicePixelRatio);
        this.render();
    }

    handleStoreChange(event) {
        // Re-render when store changes
        this.render();

        // Emit change event
        this.dispatchEvent(new CustomEvent('change', { 
            detail: event.detail 
        }));
    }

    startRenderLoop() {
        if (this.renderLoop) return;

        console.log('TLDrawEditor: Starting render loop');

        const renderFrame = () => {
            if (this.isRendering) {
                this.render();
            }
            this.renderLoop = requestAnimationFrame(renderFrame);
        };

        this.isRendering = true;
        this.renderLoop = requestAnimationFrame(renderFrame);
    }

    stopRenderLoop() {
        if (this.renderLoop) {
            cancelAnimationFrame(this.renderLoop);
            this.renderLoop = null;
        }
        this.isRendering = false;
    }

    render() {
        if (!this.renderer) {
            console.warn('TLDrawEditor: No renderer available');
            return;
        }

        // Add occasional debug logging (every 60 frames)
        if (this.renderer.renderStats.frameCount % 60 === 0) {
            console.log('TLDrawEditor: Rendering frame', this.renderer.renderStats.frameCount);
        }

        // Render canvas shapes
        this.renderer.render();

        // Render HTML embeds
        this.renderEmbeds();
    }

    renderEmbeds() {
        if (!this.embedManager || !this.htmlContainer) {
            return;
        }

        // Get all embed shapes from store
        const shapes = this.store.getCurrentPageShapes();
        const embedShapes = shapes.filter(shape => 
            shape.type === 'embed' || shape.type === 'youtube'
        );

        // Update HTML container positions for camera changes
        this.htmlContainer.updateCamera(this.getCamera());

        // Render each embed shape
        embedShapes.forEach(shape => {
            const existingElement = this.htmlContainer.getHTMLElement(shape.id);

            if (!existingElement) {
                // Create new embed
                console.log('ğŸ¥ Creating embed for shape:', shape.id, shape.type);
                this.embedManager.createEmbed(shape);
            } else {
                // Update existing embed if needed
                this.embedManager.updateEmbed(shape);
            }
        });

        // Clean up removed embeds
        this.htmlContainer.cleanup();
    }

    // Tool management
    setCurrentTool(toolId) {
        console.log('TLDrawEditor: Setting tool to', toolId);

        // Update universal input system if available
        if (this.universalInput) {
            console.log('ğŸ® Updating universal input tool to:', toolId);
            this.universalInput.setTool(toolId);
        }

        // CRITICAL FIX: Check if rootState and geoTool exist
        if (!this.rootState) {
            console.error('TLDrawEditor: rootState is undefined!');
            return false;
        }

        // Handle special geo tool cases - FIX: Call the actual tool names, not 'geo'
        if (toolId === 'rectangle' || toolId === 'ellipse') {
            // Directly set the tool to rectangle or ellipse (these exist in RootState)
            return this.rootState.setTool(toolId);
        }

        return this.rootState.setTool(toolId);
    }

    // Alias for API compatibility
    setTool(toolId) {
        return this.setCurrentTool(toolId);
    }

    getCurrentTool() {
        if (!this.rootState) {
            console.error('TLDrawEditor: rootState is undefined in getCurrentTool!');
            return 'select'; // Default fallback
        }

        if (!this.rootState.getCurrentTool) {
            console.error('TLDrawEditor: rootState.getCurrentTool is undefined!');
            return 'select'; // Default fallback
        }

        return this.rootState.getCurrentTool();
    }

    // Shape management (TLDraw-style API)
    createShapes(shapes) {
        const createdShapes = [];

        this.store.transaction(() => {
            shapes.forEach(shapeData => {
                const shape = new TLShape(shapeData.type, shapeData.id, shapeData);
                this.store.put(shape);

                // Add to current page
                const page = this.store.getCurrentPage();
                if (page) {
                    page.addShape(shape.id);
                    this.store.put(page);
                }

                // Call shape util's onShapeCreate if available
                const shapeUtil = this.shapeUtils.get(shape.type);
                if (shapeUtil && shapeUtil.onShapeCreate) {
                    shapeUtil.onShapeCreate(shape, this);
                }

                createdShapes.push(shape);
            });
        });

        return createdShapes;
    }

    // TLDraw API: createShape() - creates a single shape
    createShape(shapeData) {
        return this.createShapes([shapeData])[0];
    }

    updateShapes(updates) {
        this.store.transaction(() => {
            updates.forEach(update => {
                const shape = this.store.get(update.id);
                if (shape) {
                    Object.assign(shape, update);
                    this.store.put(shape);

                    // Call shape util's onShapeUpdate if available
                    const shapeUtil = this.shapeUtils.get(shape.type);
                    if (shapeUtil && shapeUtil.onShapeUpdate) {
                        shapeUtil.onShapeUpdate(shape, this);
                    }
                }
            });
        });
        return this;
    }

    deleteShapes(shapeIds) {
        this.store.transaction(() => {
            shapeIds.forEach(id => {
                const shape = this.store.get(id);

                // Call shape util's onShapeDelete if available
                if (shape) {
                    const shapeUtil = this.shapeUtils.get(shape.type);
                    if (shapeUtil && shapeUtil.onShapeDelete) {
                        shapeUtil.onShapeDelete(shape, this);
                    }
                }

                this.store.remove(id);

                // Remove from page
                const page = this.store.getCurrentPage();
                if (page) {
                    page.removeShape(id);
                    this.store.put(page);
                }
            });
        });
        return this;
    }

    getShape(id) {
        return this.store.get(id);
    }

    getShapes() {
        return this.store.getCurrentPageShapes();
    }

    // Alias for compatibility - many components expect this method
    getCurrentPageShapes() {
        return this.store.getCurrentPageShapes();
    }

    // Selection management
    select(...shapeIds) {
        const instance = this.store.getCurrentInstance();
        if (instance) {
            instance.setSelectedShapes(shapeIds);
            this.store.put(instance);
            // HTML containers will update selection visuals automatically
        }
        return this;
    }

    selectNone() {
        return this.select();
    }

    selectAll() {
        const shapes = this.getShapes();
        return this.select(...shapes.map(s => s.id));
    }

    getSelectedShapes() {
        return this.store.getSelectedShapes();
    }

    getSelectedShapeIds() {
        const instance = this.store.getCurrentInstance();
        return instance ? instance.selectedShapeIds : [];
    }

    // Camera management
    getCamera() {
        return this.store.getCurrentCamera();
    }

    // Hit testing with enhanced handle detection
    getShapeAtPoint(point, margin = 0) {
        // First check if we're over a resize handle
        const handleHit = this.getResizeHandleAtPoint(point);
        if (handleHit) {
            // Update cursor based on handle
            if (this.renderer && this.renderer.updateCursorFromHandle) {
                this.renderer.updateCursorFromHandle(handleHit);
            }
            return null; // Don't return shape when over handle
        } else {
            // Clear handle hover state
            if (this.renderer && this.renderer.clearHandleHover) {
                this.renderer.clearHandleHover();
            }
        }

        const shapes = this.getShapes();
        const camera = this.store.getCurrentCamera();

        // Convert screen point to page point
        const pagePoint = camera ? camera.screenToPage(point) : point;

        // Test shapes in reverse order (top to bottom)
        for (let i = shapes.length - 1; i >= 0; i--) {
            const shape = shapes[i];
            if (this.isPointInShape(pagePoint, shape, margin)) {
                return shape;
            }
        }

        return null;
    }

    // RESIZE CHUNK 1: Add resize handle hit detection
    getResizeHandleAtPoint(point) {
        if (!point || typeof point.x === 'undefined' || typeof point.y === 'undefined') {
            return null; // Invalid point
        }

        const selectedShapes = this.getSelectedShapes();
        if (selectedShapes.length !== 1) {
            return null; // Only show handles for single selection
        }

        const shape = selectedShapes[0];
        const bounds = shape.getBounds();
        const camera = this.store.getCurrentCamera();
        const handleSize = 8;
        const scale = camera ? 1 / camera.z : 1;
        const scaledHandleSize = handleSize * scale;
        const hitMargin = scaledHandleSize / 2;

        // Convert screen point to page point
        const pagePoint = camera ? camera.screenToPage(point) : point;

        console.log('ğŸ”§ RESIZE CHUNK 1: Testing handle hit at point:', {
            screenPoint: point,
            pagePoint: pagePoint,
            bounds: bounds,
            handleSize: scaledHandleSize,
            camera: camera ? { x: camera.x, y: camera.y, z: camera.z } : null
        });

        // Define handle positions (same as in renderer)
        const handles = [
            { x: bounds.x, y: bounds.y, type: 'top-left' },
            { x: bounds.x + bounds.w, y: bounds.y, type: 'top-right' },
            { x: bounds.x, y: bounds.y + bounds.h, type: 'bottom-left' },
            { x: bounds.x + bounds.w, y: bounds.y + bounds.h, type: 'bottom-right' },
            { x: bounds.x + bounds.w / 2, y: bounds.y, type: 'top' },
            { x: bounds.x + bounds.w / 2, y: bounds.y + bounds.h, type: 'bottom' },
            { x: bounds.x, y: bounds.y + bounds.h / 2, type: 'left' },
            { x: bounds.x + bounds.w, y: bounds.y + bounds.h / 2, type: 'right' }
        ];

        // Test each handle
        for (const handle of handles) {
            const distance = Math.hypot(
                pagePoint.x - handle.x,
                pagePoint.y - handle.y
            );

            if (distance <= hitMargin) {
                console.log('âœ… RESIZE CHUNK 1: Handle hit detected:', handle.type);
                return {
                    shape: shape,
                    handle: handle.type,
                    handlePos: { x: handle.x, y: handle.y },
                    distance: distance
                };
            }
        }

        console.log('âŒ RESIZE CHUNK 1: No handle hit');
        return null;
    }

    isPointInShape(point, shape, margin = 0) {
        const bounds = shape.getBounds();

        return point.x >= bounds.x - margin &&
               point.x <= bounds.x + bounds.w + margin &&
               point.y >= bounds.y - margin &&
               point.y <= bounds.y + bounds.h + margin;
    }

    getShapesInBounds(bounds) {
        const shapes = this.getShapes();
        return shapes.filter(shape => {
            const shapeBounds = shape.getBounds();
            return this.boundsIntersect(bounds, shapeBounds);
        });
    }

    boundsIntersect(a, b) {
        return !(a.x + a.w < b.x || 
                 b.x + b.w < a.x || 
                 a.y + a.h < b.y || 
                 b.y + b.h < a.y);
    }

    // Camera management
    getCamera() {
        return this.store.getCurrentCamera();
    }

    setCamera(camera) {
        this.store.put(camera);
        // Update renderer camera for HTML container positioning
        if (this.renderer) {
            this.renderer.updateCamera(camera);
        }
        return this;
    }

    panCamera(delta) {
        const camera = this.getCamera();
        if (camera) {
            camera.pan(delta);
            this.store.put(camera);
            // Update renderer camera for HTML container positioning
            if (this.renderer) {
                this.renderer.updateCamera(camera);
            }
        }
        return this;
    }

    zoomCamera(factor, center = null) {
        const camera = this.getCamera();
        if (camera) {
            camera.zoom(factor, center);
            this.store.put(camera);
            // Update renderer camera for HTML container positioning
            if (this.renderer) {
                this.renderer.updateCamera(camera);
            }
        }
        return this;
    }

    // History management
    undo() {
        return this.store.undo();
    }

    redo() {
        return this.store.redo();
    }

    mark(name) {
        this.store.mark(name);
        return this;
    }

    // Batch operations (like Editor.run())
    run(fn) {
        return this.store.transaction(fn);
    }

    // Export/Import
    getSnapshot() {
        return this.store.getSnapshot();
    }

    loadSnapshot(snapshot) {
        this.store.loadSnapshot(snapshot);
        return this;
    }

    // Event handling
    handleEvent(eventInfo) {
        return this.rootState.handleEvent(eventInfo);
    }

    // Context Menu Management - HIGHLY MODULAR!
    addContextMenuSection(id, config) {
        this.inputManager.registerContextMenuSection(id, config);
    }

    removeContextMenuSection(id) {
        this.inputManager.unregisterContextMenuSection(id);
    }

    // Cleanup
    destroy() {
        this.stopRenderLoop();
        this.inputManager.destroy();

        if (this.canvas && this.canvas.parentNode) {
            this.canvas.parentNode.removeChild(this.canvas);
        }
    }
}

// Input Manager - handles DOM events and converts to TLEventInfo
class InputManager {
    constructor(editor) {
        this.editor = editor;
        this.isEnabled = false;

        // Touch tracking
        this.activePointers = new Map();
        this.touchStartTime = 0;
        this.threeFingerTimeout = null;

        // Bound methods for cleanup
        this.boundHandlers = {};

        // MODULAR Context Menu System
        this.contextMenuSections = new Map();
        this.initializeContextMenuSections();
    }

    initialize() {
        if (this.isEnabled) return;

        const canvas = this.editor.canvas;

        // Pointer events
        this.boundHandlers.pointerdown = this.handlePointerDown.bind(this);
        this.boundHandlers.pointermove = this.handlePointerMove.bind(this);
        this.boundHandlers.pointerup = this.handlePointerUp.bind(this);
        this.boundHandlers.pointercancel = this.handlePointerUp.bind(this);

        canvas.addEventListener('pointerdown', this.boundHandlers.pointerdown);
        canvas.addEventListener('pointermove', this.boundHandlers.pointermove);
        canvas.addEventListener('pointerup', this.boundHandlers.pointerup);
        canvas.addEventListener('pointercancel', this.boundHandlers.pointercancel);

        // Mouse events
        this.boundHandlers.dblclick = this.handleDoubleClick.bind(this);
        this.boundHandlers.contextmenu = this.handleContextMenu.bind(this);
        this.boundHandlers.wheel = this.handleWheel.bind(this);

        canvas.addEventListener('dblclick', this.boundHandlers.dblclick);
        canvas.addEventListener('contextmenu', this.boundHandlers.contextmenu);
        canvas.addEventListener('wheel', this.boundHandlers.wheel);

        // Keyboard events (on document)
        this.boundHandlers.keydown = this.handleKeyDown.bind(this);
        this.boundHandlers.keyup = this.handleKeyUp.bind(this);

        document.addEventListener('keydown', this.boundHandlers.keydown);
        document.addEventListener('keyup', this.boundHandlers.keyup);

        this.isEnabled = true;
    }

    destroy() {
        if (!this.isEnabled) return;

        const canvas = this.editor.canvas;

        // Remove canvas events
        canvas.removeEventListener('pointerdown', this.boundHandlers.pointerdown);
        canvas.removeEventListener('pointermove', this.boundHandlers.pointermove);
        canvas.removeEventListener('pointerup', this.boundHandlers.pointerup);
        canvas.removeEventListener('pointercancel', this.boundHandlers.pointercancel);
        canvas.removeEventListener('dblclick', this.boundHandlers.dblclick);
        canvas.removeEventListener('contextmenu', this.boundHandlers.contextmenu);
        canvas.removeEventListener('wheel', this.boundHandlers.wheel);

        // Remove document events
        document.removeEventListener('keydown', this.boundHandlers.keydown);
        document.removeEventListener('keyup', this.boundHandlers.keyup);

        this.isEnabled = false;
    }

    createEventInfo(type, domEvent) {
        const rect = this.editor.canvas.getBoundingClientRect();
        let currentPagePoint = { x: 0, y: 0 };

        if (domEvent && domEvent.clientX !== undefined) {
            const screenPoint = {
                x: domEvent.clientX - rect.left,
                y: domEvent.clientY - rect.top
            };

            const camera = this.editor.getCamera();
            currentPagePoint = camera ? camera.screenToPage(screenPoint) : screenPoint;
        }

        return new TLEventInfo(type, {
            currentPagePoint,
            originPagePoint: currentPagePoint,
            screenPoint: domEvent && domEvent.clientX !== undefined ? {
                x: domEvent.clientX - rect.left,
                y: domEvent.clientY - rect.top
            } : { x: 0, y: 0 },
            shiftKey: domEvent?.shiftKey || false,
            ctrlKey: domEvent?.ctrlKey || false,
            altKey: domEvent?.altKey || false,
            metaKey: domEvent?.metaKey || false,
            pointerId: domEvent?.pointerId || 0,
            button: domEvent?.button || 0,
            isPen: domEvent?.pointerType === 'pen',
            target: domEvent?.target,
            key: domEvent?.key || '',
            code: domEvent?.code || '',
            delta: domEvent?.deltaX !== undefined ? 
                { x: domEvent.deltaX, y: domEvent.deltaY } : 
                { x: 0, y: 0 }
        });
    }

    handlePointerDown(event) {
        console.log('ğŸ”µ CANVAS CLICKED! Position:', event.clientX, event.clientY);
        event.preventDefault();

        // Track active pointers for multi-touch
        this.activePointers.set(event.pointerId, {
            x: event.clientX,
            y: event.clientY,
            type: event.pointerType,
            timestamp: Date.now()
        });

        console.log('ğŸ”µ Pointer tracked:', event.pointerId, 'Active pointers:', this.activePointers.size);

        // Check for three-finger touch
        if (this.activePointers.size === 3) {
            this.handleThreeFingerTouch(event);
            // CRITICAL FIX: Don't return early - still need to send event to tools
            // Three-finger gestures should STILL trigger tool behavior
        }

        // Handle pointer events (single or three-finger)
        if (this.activePointers.size === 1 || this.activePointers.size === 3) {
            console.log('ğŸ”µ CREATING EVENT INFO (size:', this.activePointers.size, ')');
            const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
            console.log('ğŸ”µ SENDING TO EDITOR');
            this.editor.handleEvent(eventInfo);
            console.log('ğŸ”µ EVENT SENT');
        }
    }

    handlePointerMove(event) {
        // Update pointer position
        if (this.activePointers.has(event.pointerId)) {
            this.activePointers.get(event.pointerId).x = event.clientX;
            this.activePointers.get(event.pointerId).y = event.clientY;
        }

        // Handle move for single pointer OR three-finger drag
        if (this.activePointers.size === 1 || this.activePointers.size === 3) {
            const eventInfo = this.createEventInfo(TLEventType.POINTER_MOVE, event);
            this.editor.handleEvent(eventInfo);
        }
    }

    handlePointerUp(event) {
        // Remove pointer from tracking
        this.activePointers.delete(event.pointerId);

        console.log('Pointer up:', event.pointerId, 'Remaining pointers:', this.activePointers.size);

        // Handle up when returning to single pointer or no pointers
        // This handles both single-click and three-finger drag completion
        if (this.activePointers.size === 0 || this.activePointers.size === 2) {
            const eventInfo = this.createEventInfo(TLEventType.POINTER_UP, event);
            this.editor.handleEvent(eventInfo);
        }
    }

    handleDoubleClick(event) {
        event.preventDefault();
        const eventInfo = this.createEventInfo(TLEventType.DOUBLE_CLICK, event);
        this.editor.handleEvent(eventInfo);
    }

    handleContextMenu(event) {
        event.preventDefault();
        const eventInfo = this.createEventInfo(TLEventType.CONTEXT_MENU, event);
        this.editor.handleEvent(eventInfo);

        // Check if right-click is on a selected shape - show asset dropdown instead
        const pointerPos = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
        const selectedShapes = this.editor.getSelectedShapes();

        if (shapeAtPoint && selectedShapes.some(s => s.id === shapeAtPoint.id)) {
            // Show TLDraw-style asset dropdown for selected shape
            this.showAssetDropdown(shapeAtPoint, event);
        } else {
            // Show general context menu
            this.showContextMenu(event);
        }
    }

    handleWheel(event) {
        event.preventDefault();

        // Handle zoom
        const factor = event.deltaY > 0 ? 0.9 : 1.1;
        const center = {
            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
        };

        // Use smooth zoom with HTML container synchronization
        this.editor.zoomCamera(factor, center);

        // Force render to update HTML container positions immediately
        this.editor.render();

        // Also send wheel event to state chart
        const eventInfo = this.createEventInfo(TLEventType.WHEEL, event);
        this.editor.handleEvent(eventInfo);
    }

    handleKeyDown(event) {
        const eventInfo = this.createEventInfo(TLEventType.KEY_DOWN, event);
        const result = this.editor.handleEvent(eventInfo);

        // Handle global shortcuts
        if (result.type === 'ignored') {
            this.handleGlobalShortcuts(event);
        }
    }

    handleKeyUp(event) {
        const eventInfo = this.createEventInfo(TLEventType.KEY_UP, event);
        this.editor.handleEvent(eventInfo);
    }

