/**
 * StateChart-driven Tool System - Based on tldraw's state management
 * Hierarchical state handling for complex tool interactions
 */

// Event types for state transitions are defined in types-definitions.js

// Event info wrapper - TLEventInfo class is defined in types-definitions.js

// State transition results
const TLTransitionResult = {
    handled: (data = {}) => ({ type: 'handled', data }),
    transition: (statePath, data = {}) => ({ type: 'transition', statePath, data }),
    ignored: () => ({ type: 'ignored' })
};

// Base StateNode class - hierarchical state management
class StateNode extends EventTarget {
    constructor(id, parent = null) {
        super();
        this.id = id;
        this.parent = parent;
        this.children = new Map();
        this.activeChildId = null;
        this.isActive = false;
        this.editor = null;

        // State data
        this.info = {};
    }

    // Add child states
    addChild(childNode) {
        childNode.parent = this;
        childNode.editor = this.editor;
        this.children.set(childNode.id, childNode);

        // Recursively set editor on all children
        this.setEditorRecursive(childNode);

        return childNode;
    }

    setEditorRecursive(node) {
        node.editor = this.editor;
        for (const child of node.children.values()) {
            this.setEditorRecursive(child);
        }
    }

    // Enter this state
    enter(info = {}) {
        console.log(`ğŸ¯ StateNode.enter called on ${this.constructor.name}(${this.id})`);
        this.isActive = true;
        this.info = { ...this.info, ...info };
        this.onEnter(info);

        // Enter initial child if exists
        const initialChild = this.getInitialChild();
        console.log(`ğŸ¯ Initial child for ${this.id}:`, initialChild?.id);
        if (initialChild) {
            console.log(`ğŸ¯ Transitioning to initial child: ${initialChild.id}`);
            this.transitionTo(initialChild.id, info);
        }
    }

    // Exit this state
    exit(info = {}) {
        // Exit active child first
        if (this.activeChildId) {
            const activeChild = this.children.get(this.activeChildId);
            if (activeChild) {
                activeChild.exit(info);
            }
            this.activeChildId = null;
        }

        this.isActive = false;
        this.onExit(info);
    }

    // Transition to child state
    transitionTo(childId, info = {}) {
        const child = this.children.get(childId);
        if (!child) {
            console.warn(`Child state '${childId}' not found in '${this.id}'`);
            return false;
        }

        // Exit current child
        if (this.activeChildId && this.activeChildId !== childId) {
            const currentChild = this.children.get(this.activeChildId);
            if (currentChild) {
                currentChild.exit(info);
            }
        }

        // Enter new child
        this.activeChildId = childId;
        child.enter(info);
        return true;
    }

    // Handle events with proper bubbling
    handleEvent(eventInfo) {
        console.log(`ğŸ¯ StateNode.handleEvent on ${this.constructor.name}(${this.id}), activeChild: ${this.activeChildId}`);

        // Try active child first
        if (this.activeChildId) {
            const activeChild = this.children.get(this.activeChildId);
            console.log(`ğŸ¯ Found active child: ${activeChild?.id}, isActive: ${activeChild?.isActive}`);

            if (activeChild && activeChild.isActive) {
                console.log(`ğŸ¯ Delegating to child ${activeChild.id}`);
                const result = activeChild.handleEvent(eventInfo);
                console.log(`ğŸ¯ Child result:`, result);

                if (result.type === 'transition') {
                    this.handleTransition(result);
                    return TLTransitionResult.handled();
                } else if (result.type === 'handled') {
                    return result;
                }
                // If ignored, continue to this node
            }
        }

        // Handle at this level
        console.log(`ğŸ¯ Handling at ${this.id} level`);
        const result = this.onEvent(eventInfo);
        console.log(`ğŸ¯ onEvent result:`, result);

        if (result.type === 'transition') {
            this.handleTransition(result);
            return TLTransitionResult.handled();
        }

        return result;
    }

    // Handle state transitions
    handleTransition(result) {
        const { statePath, data } = result;
        const parts = statePath.split('.');

        if (parts[0] === 'parent' && this.parent) {
            // Transition to parent state
            const remainingPath = parts.slice(1).join('.');
            if (remainingPath) {
                this.parent.handleTransition({ 
                    type: 'transition', 
                    statePath: remainingPath, 
                    data 
                });
            }
        } else {
            // Transition to child state
            this.transitionTo(statePath, data);
        }
    }

    // Get initial child (override in subclasses)
    getInitialChild() {
        return this.children.values().next().value;
    }

    // Get current state path
    getStatePath() {
        const path = [this.id];
        if (this.activeChildId) {
            const activeChild = this.children.get(this.activeChildId);
            if (activeChild) {
                path.push(...activeChild.getStatePath());
            }
        }
        return path;
    }

    // Override these in subclasses
    onEnter(info) {}
    onExit(info) {}
    onEvent(eventInfo) { return TLTransitionResult.ignored(); }
}

