// Select Tool State
class SelectTool extends StateNode {
    constructor(editor) {
        super('select');
        this.editor = editor;

        // Sub-states
        this.idle = this.addChild(new SelectIdle());
        this.pointing = this.addChild(new SelectPointing());
        this.dragging = this.addChild(new SelectDragging());
        this.resizing = this.addChild(new SelectResizing());
    }

    getInitialChild() {
        return this.idle;
    }
}

class SelectIdle extends StateNode {
    constructor() {
        super('idle');
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_DOWN:
                // RESIZE CHUNK 2: Check for resize handle before starting pointing (with multi-select support)
                const handleHit = this.editor.getResizeHandleAtPoint(eventInfo.screenPoint);
                if (handleHit) {
                    console.log('ğŸ”§ RESIZE CHUNK 2: Transitioning to resize mode:', handleHit.handle);

                    // ENHANCEMENT: Pass all selected shapes for multi-select resize
                    const selectedShapes = this.editor.getSelectedShapes();

                    return TLTransitionResult.transition('parent.parent.select.resizing', {
                        origin: eventInfo.currentPagePoint,
                        handle: handleHit.handle,
                        shape: handleHit.shape,
                        selectedShapes: selectedShapes,
                        handlePos: handleHit.handlePos
                    });
                }

                return TLTransitionResult.transition('parent.pointing', { 
                    origin: eventInfo.currentPagePoint 
                });
            case TLEventType.KEY_DOWN:
                if (eventInfo.key === 'd') {
                    return TLTransitionResult.transition('parent.parent.draw');
                }
                break;
        }
        return TLTransitionResult.ignored();
    }
}

class SelectPointing extends StateNode {
    constructor() {
        super('pointing');
    }

    onEnter(info) {
        this.startPoint = info.origin;
        this.editor.store.mark('select-start');
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                const distance = Math.hypot(
                    eventInfo.currentPagePoint.x - this.startPoint.x,
                    eventInfo.currentPagePoint.y - this.startPoint.y
                );

                if (distance > 4) {
                    // Select the shape at start point before dragging if not already selected
                    const hitShape = this.editor.getShapeAtPoint(this.startPoint);
                    if (hitShape) {
                        const selectedShapes = this.editor.getSelectedShapeIds();
                        if (!selectedShapes.includes(hitShape.id)) {
                            console.log('SelectPointing: Auto-selecting shape for drag:', hitShape.id);
                            this.editor.select(hitShape.id);
                        }
                    }

                    return TLTransitionResult.transition('parent.dragging', {
                        origin: this.startPoint,
                        current: eventInfo.currentPagePoint
                    });
                }
                break;

            case TLEventType.POINTER_UP:
                // Handle selection
                this.handleSelection(eventInfo.currentPagePoint);
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    handleSelection(point) {
        console.log('SelectPointing: handleSelection called at point:', point);

        // Find shape at point
        const hitShape = this.editor.getShapeAtPoint(point);
        console.log('SelectPointing: Hit shape:', hitShape);

        if (hitShape) {
            console.log('SelectPointing: Selecting shape:', hitShape.id);
            this.editor.select(hitShape.id);
        } else {
            console.log('SelectPointing: No shape hit, clearing selection');
            this.editor.selectNone();
        }
    }
}

class SelectDragging extends StateNode {
    constructor() {
        super('dragging');
    }

    onEnter(info) {
        console.log('SelectDragging: onEnter with info:', info);
        this.startPoint = info.origin;
        this.currentPoint = info.current;
        this.lastPoint = info.current;

        // Get initial selected shapes for dragging
        this.draggedShapes = this.editor.getSelectedShapes();
        console.log('SelectDragging: Dragging shapes:', this.draggedShapes.map(s => s.id));
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                this.currentPoint = eventInfo.currentPagePoint;
                this.updateDrag();
                break;

            case TLEventType.POINTER_UP:
                this.completeDrag();
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    updateDrag() {
        if (!this.lastPoint || !this.draggedShapes) return;

        // Calculate delta from last position (not start position!)
        const delta = {
            x: this.currentPoint.x - this.lastPoint.x,
            y: this.currentPoint.y - this.lastPoint.y
        };

        console.log('SelectDragging: updateDrag delta:', delta);

        // Apply delta to each dragged shape
        this.draggedShapes.forEach(shape => {
            const newX = shape.x + delta.x;
            const newY = shape.y + delta.y;

            shape.x = newX;
            shape.y = newY;

            this.editor.store.put(shape);
        });

        // Update last point for next delta calculation
        this.lastPoint = this.currentPoint;
    }

    completeDrag() {
        this.editor.store.mark('drag-complete');
    }
}

class SelectResizing extends StateNode {
    constructor() {
        super('resizing');
    }

    // RESIZE CHUNK 3: Complete resize implementation with multi-select support
    onEnter(info) {
        console.log('ğŸ”§ RESIZE CHUNK 3: Entering resize mode:', info);

        this.handle = info.handle;
        this.startPoint = info.origin;

        // ENHANCEMENT: Support multi-select resize
        if (info.selectedShapes && info.selectedShapes.length > 1) {
            this.shapes = info.selectedShapes;
            this.isMultiSelect = true;
            this.startBounds = this.calculateMultiSelectBounds(this.shapes);
            this.individualStartBounds = this.shapes.map(shape => ({
                shape: shape,
                bounds: shape.getBounds()
            }));

            console.log('ğŸ”§ MULTI-SELECT RESIZE:', {
                handle: this.handle,
                shapeCount: this.shapes.length,
                combinedBounds: this.startBounds
            });
        } else {
            // Single shape resize
            this.shape = info.shape;
            this.shapes = [this.shape];
            this.isMultiSelect = false;
            this.startBounds = this.shape.getBounds();

            console.log('ğŸ”§ SINGLE RESIZE:', {
                handle: this.handle,
                shapeId: this.shape.id,
                startBounds: this.startBounds
            });
        }

        // Call onResizeStart for all shapes
        this.shapes.forEach(shape => {
            const shapeUtil = this.editor.shapeUtils.get(shape.type);
            if (shapeUtil && shapeUtil.onResizeStart) {
                shapeUtil.onResizeStart(shape, this.editor);
            }
        });

        this.editor.store.mark('resize-start');
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                this.updateResize(eventInfo.currentPagePoint);
                break;

            case TLEventType.POINTER_UP:
                this.completeResize();
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    updateResize(currentPoint) {
        if (!this.shapes || !this.startBounds) return;

        if (this.isMultiSelect) {
            this.updateMultiSelectResize(currentPoint);
        } else {
            this.updateSingleResize(currentPoint);
        }
    }

    // ENHANCEMENT: Update single shape resize
    updateSingleResize(currentPoint) {
        const newBounds = this.calculateNewBounds(currentPoint);
        const shape = this.shapes[0];

        console.log('ğŸ”§ SINGLE RESIZE UPDATE:', {
            handle: this.handle,
            currentPoint: currentPoint,
            newBounds: newBounds
        });

        // Call BaseBoxShapeUtil onResize
        const shapeUtil = this.editor.shapeUtils.get(shape.type);
        if (shapeUtil && shapeUtil.onResize) {
            shapeUtil.onResize(shape, this.editor, newBounds);
        } else {
            // Fallback: direct shape update
            shape.x = newBounds.x;
            shape.y = newBounds.y;
            if (shape.props) {
                shape.props.w = newBounds.w;
                shape.props.h = newBounds.h;
            } else {
                shape.w = newBounds.w;
                shape.h = newBounds.h;
            }
            this.editor.store.put(shape);
        }
    }

    // ENHANCEMENT: Update multi-select resize
    updateMultiSelectResize(currentPoint) {
        const newCombinedBounds = this.calculateNewBounds(currentPoint);

        console.log('ğŸ”§ MULTI-SELECT RESIZE UPDATE:', {
            handle: this.handle,
            shapeCount: this.shapes.length,
            newCombinedBounds: newCombinedBounds
        });

        // Calculate scale factors
        const scaleX = newCombinedBounds.w / this.startBounds.w;
        const scaleY = newCombinedBounds.h / this.startBounds.h;

        // Apply proportional resize to each shape
        this.individualStartBounds.forEach(({ shape, bounds }) => {
            // Calculate relative position within the combined bounds
            const relativeX = (bounds.x - this.startBounds.x) / this.startBounds.w;
            const relativeY = (bounds.y - this.startBounds.y) / this.startBounds.h;
            const relativeW = bounds.w / this.startBounds.w;
            const relativeH = bounds.h / this.startBounds.h;

            // Calculate new bounds for this shape
            const newShapeBounds = {
                x: newCombinedBounds.x + (relativeX * newCombinedBounds.w),
                y: newCombinedBounds.y + (relativeY * newCombinedBounds.h),
                w: relativeW * newCombinedBounds.w,
                h: relativeH * newCombinedBounds.h
            };

            // Enforce minimum size per shape
            newShapeBounds.w = Math.max(20, newShapeBounds.w);
            newShapeBounds.h = Math.max(20, newShapeBounds.h);

            // Apply bounds to shape
            const shapeUtil = this.editor.shapeUtils.get(shape.type);
            if (shapeUtil && shapeUtil.onResize) {
                shapeUtil.onResize(shape, this.editor, newShapeBounds);
            } else {
                // Fallback: direct shape update
                shape.x = newShapeBounds.x;
                shape.y = newShapeBounds.y;
                if (shape.props) {
                    shape.props.w = newShapeBounds.w;
                    shape.props.h = newShapeBounds.h;
                } else {
                    shape.w = newShapeBounds.w;
                    shape.h = newShapeBounds.h;
                }
                this.editor.store.put(shape);
            }

            // Update HTML container if it's an embed
            if ((shape.type === 'embed' || shape.type === 'youtube') && this.editor.htmlContainer) {
                this.editor.htmlContainer.updateShapeContainer(shape.id);
            }
        });
    }

    calculateNewBounds(currentPoint) {
        const startBounds = this.startBounds;
        const deltaX = currentPoint.x - this.startPoint.x;
        const deltaY = currentPoint.y - this.startPoint.y;

        let newBounds = { ...startBounds };

        // ENHANCEMENT: Get shape constraints
        const shapeUtil = this.editor.shapeUtils.get(this.shape.type);
        const constraints = this.getResizeConstraints(shapeUtil);

        // Calculate new bounds based on handle type
        switch (this.handle) {
            case 'top-left':
                newBounds.x = startBounds.x + deltaX;
                newBounds.y = startBounds.y + deltaY;
                newBounds.w = startBounds.w - deltaX;
                newBounds.h = startBounds.h - deltaY;
                break;
            case 'top-right':
                newBounds.y = startBounds.y + deltaY;
                newBounds.w = startBounds.w + deltaX;
                newBounds.h = startBounds.h - deltaY;
                break;
            case 'bottom-left':
                newBounds.x = startBounds.x + deltaX;
                newBounds.w = startBounds.w - deltaX;
                newBounds.h = startBounds.h + deltaY;
                break;
            case 'bottom-right':
                newBounds.w = startBounds.w + deltaX;
                newBounds.h = startBounds.h + deltaY;
                break;
            case 'top':
                newBounds.y = startBounds.y + deltaY;
                newBounds.h = startBounds.h - deltaY;
                break;
            case 'bottom':
                newBounds.h = startBounds.h + deltaY;
                break;
            case 'left':
                newBounds.x = startBounds.x + deltaX;
                newBounds.w = startBounds.w - deltaX;
                break;
            case 'right':
                newBounds.w = startBounds.w + deltaX;
                break;
        }

        // ENHANCEMENT: Apply constraints
        newBounds = this.applyResizeConstraints(newBounds, constraints, startBounds);

        return newBounds;
    }

    // ENHANCEMENT: Get resize constraints for shape type
    getResizeConstraints(shapeUtil) {
        const constraints = {
            minWidth: 20,
            minHeight: 20,
            maxWidth: 5000,
            maxHeight: 5000,
            aspectRatio: null,
            snapToGrid: false,
            gridSize: 10
        };

        // Shape-specific constraints
        if (this.shape.type === 'embed' || this.shape.type === 'youtube') {
            // Video embeds should maintain aspect ratio by default
            constraints.aspectRatio = this.startBounds.w / this.startBounds.h;
            constraints.minWidth = 200;
            constraints.minHeight = 150;
        } else if (this.shape.type === 'ellipse') {
            // Circle constraint for ellipses when shift is held
            if (this.editor.inputManager?.shiftKey) {
                constraints.aspectRatio = 1; // Perfect circle
            }
        } else if (this.shape.type === 'rectangle') {
            // Square constraint for rectangles when shift is held
            if (this.editor.inputManager?.shiftKey) {
                constraints.aspectRatio = 1; // Perfect square
            }
        }

        // Check if shape util has custom constraints
        if (shapeUtil && shapeUtil.getResizeConstraints) {
            const customConstraints = shapeUtil.getResizeConstraints(this.shape);
            Object.assign(constraints, customConstraints);
        }

        return constraints;
    }

    // ENHANCEMENT: Apply resize constraints
    applyResizeConstraints(bounds, constraints, originalBounds) {
        let { x, y, w, h } = bounds;

        // Enforce minimum/maximum size
        w = Math.max(w, constraints.minWidth);
        h = Math.max(h, constraints.minHeight);
        w = Math.min(w, constraints.maxWidth);
        h = Math.min(h, constraints.maxHeight);

        // Apply aspect ratio constraint
        if (constraints.aspectRatio) {
            const targetRatio = constraints.aspectRatio;
            const currentRatio = w / h;

            if (Math.abs(currentRatio - targetRatio) > 0.01) {
                // Determine which dimension to adjust based on handle type
                const isCornerHandle = ['top-left', 'top-right', 'bottom-left', 'bottom-right'].includes(this.handle);

                if (isCornerHandle) {
                    // For corner handles, maintain aspect ratio by adjusting both dimensions
                    if (currentRatio > targetRatio) {
                        // Too wide, adjust height
                        h = w / targetRatio;
                    } else {
                        // Too tall, adjust width
                        w = h * targetRatio;
                    }
                } else if (['left', 'right'].includes(this.handle)) {
                    // For horizontal handles, adjust height to maintain ratio
                    h = w / targetRatio;
                } else if (['top', 'bottom'].includes(this.handle)) {
                    // For vertical handles, adjust width to maintain ratio
                    w = h * targetRatio;
                }
            }

            // Adjust position for top/left handles when dimensions change
            if (this.handle.includes('top')) {
                y = originalBounds.y + originalBounds.h - h;
            }
            if (this.handle.includes('left')) {
                x = originalBounds.x + originalBounds.w - w;
            }
        }

        // Apply grid snapping
        if (constraints.snapToGrid) {
            const gridSize = constraints.gridSize;
            w = Math.round(w / gridSize) * gridSize;
            h = Math.round(h / gridSize) * gridSize;
            x = Math.round(x / gridSize) * gridSize;
            y = Math.round(y / gridSize) * gridSize;
        }

        return { x, y, w, h };
    }

    completeResize() {
        console.log('âœ… RESIZE COMPLETE:', {
            isMultiSelect: this.isMultiSelect,
            shapeCount: this.shapes.length
        });

        // Call onResizeEnd for all shapes
        this.shapes.forEach(shape => {
            const shapeUtil = this.editor.shapeUtils.get(shape.type);
            if (shapeUtil && shapeUtil.onResizeEnd) {
                shapeUtil.onResizeEnd(shape, this.editor);
            }
        });

        this.editor.store.mark('resize-complete');
    }

    // ENHANCEMENT: Calculate multi-select bounds
    calculateMultiSelectBounds(shapes) {
        if (shapes.length === 0) return null;
        if (shapes.length === 1) return shapes[0].getBounds();

        let minX = Infinity;
        let minY = Infinity;
        let maxX = -Infinity;
        let maxY = -Infinity;

        shapes.forEach(shape => {
            const bounds = shape.getBounds();
            if (bounds) {
                minX = Math.min(minX, bounds.x);
                minY = Math.min(minY, bounds.y);
                maxX = Math.max(maxX, bounds.x + bounds.w);
                maxY = Math.max(maxY, bounds.y + bounds.h);
            }
        });

        if (minX === Infinity) return null;

        return {
            x: minX,
            y: minY,
            w: maxX - minX,
            h: maxY - minY
        };
    }
}

// Draw Tool State
class DrawTool extends StateNode {
    constructor(editor) {
        super('draw');
        this.editor = editor;

        this.idle = this.addChild(new DrawIdle());
        this.drawing = this.addChild(new DrawDrawing());
    }

    getInitialChild() {
        return this.idle;
    }
}

class DrawIdle extends StateNode {
    constructor() {
        super('idle');
    }

    onEvent(eventInfo) {
        if (eventInfo.type === TLEventType.POINTER_DOWN) {
            return TLTransitionResult.transition('parent.drawing', {
                origin: eventInfo.currentPagePoint
            });
        }
        return TLTransitionResult.ignored();
    }
}

class DrawDrawing extends StateNode {
    constructor() {
        super('drawing');
    }

    onEnter(info) {
        console.log('DrawDrawing: onEnter called with info:', info);
        console.log('DrawDrawing: this.editor:', this.editor);

        this.startPoint = info.origin;
        this.currentShape = this.createDrawShape(this.startPoint);
        this.editor.store.mark('draw-start');

        console.log('DrawDrawing: Created shape and marked store');
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                this.updateDrawing(eventInfo.currentPagePoint);
                break;

            case TLEventType.POINTER_UP:
                this.completeDrawing();
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    createDrawShape(point) {
        const shape = new TLShape(TLShapeType.DRAW, null, {
            x: point.x,
            y: point.y,
            props: {
                segments: [{
                    points: [{ x: 0, y: 0, z: 0.5 }]
                }],
                color: '#000000',
                size: 'medium',
                isComplete: false
            }
        });

        // Add to store and page
        this.editor.store.put(shape);

        // Add to current page
        const page = this.editor.store.getCurrentPage();
        if (page) {
            page.addShape(shape.id);
            this.editor.store.put(page);
        }

        console.log('Created draw shape:', shape.id, 'at point:', point);
        return shape;
    }

    updateDrawing(point) {
        if (!this.currentShape) return;

        const newPoint = {
            x: point.x - this.currentShape.x,
            y: point.y - this.currentShape.y,
            z: 0.5
        };

        // Add point to the drawing
        this.currentShape.props.segments[0].points.push(newPoint);

        // Update in store
        this.editor.store.put(this.currentShape);

        console.log('Added point to draw shape:', newPoint, 'total points:', this.currentShape.props.segments[0].points.length);
    }

    completeDrawing() {
        if (this.currentShape) {
            this.currentShape.props.isComplete = true;
            this.editor.store.put(this.currentShape);
            this.editor.store.mark('draw-complete');
        }
    }
}

