class EraserIdle extends StateNode {
    constructor() {
        super('idle');
    }

    onEvent(eventInfo) {
        if (eventInfo.type === TLEventType.POINTER_DOWN) {
            // DIAGNOSTIC: Log button information for trackpad three-finger drag debugging
            console.log('EraserIdle: POINTER_DOWN detected', {
                button: eventInfo.button,
                buttons: eventInfo.buttons,
                ctrlKey: eventInfo.ctrlKey,
                altKey: eventInfo.altKey,
                metaKey: eventInfo.metaKey,
                pointerId: eventInfo.pointerId
            });

            // Accept any pointer down for eraser activation
            // Trackpad three-finger drag should send POINTER_DOWN events
            return TLTransitionResult.transition('parent.erasing', {
                origin: eventInfo.currentPagePoint
            });
        }
        return TLTransitionResult.ignored();
    }
}

class EraserErasing extends StateNode {
    constructor() {
        super('erasing');
    }

    onEnter(info) {
        this.eraseAtPoint(info.origin);
        this.editor.store.mark('erase-start');
    }

    onEvent(eventInfo) {
        switch (eventInfo.type) {
            case TLEventType.POINTER_MOVE:
                this.eraseAtPoint(eventInfo.currentPagePoint);
                break;

            case TLEventType.POINTER_UP:
                this.editor.store.mark('erase-complete');
                return TLTransitionResult.transition('parent.idle');
        }
        return TLTransitionResult.ignored();
    }

    eraseAtPoint(point) {
        console.log('EraserErasing: Checking for shape at point:', point);

        const hitShape = this.editor.getShapeAtPoint(point);
        if (hitShape) {
            console.log('EraserErasing: Found shape to erase:', hitShape.id);

            // Remove from store
            this.editor.store.remove(hitShape.id);

            // Remove from page 
            const page = this.editor.store.getCurrentPage();
            if (page) {
                page.removeShape(hitShape.id);
                this.editor.store.put(page);
                console.log('EraserErasing: Removed shape from page:', hitShape.id);
            }

            console.log('EraserErasing: Shape erased successfully:', hitShape.id);
        } else {
            console.log('EraserErasing: No shape found at point');
        }
    }
}

