    window.RootState = RootState;
    window.SelectTool = SelectTool;
    window.DrawTool = DrawTool;
    window.GeoTool = GeoTool;
    window.TextTool = TextTool;
    window.HandTool = HandTool;
    window.EraserTool = EraserTool;
    window.ArrowTool = ArrowTool;
    window.LineTool = LineTool;
}

