            except (EOFError, KeyboardInterrupt):
                print("\nâ¹ï¸ Interrupted by user")
                return False

    def interactive_debug(self):
        """Interactive debugging mode"""
        print("\nğŸ› INTERACTIVE DEBUG MODE")
        print("Available commands:")
        print("  - state: Show current system state")
        print("  - buttons: List all toolbar buttons") 
        print("  - click <tool>: Click a specific tool button")
        print("  - screenshot: Take a screenshot")
        print("  - console: Show console messages")
        print("  - exit: Return to main menu")

        while True:
            try:
                cmd = input("debug> ").strip().lower()

                if cmd == 'exit':
                    break
                elif cmd == 'state':
                    state = self.capture_comprehensive_state()
                    print(json.dumps(state, indent=2))
                elif cmd == 'buttons':
                    buttons = self.driver.execute_script("""
                        const buttons = document.querySelectorAll('[data-tool]');
                        return Array.from(buttons).map(btn => ({
                            tool: btn.dataset.tool,
                            visible: btn.offsetParent !== null,
                            enabled: !btn.disabled,
                            text: btn.textContent.trim(),
                            rect: btn.getBoundingClientRect()
                        }));
                    """)
                    for btn in buttons:
                        print(f"  {btn['tool']}: visible={btn['visible']}, enabled={btn['enabled']}, text='{btn['text']}'")
                elif cmd.startswith('click '):
                    tool = cmd.split(' ', 1)[1]
                    self.test_tool_selection_workflow(tool)
                elif cmd == 'screenshot':
                    filename = f"debug_screenshot_{int(time.time())}.png"
                    self.driver.save_screenshot(filename)
                    print(f"Screenshot saved: {filename}")
                elif cmd == 'console':
                    logs = self.driver.get_log('browser')
                    for log in logs[-10:]:  # Last 10 entries
                        print(f"  {log['level']}: {log['message']}")
                else:
                    print("Unknown command. Type 'exit' to return.")
            except (EOFError, KeyboardInterrupt):
                break

    def run_comprehensive_test_cycle(self):
        """Run complete test cycle with ability to edit and re-test"""
        tools_to_test = ['select', 'rectangle', 'ellipse', 'eraser']

        while self.session_active:
            print("\n" + "="*60)
            print("ğŸš€ STARTING COMPREHENSIVE TEST CYCLE")
            print("="*60)

            cycle_results = {}

            # Test each tool
            for tool in tools_to_test:
                print(f"\nğŸ“‹ Testing {tool} tool...")

                # Test tool selection
                selection_result = self.test_tool_selection_workflow(tool)
                cycle_results[f'{tool}_selection'] = selection_result

                # Test shape creation (for creation tools)
                if tool in ['rectangle', 'ellipse']:
                    creation_result = self.test_shape_creation_workflow(tool)
                    cycle_results[f'{tool}_creation'] = creation_result

            # Summary
            print("\n" + "="*60)
            print("ğŸ“Š TEST CYCLE SUMMARY")
            print("="*60)

            passed_tests = [k for k, v in cycle_results.items() if v]
            failed_tests = [k for k, v in cycle_results.items() if not v]

            print(f"âœ… PASSED: {len(passed_tests)} tests")
            for test in passed_tests:
                print(f"   âœ… {test}")

            print(f"\nâŒ FAILED: {len(failed_tests)} tests")
            for test in failed_tests:
                print(f"   âŒ {test}")

            # Generate DEZ validation report
            if len(failed_tests) == 0:
                print("\nğŸ‰ ALL TESTS PASSED - READY FOR DEZ VALIDATION")
                self.generate_dez_validation_report(cycle_results)
            else:
                print(f"\nğŸ”§ {len(failed_tests)} ISSUES FOUND - CODE EDITING REQUIRED")

            # Wait for code editing or exit
            if not self.wait_for_code_editing():
                break

    def generate_dez_validation_report(self, results):
        """Generate report for DEZ validation"""
        print("\n" + "="*60)
        print("ğŸ“‹ DEZ VALIDATION REPORT")
        print("="*60)

        print("ğŸ¯ TRIPLE VERIFICATION RESULTS:")
        print("   All tests passed Visual + Backend + State verification")
        print("   Ready for human validation")

        print("\nâœ… CONFIRMED WORKING:")
        for test, passed in results.items():
            if passed:
                print(f"   âœ… {test.replace('_', ' ').title()}")

        print("\nğŸ” TEST METHODOLOGY:")
        print("   1. Visual verification: UI responds correctly")
        print("   2. Backend verification: Functions work properly") 
        print("   3. State verification: Data model updates correctly")

        print("\nğŸš¨ IMPORTANT:")
        print("   These are automated test results")
        print("   DEZ validation still required to confirm user experience")
        print("   If DEZ reports issues, diagnostic tools need improvement")

        # Export detailed results
        report_file = f"dez_validation_report_{int(time.time())}.json"
        with open(report_file, 'w') as f:
            json.dump({
                'timestamp': time.time(),
                'results': results,
                'verification_log': self.verification_log,
                'state_snapshots': self.state_snapshots[-10:]  # Last 10 snapshots
            }, f, indent=2)

        print(f"\nğŸ“„ Detailed report saved: {report_file}")

    def cleanup(self):
        """Clean up browser session"""
        if self.driver:
            print("\nğŸ”„ Cleaning up browser session...")
            self.driver.quit()
            self.session_active = False

def main():
    diagnostic = TripleVerificationDiagnostic()

    try:
        diagnostic.initialize_persistent_session()
        diagnostic.run_comprehensive_test_cycle()
    except KeyboardInterrupt:
        print("\nâ¹ï¸  Test interrupted by user")
    except Exception as e:
        print(f"\nâŒ Error during testing: {e}")
    finally:
        diagnostic.cleanup()

if __name__ == "__main__":
    main()

