   399â†’    // Hit testing with enhanced handle detection
   400â†’    getShapeAtPoint(point, margin = 0) {
   401â†’        // First check if we're over a resize handle
   402â†’        const handleHit = this.getResizeHandleAtPoint(point);
   403â†’        if (handleHit) {
   404â†’            // Update cursor based on handle
   405â†’            if (this.renderer && this.renderer.updateCursorFromHandle) {
   406â†’                this.renderer.updateCursorFromHandle(handleHit);
   407â†’            }
   408â†’            return null; // Don't return shape when over handle
   409â†’        } else {
   410â†’            // Clear handle hover state
   411â†’            if (this.renderer && this.renderer.clearHandleHover) {
   412â†’                this.renderer.clearHandleHover();
   413â†’            }
   414â†’        }
   415â†’        
   416â†’        const shapes = this.getShapes();
   417â†’        const camera = this.store.getCurrentCamera();
   418â†’        
   419â†’        // Convert screen point to page point
   420â†’        const pagePoint = camera ? camera.screenToPage(point) : point;
   421â†’        
   422â†’        // Test shapes in reverse order (top to bottom)
   423â†’        for (let i = shapes.length - 1; i >= 0; i--) {
   424â†’            const shape = shapes[i];
   425â†’            if (this.isPointInShape(pagePoint, shape, margin)) {
   426â†’                return shape;
   427â†’            }
   428â†’        }
   429â†’        
   430â†’        return null;
   431â†’    }
   432â†’
   433â†’    // RESIZE CHUNK 1: Add resize handle hit detection
   434â†’    getResizeHandleAtPoint(point) {
   435â†’        if (!point || typeof point.x === 'undefined' || typeof point.y === 'undefined') {
   436â†’            return null; // Invalid point
   437â†’        }
   438â†’        
   439â†’        const selectedShapes = this.getSelectedShapes();
   440â†’        if (selectedShapes.length !== 1) {
   441â†’            return null; // Only show handles for single selection
   442â†’        }
   443â†’        
   444â†’        const shape = selectedShapes[0];
   445â†’        const bounds = shape.getBounds();
   446â†’        const camera = this.store.getCurrentCamera();
   447â†’        const handleSize = 8;
   448â†’        const scale = camera ? 1 / camera.z : 1;
   449â†’        const scaledHandleSize = handleSize * scale;
   450â†’        const hitMargin = scaledHandleSize / 2;
   451â†’        
   452â†’        // Convert screen point to page point
   453â†’        const pagePoint = camera ? camera.screenToPage(point) : point;
   454â†’        
   455â†’        console.log('ğŸ”§ RESIZE CHUNK 1: Testing handle hit at point:', {
   456â†’            screenPoint: point,
   457â†’            pagePoint: pagePoint,
   458â†’            bounds: bounds,
   459â†’            handleSize: scaledHandleSize,
   460â†’            camera: camera ? { x: camera.x, y: camera.y, z: camera.z } : null
   461â†’        });
   462â†’        
   463â†’        // Define handle positions (same as in renderer)
   464â†’        const handles = [
   465â†’            { x: bounds.x, y: bounds.y, type: 'top-left' },
   466â†’            { x: bounds.x + bounds.w, y: bounds.y, type: 'top-right' },
   467â†’            { x: bounds.x, y: bounds.y + bounds.h, type: 'bottom-left' },
   468â†’            { x: bounds.x + bounds.w, y: bounds.y + bounds.h, type: 'bottom-right' },
   469â†’            { x: bounds.x + bounds.w / 2, y: bounds.y, type: 'top' },
   470â†’            { x: bounds.x + bounds.w / 2, y: bounds.y + bounds.h, type: 'bottom' },
   471â†’            { x: bounds.x, y: bounds.y + bounds.h / 2, type: 'left' },
   472â†’            { x: bounds.x + bounds.w, y: bounds.y + bounds.h / 2, type: 'right' }
   473â†’        ];
   474â†’        
   475â†’        // Test each handle
   476â†’        for (const handle of handles) {
   477â†’            const distance = Math.hypot(
   478â†’                pagePoint.x - handle.x,
   479â†’                pagePoint.y - handle.y
   480â†’            );
   481â†’            
   482â†’            if (distance <= hitMargin) {
   483â†’                console.log('âœ… RESIZE CHUNK 1: Handle hit detected:', handle.type);
   484â†’                return {
   485â†’                    shape: shape,
   486â†’                    handle: handle.type,
   487â†’                    handlePos: { x: handle.x, y: handle.y },
   488â†’                    distance: distance
   489â†’                };
   490â†’            }
   491â†’        }
   492â†’        
   493â†’        console.log('âŒ RESIZE CHUNK 1: No handle hit');
   494â†’        return null;
   495â†’    }
   496â†’
   497â†’    isPointInShape(point, shape, margin = 0) {
   498â†’        const bounds = shape.getBounds();
   499â†’        
   500â†’        return point.x >= bounds.x - margin &&
   501â†’               point.x <= bounds.x + bounds.w + margin &&
   502â†’               point.y >= bounds.y - margin &&
   503â†’               point.y <= bounds.y + bounds.h + margin;
   504â†’    }
   505â†’
   506â†’    getShapesInBounds(bounds) {
   507â†’        const shapes = this.getShapes();
   508â†’        return shapes.filter(shape => {
   509â†’            const shapeBounds = shape.getBounds();
   510â†’            return this.boundsIntersect(bounds, shapeBounds);
   511â†’        });
   512â†’    }
   513â†’
   514â†’    boundsIntersect(a, b) {
   515â†’        return !(a.x + a.w < b.x || 
   516â†’                 b.x + b.w < a.x || 
   517â†’                 a.y + a.h < b.y || 
   518â†’                 b.y + b.h < a.y);
   519â†’    }
