   637â†’        const canvas = this.editor.canvas;
   638â†’        
   639â†’        // Pointer events
   640â†’        this.boundHandlers.pointerdown = this.handlePointerDown.bind(this);
   641â†’        this.boundHandlers.pointermove = this.handlePointerMove.bind(this);
   642â†’        this.boundHandlers.pointerup = this.handlePointerUp.bind(this);
   643â†’        this.boundHandlers.pointercancel = this.handlePointerUp.bind(this);
   644â†’        
   645â†’        canvas.addEventListener('pointerdown', this.boundHandlers.pointerdown);
