   657â†’        canvas.addEventListener('wheel', this.boundHandlers.wheel);
   658â†’        
   659â†’        // Keyboard events (on document)
   660â†’        this.boundHandlers.keydown = this.handleKeyDown.bind(this);
   661â†’        this.boundHandlers.keyup = this.handleKeyUp.bind(this);
