   690â†’    createEventInfo(type, domEvent) {
   691â†’        const rect = this.editor.canvas.getBoundingClientRect();
   692â†’        let currentPagePoint = { x: 0, y: 0 };
   693â†’        
   694â†’        if (domEvent && domEvent.clientX !== undefined) {
   695â†’            const screenPoint = {
   696â†’                x: domEvent.clientX - rect.left,
   697â†’                y: domEvent.clientY - rect.top
   698â†’            };
   699â†’            
   700â†’            const camera = this.editor.getCamera();
   701â†’            currentPagePoint = camera ? camera.screenToPage(screenPoint) : screenPoint;
   702â†’        }
   703â†’        
   704â†’        return new TLEventInfo(type, {
   705â†’            currentPagePoint,
   706â†’            originPagePoint: currentPagePoint,
   707â†’            screenPoint: domEvent && domEvent.clientX !== undefined ? {
   708â†’                x: domEvent.clientX - rect.left,
   709â†’                y: domEvent.clientY - rect.top
   710â†’            } : { x: 0, y: 0 },
   711â†’            shiftKey: domEvent?.shiftKey || false,
   712â†’            ctrlKey: domEvent?.ctrlKey || false,
   713â†’            altKey: domEvent?.altKey || false,
   714â†’            metaKey: domEvent?.metaKey || false,
   715â†’            pointerId: domEvent?.pointerId || 0,
   716â†’            button: domEvent?.button || 0,
   717â†’            isPen: domEvent?.pointerType === 'pen',
   718â†’            target: domEvent?.target,
   719â†’            key: domEvent?.key || '',
   720â†’            code: domEvent?.code || '',
   721â†’            delta: domEvent?.deltaX !== undefined ? 
   722â†’                { x: domEvent.deltaX, y: domEvent.deltaY } : 
   723â†’                { x: 0, y: 0 }
   724â†’        });
   725â†’    }
