  1023â†’        this.editor.store.transaction(() => {
  1024â†’            selectedShapes.forEach(shape => {
  1025â†’                const currentBounds = shape.getBounds();
  1026â†’                const newW = Math.max(20, currentBounds.w + deltaW);
  1027â†’                const newH = Math.max(20, currentBounds.h + deltaH);
