  1240â†’    // UNIVERSAL SMART CONTEXTUAL INPUT SYSTEM
  1241â†’    analyzeFullContext(event) {
  1242â†’        const pointerPos = {
  1243â†’            x: event.clientX - this.editor.canvas.getBoundingClientRect().left,
  1244â†’            y: event.clientY - this.editor.canvas.getBoundingClientRect().top
  1245â†’        };
  1246â†’        
  1247â†’        const currentTool = this.editor.getCurrentTool();
  1248â†’        const selectedShapes = this.editor.getSelectedShapes();
  1249â†’        const shapeAtPoint = this.editor.getShapeAtPoint(pointerPos);
  1250â†’        const resizeHandle = this.editor.getResizeHandleAtPoint(pointerPos);
  1251â†’        
  1252â†’        return {
  1253â†’            pointer: pointerPos,
  1254â†’            currentTool: currentTool ? currentTool.id : 'select',
  1255â†’            selectedShapes: selectedShapes,
  1256â†’            selectedCount: selectedShapes.length,
  1257â†’            shapeAtPoint: shapeAtPoint,
  1258â†’            resizeHandle: resizeHandle,
  1259â†’            isOverShape: !!shapeAtPoint,
  1260â†’            isOverHandle: !!resizeHandle,
  1261â†’            isEmptyCanvas: !shapeAtPoint && !resizeHandle
  1262â†’        };
  1263â†’    }
  1264â†’
  1265â†’    determineContextualAction(context) {
  1266â†’        // Priority-based contextual decision system
  1267â†’        
  1268â†’        // HIGHEST PRIORITY: Resize handle detected
  1269â†’        if (context.isOverHandle && context.selectedCount > 0) {
  1270â†’            return {
  1271â†’                type: 'resize',
  1272â†’                target: context.selectedShapes,
  1273â†’                handle: context.resizeHandle,
  1274â†’                description: 'Resize selected shapes'
  1275â†’            };
  1276â†’        }
  1277â†’        
  1278â†’        // HIGH PRIORITY: Shape selected and pointer over selected shape = move
  1279â†’        if (context.selectedCount > 0 && context.shapeAtPoint && 
  1280â†’            context.selectedShapes.some(s => s.id === context.shapeAtPoint.id)) {
  1281â†’            return {
  1282â†’                type: 'move',
  1283â†’                target: context.selectedShapes,
  1284â†’                description: 'Move selected shapes'
  1285â†’            };
  1286â†’        }
  1287â†’        
  1288â†’        // MEDIUM PRIORITY: Shape creation tools
  1289â†’        if (['rectangle', 'ellipse', 'draw'].includes(context.currentTool)) {
  1290â†’            return {
  1291â†’                type: 'create',
  1292â†’                tool: context.currentTool,
  1293â†’                description: `Create ${context.currentTool} shape`
  1294â†’            };
  1295â†’        }
  1296â†’        
  1297â†’        // MEDIUM PRIORITY: Eraser tool over shape
  1298â†’        if (context.currentTool === 'eraser' && context.isOverShape) {
  1299â†’            return {
  1300â†’                type: 'erase',
  1301â†’                target: context.shapeAtPoint,
  1302â†’                description: 'Erase shape'
  1303â†’            };
  1304â†’        }
  1305â†’        
  1306â†’        // LOW PRIORITY: Shape at point but different tool = select shape then use tool
  1307â†’        if (context.isOverShape) {
  1308â†’            return {
  1309â†’                type: 'select_and_tool',
  1310â†’                target: context.shapeAtPoint,
  1311â†’                tool: context.currentTool,
  1312â†’                description: `Select shape and apply ${context.currentTool} tool`
  1313â†’            };
  1314â†’        }
  1315â†’        
  1316â†’        // DEFAULT: Empty canvas = use current tool
  1317â†’        return {
  1318â†’            type: 'tool_default',
  1319â†’            tool: context.currentTool,
  1320â†’            description: `Use ${context.currentTool} tool on empty canvas`
  1321â†’        };
  1322â†’    }
  1323â†’
  1324â†’    executeContextualAction(action, context, event) {
  1325â†’        console.log('ğŸ–ï¸ CONTEXTUAL: Executing action:', action.type);
  1326â†’        
  1327â†’        // SIMPLIFIED APPROACH: Just ensure correct tool is active
  1328â†’        // Let the normal pointer event flow handle the actual interaction
  1329â†’        switch (action.type) {
  1330â†’            case 'resize':
  1331â†’            case 'move':
  1332â†’                // For resize/move, ensure select tool is active
  1333â†’                this.editor.setCurrentTool('select');
  1334â†’                this.updateToolbarUI('select');
