     1â†’/**
     2â†’ * Complete Canvas System Integration
     3â†’ * Main editor class that orchestrates all canvas functionality
     4â†’ */
     5â†’
     6â†’// =====================================================
     7â†’// MAIN CANVAS EDITOR
     8â†’// =====================================================
     9â†’
    10â†’class CanvasEditor extends EventTarget {
    11â†’    constructor(container, options = {}) {
    12â†’        super();
    13â†’        
    14â†’        // Core setup
    15â†’        this.container = container;
    16â†’        this.options = { 
    17â†’            persistenceKey: 'tldraw-canvas',
    18â†’            autoSave: true,
    19â†’            ...options 
    20â†’        };
    21â†’        
    22â†’        // Initialize systems
    23â†’        this.initializeCanvas();
    24â†’        this.initializeSystems();
    25â†’        this.setupEventListeners();
    26â†’        this.setupPersistence();
    27â†’        
    28â†’        // Start the editor
    29â†’        this.start();
    30â†’    }
    31â†’
    32â†’    // =====================================================
    33â†’    // INITIALIZATION
    34â†’    // =====================================================
    35â†’
    36â†’    initializeCanvas() {
    37â†’        // Create canvas element
    38â†’        this.canvas = document.createElement('canvas');
    39â†’        this.canvas.className = 'tldraw-canvas';
    40â†’        this.canvas.style.width = '100%';
    41â†’        this.canvas.style.height = '100%';
    42â†’        this.canvas.style.display = 'block';
    43â†’        this.canvas.style.touchAction = 'none';
    44â†’        
    45â†’        // Set size
    46â†’        const rect = this.container.getBoundingClientRect();
    47â†’        this.canvas.width = rect.width;
    48â†’        this.canvas.height = rect.height;
    49â†’        
    50â†’        // Add to container
    51â†’        this.container.appendChild(this.canvas);
    52â†’    }
    53â†’
    54â†’    initializeSystems() {
    55â†’        // Core systems
    56â†’        this.store = new ReactiveStore();
    57â†’        this.camera = new CameraSystem(this.store, this.canvas);
    58â†’        this.drawingEngine = new DrawingEngine(this.canvas, this.store, this.camera);
    59â†’        this.toolManager = new SimpleToolManager(this);
    60â†’        
    61â†’        // UI state
    62â†’        this.currentColor = '#000000';
    63â†’        this.currentFill = 'none';
    64â†’        this.currentStroke = '#000000';
    65â†’        this.currentStrokeWidth = 2;
    66â†’        this.currentSize = 'medium';
    67â†’        this.currentOpacity = 1;
    68â†’        
    69â†’        // Interaction state
    70â†’        this.brush = null;
    71â†’        this.contextMenu = null;
    72â†’        this.isEditingText = false;
    73â†’        
    74â†’        // Performance tracking
    75â†’        this.renderStats = {
    76â†’            frameCount: 0,
    77â†’            lastFrameTime: 0,
    78â†’            averageFPS: 60
    79â†’        };
    80â†’    }
    81â†’
    82â†’    setupEventListeners() {
    83â†’        // Canvas events - comprehensive interaction support
    84â†’        this.canvas.addEventListener('pointerdown', this.handlePointerDown.bind(this));
    85â†’        this.canvas.addEventListener('pointermove', this.handlePointerMove.bind(this));
    86â†’        this.canvas.addEventListener('pointerup', this.handlePointerUp.bind(this));
    87â†’        this.canvas.addEventListener('pointercancel', this.handlePointerUp.bind(this));
    88â†’        this.canvas.addEventListener('dblclick', this.handleDoubleClick.bind(this));
    89â†’        this.canvas.addEventListener('contextmenu', this.handleContextMenu.bind(this));
    90â†’        this.canvas.addEventListener('wheel', this.handleWheel.bind(this), { passive: false });
    91â†’        
    92â†’        // Touch events for multi-touch gestures
    93â†’        this.canvas.addEventListener('touchstart', this.handleTouchStart.bind(this), { passive: false });
    94â†’        this.canvas.addEventListener('touchmove', this.handleTouchMove.bind(this), { passive: false });
    95â†’        this.canvas.addEventListener('touchend', this.handleTouchEnd.bind(this), { passive: false });
    96â†’        this.canvas.addEventListener('touchcancel', this.handleTouchEnd.bind(this), { passive: false });
    97â†’        
    98â†’        // Keyboard events
    99â†’        document.addEventListener('keydown', this.handleKeyDown.bind(this));
   100â†’        document.addEventListener('keyup', this.handleKeyUp.bind(this));
   101â†’        
   102â†’        // Window events
   103â†’        window.addEventListener('resize', this.handleResize.bind(this));
   104â†’        window.addEventListener('beforeunload', this.handleBeforeUnload.bind(this));
   105â†’        
   106â†’        // Store events
   107â†’        this.store.addEventListener('change', this.handleStoreChange.bind(this));
   108â†’        
   109â†’        // Drawing engine events
   110â†’        this.drawingEngine.canvas.addEventListener('render', this.handleRender.bind(this));
   111â†’        
   112â†’        // Interaction state
   113â†’        this.pointers = new Map(); // Track multiple pointers for multi-touch
   114â†’        this.lastPinchDistance = 0;
   115â†’        this.isPanning = false;
   116â†’        this.isResizing = false;
   117â†’        this.isDragging = false;
   118â†’    }
   119â†’
   120â†’    setupPersistence() {
   121â†’        if (this.options.persistenceKey) {
   122â†’            this.persistence = new PersistenceManager(this.store, this.options.persistenceKey);
   123â†’            
   124â†’            // Try to load saved state
   125â†’            this.persistence.load();
   126â†’            
   127â†’            // Listen for save events
   128â†’            this.store.addEventListener('saved', (event) => {
   129â†’                this.emit('saved', event.detail);
   130â†’                this.updateLastSaved();
   131â†’            });
   132â†’        }
   133â†’    }
   134â†’
   135â†’    // =====================================================
   136â†’    // LIFECYCLE
   137â†’    // =====================================================
   138â†’
   139â†’    start() {
   140â†’        // Build spatial index
   141â†’        this.drawingEngine.buildSpatialIndex();
   142â†’        
   143â†’        // Start rendering
   144â†’        this.drawingEngine.scheduleRender();
   145â†’        
   146â†’        // Initialize tools
   147â†’        this.setTool(TLToolType.SELECT);
   148â†’        
   149â†’        // Emit ready event
   150â†’        this.emit('ready');
   151â†’    }
   152â†’
   153â†’    destroy() {
   154â†’        // Clean up event listeners
   155â†’        this.canvas.removeEventListener('pointerdown', this.handlePointerDown);
   156â†’        this.canvas.removeEventListener('pointermove', this.handlePointerMove);
   157â†’        this.canvas.removeEventListener('pointerup', this.handlePointerUp);
   158â†’        this.canvas.removeEventListener('dblclick', this.handleDoubleClick);
   159â†’        this.canvas.removeEventListener('contextmenu', this.handleContextMenu);
   160â†’        this.canvas.removeEventListener('wheel', this.handleWheel);
   161â†’        
   162â†’        document.removeEventListener('keydown', this.handleKeyDown);
   163â†’        document.removeEventListener('keyup', this.handleKeyUp);
   164â†’        window.removeEventListener('resize', this.handleResize);
   165â†’        window.removeEventListener('beforeunload', this.handleBeforeUnload);
   166â†’        
   167â†’        // Remove canvas
   168â†’        if (this.canvas.parentNode) {
   169â†’            this.canvas.parentNode.removeChild(this.canvas);
   170â†’        }
   171â†’        
   172â†’        // Clean up systems
   173â†’        this.toolManager = null;
   174â†’        this.drawingEngine = null;
   175â†’        this.camera = null;
   176â†’        this.store = null;
   177â†’    }
   178â†’
   179â†’    // =====================================================
   180â†’    // EVENT HANDLERS
   181â†’    // =====================================================
   182â†’
   183â†’    handlePointerDown(event) {
   184â†’        event.preventDefault();
   185â†’        this.canvas.setPointerCapture(event.pointerId);
   186â†’        
   187â†’        const eventInfo = this.createEventInfo(TLEventType.POINTER_DOWN, event);
   188â†’        this.toolManager.handleEvent(eventInfo);
   189â†’    }
   190â†’
   191â†’    handlePointerMove(event) {
   192â†’        const eventInfo = this.createEventInfo(TLEventType.POINTER_MOVE, event);
   193â†’        this.toolManager.handleEvent(eventInfo);
   194â†’        
   195â†’        // Update hover state and cursor
   196â†’        this.updateHover(eventInfo.currentPagePoint);
   197â†’        this.updateCursor(eventInfo.currentPagePoint);
   198â†’    }
   199â†’
   200â†’    updateCursor(point) {
   201â†’        let cursor = 'default';
   202â†’        
   203â†’        if (this.getActiveToolId() === 'select') {
   204â†’            // Check for resize handles first
   205â†’            const handle = this.drawingEngine.getHandleAtPoint(point);
   206â†’            if (handle) {
   207â†’                cursor = handle.cursor;
   208â†’            } else {
   209â†’                // Check if hovering over a shape
   210â†’                const hitShape = this.drawingEngine.getShapeAtPoint(point);
   211â†’                if (hitShape) {
   212â†’                    cursor = 'move';
   213â†’                }
   214â†’            }
   215â†’        } else if (this.getActiveToolId() === 'hand') {
   216â†’            cursor = 'grab';
   217â†’        } else if (['rectangle', 'ellipse', 'arrow', 'line'].includes(this.getActiveToolId())) {
   218â†’            cursor = 'crosshair';
   219â†’        } else if (this.getActiveToolId() === 'text') {
   220â†’            cursor = 'text';
   221â†’        } else if (this.getActiveToolId() === 'draw') {
   222â†’            cursor = 'crosshair';
   223â†’        }
   224â†’        
   225â†’        this.canvas.style.cursor = cursor;
   226â†’    }
   227â†’
   228â†’    handlePointerUp(event) {
   229â†’        event.preventDefault();
   230â†’        this.canvas.releasePointerCapture(event.pointerId);
   231â†’        
   232â†’        const eventInfo = this.createEventInfo(TLEventType.POINTER_UP, event);
   233â†’        this.toolManager.handleEvent(eventInfo);
   234â†’    }
   235â†’
   236â†’    handleDoubleClick(event) {
   237â†’        event.preventDefault();
   238â†’        
   239â†’        const eventInfo = this.createEventInfo(TLEventType.DOUBLE_CLICK, event);
   240â†’        this.toolManager.handleEvent(eventInfo);
   241â†’    }
   242â†’
   243â†’    handleContextMenu(event) {
   244â†’        console.log('CanvasEditor: handleContextMenu called', event);
   245â†’        event.preventDefault();
   246â†’        
   247â†’        // Delegate to SimpleToolManager's context menu
   248â†’        if (this.toolManager && this.toolManager.showContextMenu) {
   249â†’            console.log('CanvasEditor: Delegating to SimpleToolManager');
   250â†’            this.toolManager.showContextMenu(event);
   251â†’        } else {
   252â†’            console.log('CanvasEditor: No SimpleToolManager found, using fallback');
   253â†’            // Fallback to emit event
   254â†’            const rect = this.canvas.getBoundingClientRect();
   255â†’            const screenPoint = {
   256â†’                x: event.clientX - rect.left,
   257â†’                y: event.clientY - rect.top
   258â†’            };
   259â†’            
   260â†’            const pagePoint = this.camera.screenToPage(screenPoint);
   261â†’            const hitShape = this.drawingEngine.getShapeAtPoint(screenPoint);
   262â†’            
   263â†’            this.showContextMenu(screenPoint, hitShape);
   264â†’        }
   265â†’    }
   266â†’
   267â†’    handleWheel(event) {
   268â†’        event.preventDefault();
   269â†’        
   270â†’        const rect = this.canvas.getBoundingClientRect();
   271â†’        const screenPoint = {
   272â†’            x: event.clientX - rect.left,
   273â†’            y: event.clientY - rect.top
   274â†’        };
   275â†’        
   276â†’        const camera = this.store.getCurrentCamera();
   277â†’        if (!camera) return;
   278â†’        
   279â†’        // Handle zoom with wheel (Ctrl+scroll or trackpad pinch)
   280â†’        if (event.ctrlKey || event.metaKey || Math.abs(event.deltaY) > Math.abs(event.deltaX)) {
   281â†’            const zoomDelta = event.deltaY > 0 ? -0.1 : 0.1;
   282â†’            const pagePoint = this.camera.screenToPage(screenPoint);
   283â†’            camera.zoom(zoomDelta, pagePoint);
   284â†’            this.store.put(camera);
   285â†’        } else {
   286â†’            // Pan with wheel (horizontal scroll or Shift+scroll)
   287â†’            const panDelta = {
   288â†’                x: -event.deltaX * 0.5,
   289â†’                y: -event.deltaY * 0.5
   290â†’            };
   291â†’            camera.pan(panDelta);
   292â†’            this.store.put(camera);
   293â†’        }
   294â†’    }
   295â†’
   296â†’    // Touch event handlers for multi-touch gestures
   297â†’    handleTouchStart(event) {
   298â†’        event.preventDefault();
   299â†’        
   300â†’        // Check for multi-finger touch gestures
   301â†’        if (event.touches.length === 3) {
   302â†’            console.log('Three-finger touch detected - switching to draw tool');
   303â†’            this.setTool('draw');
   304â†’            return;
   305â†’        }
   306â†’        
   307â†’        // CRITICAL FIX: Check for four-finger touch to activate eraser tool
   308â†’        if (event.touches.length === 4) {
   309â†’            console.log('Four-finger touch detected - switching to eraser tool');
   310â†’            this.setTool('eraser');
   311â†’            return;
   312â†’        }
   313â†’        
   314â†’        // Update pointer tracking
   315â†’        for (const touch of event.changedTouches) {
   316â†’            this.pointers.set(touch.identifier, {
   317â†’                x: touch.clientX,
   318â†’                y: touch.clientY,
   319â†’                pageX: touch.pageX,
   320â†’                pageY: touch.pageY
   321â†’            });
   322â†’        }
   323â†’
   324â†’        // Handle multi-touch gestures
   325â†’        if (event.touches.length === 2) {
   326â†’            const touch1 = event.touches[0];
   327â†’            const touch2 = event.touches[1];
   328â†’            this.lastPinchDistance = this.getTouchDistance(touch1, touch2);
   329â†’        }
   330â†’    }
   331â†’
   332â†’    handleTouchMove(event) {
   333â†’        event.preventDefault();
   334â†’        
   335â†’        if (event.touches.length === 1) {
   336â†’            // Single touch - pan the canvas
   337â†’            const touch = event.touches[0];
   338â†’            const lastPointer = this.pointers.get(touch.identifier);
   339â†’            
   340â†’            if (lastPointer) {
   341â†’                const deltaX = touch.clientX - lastPointer.x;
   342â†’                const deltaY = touch.clientY - lastPointer.y;
   343â†’                
   344â†’                const camera = this.store.getCurrentCamera();
   345â†’                if (camera) {
   346â†’                    camera.pan({ x: deltaX, y: deltaY });
   347â†’                    this.store.put(camera);
   348â†’                }
   349â†’                
   350â†’                // Update pointer position
   351â†’                this.pointers.set(touch.identifier, {
   352â†’                    x: touch.clientX,
   353â†’                    y: touch.clientY,
   354â†’                    pageX: touch.pageX,
   355â†’                    pageY: touch.pageY
   356â†’                });
   357â†’            }
   358â†’        } else if (event.touches.length === 2) {
   359â†’            // Two fingers - pinch to zoom
   360â†’            const touch1 = event.touches[0];
   361â†’            const touch2 = event.touches[1];
   362â†’            const currentDistance = this.getTouchDistance(touch1, touch2);
   363â†’            
   364â†’            if (this.lastPinchDistance > 0) {
   365â†’                const zoomDelta = (currentDistance - this.lastPinchDistance) * 0.01;
   366â†’                const centerX = (touch1.clientX + touch2.clientX) / 2;
   367â†’                const centerY = (touch1.clientY + touch2.clientY) / 2;
   368â†’                
   369â†’                const rect = this.canvas.getBoundingClientRect();
   370â†’                const screenPoint = {
   371â†’                    x: centerX - rect.left,
   372â†’                    y: centerY - rect.top
   373â†’                };
   374â†’                
   375â†’                const camera = this.store.getCurrentCamera();
   376â†’                if (camera) {
   377â†’                    const pagePoint = this.camera.screenToPage(screenPoint);
   378â†’                    camera.zoom(zoomDelta, pagePoint);
   379â†’                    this.store.put(camera);
   380â†’                }
   381â†’            }
   382â†’            
   383â†’            this.lastPinchDistance = currentDistance;
   384â†’        }
   385â†’    }
   386â†’
   387â†’    handleTouchEnd(event) {
   388â†’        event.preventDefault();
   389â†’        
   390â†’        // Remove ended touches from tracking
   391â†’        for (const touch of event.changedTouches) {
   392â†’            this.pointers.delete(touch.identifier);
   393â†’        }
   394â†’        
   395â†’        // Reset pinch tracking if no touches remain
   396â†’        if (event.touches.length < 2) {
   397â†’            this.lastPinchDistance = 0;
   398â†’        }
   399â†’    }
   400â†’
   401â†’    getTouchDistance(touch1, touch2) {
   402â†’        const dx = touch2.clientX - touch1.clientX;
   403â†’        const dy = touch2.clientY - touch1.clientY;
   404â†’        return Math.sqrt(dx * dx + dy * dy);
   405â†’    }
   406â†’
   407â†’    handleKeyDown(event) {
   408â†’        // Check for tool shortcuts first
   409â†’        if (this.toolManager.handleKeyboardShortcut(event.key, event.shiftKey)) {
   410â†’            event.preventDefault();
   411â†’            return;
   412â†’        }
   413â†’        
   414â†’        const eventInfo = this.createEventInfo(TLEventType.KEY_DOWN, event);
