   509â†’    createEventInfo(type, domEvent) {
   510â†’        const rect = this.canvas.getBoundingClientRect();
   511â†’        const camera = this.store.getCurrentCamera();
   512â†’        
   513â†’        let currentPagePoint = { x: 0, y: 0 };
   514â†’        
   515â†’        if (domEvent && domEvent.clientX !== undefined) {
   516â†’            const screenPoint = {
   517â†’                x: domEvent.clientX - rect.left,
   518â†’                y: domEvent.clientY - rect.top
   519â†’            };
   520â†’            
   521â†’            if (camera) {
   522â†’                currentPagePoint = camera.screenToPage(screenPoint);
   523â†’            }
   524â†’        }
   525â†’        
   526â†’        return new TLEventInfo(type, {
   527â†’            currentPagePoint,
   528â†’            originPagePoint: currentPagePoint,
   529â†’            shiftKey: domEvent?.shiftKey || false,
   530â†’            ctrlKey: domEvent?.ctrlKey || false,
   531â†’            altKey: domEvent?.altKey || false,
   532â†’            metaKey: domEvent?.metaKey || false,
   533â†’            pointerId: domEvent?.pointerId || 0,
   534â†’            button: domEvent?.button || 0,
   535â†’            pressure: domEvent?.pressure || 0.5,
   536â†’            isPen: domEvent?.pointerType === 'pen',
   537â†’            target: domEvent?.target,
   538â†’            key: domEvent?.key || '',
   539â†’            code: domEvent?.code || '',
   540â†’            delta: domEvent?.deltaX !== undefined ? 
   541â†’                { x: domEvent.deltaX, y: domEvent.deltaY } : { x: 0, y: 0 }
   542â†’        });
   543â†’    }
