/**
 * Enterprise Diagnostic Architecture
 * 
 * Implements scalable, modular diagnostic services using:
 * - Dependency Injection Container
 * - Interface-based Design
 * - Event-driven Architecture
 * - Plugin System
 * - Comprehensive Error Handling
 * 
 * Date: 2025-07-25
 * Architecture: Enterprise-grade, horizontally scalable
 */

// =============================================================================
// CORE INTERFACES & CONTRACTS
// =============================================================================

/**
 * Core diagnostic service interface
 */
class IDiagnosticService {
    async diagnose(target, context) {
        throw new Error('Method must be implemented by concrete service');
    }

    async verify(action, expectedResult) {
        throw new Error('Method must be implemented by concrete service');
    }

    getServiceId() {
        throw new Error('Method must be implemented by concrete service');
    }
}

/**
 * Event bus interface for decoupled communication
 */
class IEventBus {
    emit(event, data) {
        throw new Error('Method must be implemented');
    }

    subscribe(event, handler) {
        throw new Error('Method must be implemented');
    }

    unsubscribe(event, handler) {
        throw new Error('Method must be implemented');
    }
}

/**
 * Logger interface for structured logging
 */
class ILogger {
    info(message, data) {
        throw new Error('Method must be implemented');
    }

    warn(message, data) {
        throw new Error('Method must be implemented');
    }

    error(message, data) {
        throw new Error('Method must be implemented');
    }

    debug(message, data) {
        throw new Error('Method must be implemented');
    }
}

// =============================================================================
// DEPENDENCY INJECTION CONTAINER
// =============================================================================

class DiagnosticContainer {
    constructor() {
        this.services = new Map();
        this.instances = new Map();
        this.interfaces = new Map();
        this.configs = new Map();
    }

    /**
     * Register a service implementation for an interface
     */
    register(interfaceName, implementation, config = {}) {
        this.services.set(interfaceName, {
            implementation,
            config,
            singleton: config.singleton !== false,
            dependencies: config.dependencies || []
        });

        console.log(`ğŸ—ï¸ Registered service: ${interfaceName}`);
        return this;
    }

    /**
     * Resolve a service instance with dependency injection
     */
    resolve(interfaceName) {
        const serviceInfo = this.services.get(interfaceName);

        if (!serviceInfo) {
            throw new Error(`Service not registered: ${interfaceName}`);
        }

        // Return singleton instance if already created
        if (serviceInfo.singleton && this.instances.has(interfaceName)) {
            return this.instances.get(interfaceName);
        }

        // Resolve dependencies first
        const dependencies = serviceInfo.dependencies.map(dep => this.resolve(dep));

        // Create instance with dependency injection
        const instance = new serviceInfo.implementation(...dependencies, serviceInfo.config);

        // Store singleton instance
        if (serviceInfo.singleton) {
            this.instances.set(interfaceName, instance);
        }

        console.log(`ğŸ”§ Resolved service: ${interfaceName}`);
        return instance;
    }

    /**
     * Configure service parameters
     */
    configure(interfaceName, config) {
        const existing = this.configs.get(interfaceName) || {};
        this.configs.set(interfaceName, { ...existing, ...config });
        return this;
    }

    /**
     * Check if service is registered
     */
    has(interfaceName) {
        return this.services.has(interfaceName);
    }

    /**
     * Get all registered service names
     */
    getRegisteredServices() {
        return Array.from(this.services.keys());
    }
}

// =============================================================================
// EVENT-DRIVEN COMMUNICATION
// =============================================================================

class DiagnosticEventBus extends IEventBus {
    constructor() {
        super();
        this.listeners = new Map();
        this.middlewares = [];
        this.eventHistory = [];
        this.maxHistorySize = 1000;
    }

    /**
     * Emit an event with optional middleware processing
     */
    emit(event, data = {}) {
        const eventObject = {
            type: event,
            data,
            timestamp: Date.now(),
            id: this.generateEventId()
        };

        // Process through middleware
        const processedEvent = this.applyMiddleware(eventObject);

        // Store in history
        this.addToHistory(processedEvent);

        // Emit to listeners
        const listeners = this.listeners.get(event) || [];
        listeners.forEach(listener => {
            try {
                listener(processedEvent);
            } catch (error) {
                console.error(`Event listener error for ${event}:`, error);
            }
        });

        // Emit to wildcard listeners
        const wildcardListeners = this.listeners.get('*') || [];
        wildcardListeners.forEach(listener => {
            try {
                listener(processedEvent);
            } catch (error) {
                console.error(`Wildcard listener error:`, error);
            }
        });

        return eventObject;
    }

    /**
     * Subscribe to events with optional filter
     */
    subscribe(event, handler, options = {}) {
        if (!this.listeners.has(event)) {
            this.listeners.set(event, []);
        }

        const wrappedHandler = options.filter ? 
            (eventObj) => options.filter(eventObj) && handler(eventObj) :
            handler;

        this.listeners.get(event).push(wrappedHandler);

        return () => this.unsubscribe(event, wrappedHandler);
    }

    /**
     * Unsubscribe from events
     */
    unsubscribe(event, handler) {
        const listeners = this.listeners.get(event);
        if (listeners) {
            const index = listeners.indexOf(handler);
            if (index > -1) {
                listeners.splice(index, 1);
            }
        }
    }

    /**
     * Add middleware for event processing
     */
    addMiddleware(middleware) {
        this.middlewares.push(middleware);
    }

    /**
     * Apply middleware to events
     */
    applyMiddleware(event) {
        return this.middlewares.reduce((processedEvent, middleware) => {
            return middleware(processedEvent);
        }, event);
    }

    /**
     * Generate unique event ID
     */
    generateEventId() {
        return `event_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }

    /**
     * Add event to history with size management
     */
    addToHistory(event) {
        this.eventHistory.push(event);
        if (this.eventHistory.length > this.maxHistorySize) {
            this.eventHistory.shift();
        }
    }

    /**
     * Get event history with optional filtering
     */
    getHistory(filter = null) {
        return filter ? this.eventHistory.filter(filter) : [...this.eventHistory];
    }
}

// =============================================================================
// STRUCTURED LOGGING SYSTEM
// =============================================================================

class StructuredLogger extends ILogger {
    constructor(config = {}) {
        super();
        this.config = {
            level: config.level || 'info',
            namespace: config.namespace || 'diagnostic',
            enableConsole: config.enableConsole !== false,
            enableHistory: config.enableHistory !== false,
            maxHistorySize: config.maxHistorySize || 1000,
            ...config
        };

        this.levels = {
            debug: 0,
            info: 1,
            warn: 2,
            error: 3
        };

        this.history = [];
        this.currentLevel = this.levels[this.config.level] || 1;
    }

    /**
     * Log with structured format
     */
    log(level, message, data = {}) {
        if (this.levels[level] < this.currentLevel) {
            return; // Skip if below current log level
        }

        const logEntry = {
            timestamp: new Date().toISOString(),
            level,
            namespace: this.config.namespace,
            message,
            data,
            stackTrace: level === 'error' ? new Error().stack : null
        };

        // Add to history
        if (this.config.enableHistory) {
            this.addToHistory(logEntry);
        }

        // Console output
        if (this.config.enableConsole) {
            this.outputToConsole(logEntry);
        }

        return logEntry;
    }

    info(message, data) {
        return this.log('info', message, data);
    }

    warn(message, data) {
        return this.log('warn', message, data);
    }

    error(message, data) {
        return this.log('error', message, data);
    }

    debug(message, data) {
        return this.log('debug', message, data);
    }

    /**
     * Output formatted log to console
     */
    outputToConsole(logEntry) {
        const { timestamp, level, namespace, message, data } = logEntry;
        const prefix = `[${timestamp}] ${level.toUpperCase()} [${namespace}]`;

        const consoleMethods = {
            debug: console.debug,
            info: console.info,
            warn: console.warn,
            error: console.error
        };

        const method = consoleMethods[level] || console.log;

        if (Object.keys(data).length > 0) {
            method(`${prefix} ${message}`, data);
        } else {
            method(`${prefix} ${message}`);
        }
    }

    /**
     * Add to history with size management
     */
    addToHistory(logEntry) {
        this.history.push(logEntry);
        if (this.history.length > this.config.maxHistorySize) {
            this.history.shift();
        }
    }

    /**
     * Get log history with optional filtering
     */
    getHistory(filter = null) {
        return filter ? this.history.filter(filter) : [...this.history];
    }

    /**
     * Set log level dynamically
     */
    setLevel(level) {
        if (this.levels[level] !== undefined) {
            this.currentLevel = this.levels[level];
            this.config.level = level;
        }
    }
}

// =============================================================================
// TOOL SELECTION DIAGNOSTIC SERVICE
// =============================================================================

class ToolSelectionDiagnosticService extends IDiagnosticService {
    constructor(eventBus, logger, config = {}) {
        super();
        this.eventBus = eventBus;
        this.logger = logger;
        this.config = {
            retryAttempts: config.retryAttempts || 3,
            retryDelay: config.retryDelay || 100,
            timeout: config.timeout || 5000,
            ...config
        };

        this.serviceId = 'tool-selection-diagnostic';
        this.state = {
            isRunning: false,
            lastDiagnosis: null,
            statistics: {
                totalDiagnoses: 0,
                successfulDiagnoses: 0,
                failedDiagnoses: 0
            }
        };

        this.setupEventListeners();
        this.logger.info('ToolSelectionDiagnosticService initialized', { serviceId: this.serviceId });
    }

    /**
     * Setup event listeners for diagnostic coordination
     */
    setupEventListeners() {
        this.eventBus.subscribe('tool-selection-requested', (event) => {
            this.handleToolSelectionRequest(event.data);
        });

        this.eventBus.subscribe('editor-state-changed', (event) => {
            this.handleEditorStateChange(event.data);
        });
    }

    /**
     * Main diagnostic method for tool selection issues
     */
    async diagnose(target = 'editor', context = {}) {
        this.state.isRunning = true;
        this.state.statistics.totalDiagnoses++;

        const diagnosisId = this.generateDiagnosisId();

        this.logger.info('Starting tool selection diagnosis', {
            diagnosisId,
            target,
            context
        });

        this.eventBus.emit('diagnosis-started', {
            serviceId: this.serviceId,
            diagnosisId,
            target,
            context
        });

        try {
            const result = await this.performComprehensiveDiagnosis(target, context, diagnosisId);

            this.state.lastDiagnosis = result;
            this.state.statistics.successfulDiagnoses++;

            this.eventBus.emit('diagnosis-completed', {
                serviceId: this.serviceId,
                diagnosisId,
                result
            });

            this.logger.info('Tool selection diagnosis completed', {
                diagnosisId,
                success: result.success,
                issues: result.issues.length
            });

            return result;

        } catch (error) {
            this.state.statistics.failedDiagnoses++;

            this.eventBus.emit('diagnosis-failed', {
                serviceId: this.serviceId,
                diagnosisId,
                error: error.message
            });

            this.logger.error('Tool selection diagnosis failed', {
                diagnosisId,
                error: error.message,
                stack: error.stack
            });

            throw error;

        } finally {
            this.state.isRunning = false;
        }
    }

    /**
     * Perform comprehensive diagnosis with multiple verification layers
     */
    async performComprehensiveDiagnosis(target, context, diagnosisId) {
        const diagnosis = {
            id: diagnosisId,
            timestamp: Date.now(),
            target,
            context,
            success: false,
            layers: {
                editor: null,
                rootState: null,
                toolSystem: null,
                eventSystem: null
            },
            issues: [],
            recommendations: [],
            metadata: {
                duration: 0,
                retryCount: 0
            }
        };

        const startTime = Date.now();

        try {
            // Layer 1: Editor Existence and Initialization
            diagnosis.layers.editor = await this.diagnoseEditorLayer();

            // Layer 2: RootState and Tool State Management
            diagnosis.layers.rootState = await this.diagnoseRootStateLayer();

            // Layer 3: Tool System Integration
            diagnosis.layers.toolSystem = await this.diagnoseToolSystemLayer();

            // Layer 4: Event System and Communication
            diagnosis.layers.eventSystem = await this.diagnoseEventSystemLayer();

            // Analyze results and generate recommendations
            this.analyzeResults(diagnosis);

            diagnosis.success = diagnosis.issues.length === 0;
            diagnosis.metadata.duration = Date.now() - startTime;

            return diagnosis;

        } catch (error) {
            diagnosis.metadata.duration = Date.now() - startTime;
            diagnosis.issues.push({
                type: 'diagnosis_error',
                severity: 'critical',
                message: `Diagnosis failed: ${error.message}`,
                data: { error: error.message, stack: error.stack }
            });

            return diagnosis;
        }
    }

    /**
     * Diagnose editor layer
     */
    async diagnoseEditorLayer() {
        const layer = {
            name: 'editor',
            status: 'unknown',
            checks: {},
            issues: [],
            data: {}
        };

        try {
            // Check editor existence
            layer.checks.editorExists = typeof window.tlEditor !== 'undefined';
            layer.data.editorType = typeof window.tlEditor;

            if (!layer.checks.editorExists) {
                layer.issues.push({
                    type: 'editor_missing',
                    severity: 'critical',
                    message: 'window.tlEditor is not defined'
                });
                layer.status = 'failed';
                return layer;
            }

            // Check editor initialization
            layer.checks.editorInitialized = !!window.tlEditor.initialized;
            layer.data.initialized = window.tlEditor.initialized;

            // Check editor methods
            layer.checks.hasGetCurrentTool = typeof window.tlEditor.getCurrentTool === 'function';
            layer.checks.hasSetCurrentTool = typeof window.tlEditor.setCurrentTool === 'function';

            layer.data.methods = {
                getCurrentTool: typeof window.tlEditor.getCurrentTool,
                setCurrentTool: typeof window.tlEditor.setCurrentTool
            };

            // Test getCurrentTool method
            if (layer.checks.hasGetCurrentTool) {
                try {
                    const currentTool = window.tlEditor.getCurrentTool();
                    layer.data.currentTool = currentTool;
                    layer.checks.getCurrentToolWorks = true;
                } catch (error) {
                    layer.checks.getCurrentToolWorks = false;
                    layer.issues.push({
                        type: 'getCurrentTool_error',
                        severity: 'high',
                        message: `getCurrentTool() throws error: ${error.message}`,
                        data: { error: error.message }
                    });
                }
            }

            layer.status = layer.issues.length === 0 ? 'passed' : 'failed';

        } catch (error) {
            layer.status = 'error';
            layer.issues.push({
                type: 'layer_error',
                severity: 'critical',
                message: `Editor layer diagnosis failed: ${error.message}`,
                data: { error: error.message }
            });
        }

        return layer;
    }

    /**
     * Diagnose root state layer
     */
    async diagnoseRootStateLayer() {
        const layer = {
            name: 'rootState',
            status: 'unknown',
            checks: {},
            issues: [],
            data: {}
        };

        try {
            // Check rootState existence
            layer.checks.rootStateExists = !!(window.tlEditor && window.tlEditor.rootState);

            if (!layer.checks.rootStateExists) {
                layer.issues.push({
                    type: 'rootState_missing',
                    severity: 'critical',
                    message: 'editor.rootState is not defined'
                });
                layer.status = 'failed';
                return layer;
            }

            const rootState = window.tlEditor.rootState;

            // Check currentTool property
            layer.checks.hasCurrentTool = 'currentTool' in rootState;
            layer.data.currentTool = rootState.currentTool;

            // Check getCurrentToolId method
            layer.checks.hasGetCurrentToolId = typeof rootState.getCurrentToolId === 'function';

            if (layer.checks.hasGetCurrentToolId) {
                try {
                    const toolId = rootState.getCurrentToolId();
                    layer.data.getCurrentToolIdResult = toolId;
                    layer.checks.getCurrentToolIdWorks = true;
                } catch (error) {
                    layer.checks.getCurrentToolIdWorks = false;
                    layer.issues.push({
                        type: 'getCurrentToolId_error',
                        severity: 'high',
                        message: `getCurrentToolId() throws error: ${error.message}`,
                        data: { error: error.message }
                    });
                }
            } else {
                layer.issues.push({
                    type: 'getCurrentToolId_missing',
                    severity: 'high',
                    message: 'rootState.getCurrentToolId method is not defined'
                });
            }

            // Check setTool method
            layer.checks.hasSetTool = typeof rootState.setTool === 'function';
            layer.checks.hasSetCurrentTool = typeof rootState.setCurrentTool === 'function';

            layer.status = layer.issues.length === 0 ? 'passed' : 'failed';

        } catch (error) {
            layer.status = 'error';
            layer.issues.push({
                type: 'layer_error',
                severity: 'critical',
                message: `RootState layer diagnosis failed: ${error.message}`,
                data: { error: error.message }
            });
        }

        return layer;
    }

    /**
     * Diagnose tool system layer
     */
    async diagnoseToolSystemLayer() {
        const layer = {
            name: 'toolSystem',
            status: 'unknown',
            checks: {},
            issues: [],
            data: {}
        };

        try {
            // Check for tool buttons in DOM
            const toolButtons = document.querySelectorAll('[data-tool]');
            layer.checks.toolButtonsExist = toolButtons.length > 0;
            layer.data.toolButtonCount = toolButtons.length;

            if (toolButtons.length > 0) {
                layer.data.availableTools = Array.from(toolButtons).map(btn => ({
                    tool: btn.dataset.tool,
                    visible: btn.offsetParent !== null,
                    enabled: !btn.disabled
                }));
            }

            // Check for event listeners on tool buttons
            let buttonsWithListeners = 0;
            toolButtons.forEach(button => {
                // Check for click listener (simplified check)
                if (button.onclick || button.addEventListener) {
                    buttonsWithListeners++;
                }
            });

            layer.checks.buttonsHaveListeners = buttonsWithListeners > 0;
            layer.data.buttonsWithListeners = buttonsWithListeners;

            layer.status = layer.issues.length === 0 ? 'passed' : 'failed';

        } catch (error) {
            layer.status = 'error';
            layer.issues.push({
                type: 'layer_error',
                severity: 'critical',
                message: `Tool system layer diagnosis failed: ${error.message}`,
                data: { error: error.message }
            });
        }

        return layer;
    }

    /**
     * Diagnose event system layer
     */
    async diagnoseEventSystemLayer() {
        const layer = {
            name: 'eventSystem',
            status: 'unknown',
            checks: {},
            issues: [],
            data: {}
        };

        try {
            // Check for universal input system
            layer.checks.universalInputExists = typeof window.UniversalInputSystem !== 'undefined';

            // Check for event bus
            layer.checks.eventBusExists = !!(this.eventBus && typeof this.eventBus.emit === 'function');

            // Check for active event listeners
            layer.data.eventListeners = {
                hasPointerEvents: !!document.onpointerdown,
                hasMouseEvents: !!document.onmousedown,
                hasTouchEvents: !!document.ontouchstart
            };

            layer.status = layer.issues.length === 0 ? 'passed' : 'failed';

        } catch (error) {
            layer.status = 'error';
            layer.issues.push({
                type: 'layer_error',
                severity: 'critical',
                message: `Event system layer diagnosis failed: ${error.message}`,
                data: { error: error.message }
            });
        }

        return layer;
    }

    /**
     * Analyze diagnosis results and generate recommendations
     */
    analyzeResults(diagnosis) {
        // Collect all issues from layers
        Object.values(diagnosis.layers).forEach(layer => {
            if (layer && layer.issues) {
                diagnosis.issues.push(...layer.issues);
            }
        });

        // Generate specific recommendations based on issues
        diagnosis.issues.forEach(issue => {
            switch (issue.type) {
                case 'editor_missing':
                    diagnosis.recommendations.push({
                        type: 'fix',
                        priority: 'critical',
                        message: 'Initialize TLDrawEditor instance',
                        action: 'Create window.tlEditor = new TLDrawEditor(container)'
                    });
                    break;

                case 'rootState_missing':
                    diagnosis.recommendations.push({
                        type: 'fix',
                        priority: 'critical',
                        message: 'Initialize RootState in editor constructor',
                        action: 'Add this.rootState = new RootState(this) in TLDrawEditor constructor'
                    });
                    break;

                case 'getCurrentToolId_missing':
                    diagnosis.recommendations.push({
                        type: 'fix',
                        priority: 'high',
                        message: 'Add getCurrentToolId method to RootState',
                        action: 'Add getCurrentToolId() { return this.currentTool; } to RootState class'
                    });
                    break;

                case 'getCurrentTool_error':
                    diagnosis.recommendations.push({
                        type: 'fix',
                        priority: 'high',
                        message: 'Fix getCurrentTool method implementation',
                        action: 'Ensure getCurrentTool delegates to rootState.getCurrentToolId()'
                    });
                    break;
            }
        });

        // Add general recommendations
        if (diagnosis.issues.length === 0) {
            diagnosis.recommendations.push({
                type: 'info',
                priority: 'low',
                message: 'Tool selection system appears to be functioning correctly',
                action: 'Continue monitoring for edge cases'
            });
        }
    }

    /**
     * Handle tool selection requests
     */
    async handleToolSelectionRequest(data) {
        this.logger.debug('Tool selection requested', data);

        // Trigger diagnosis if tool selection fails
        if (data.success === false) {
            await this.diagnose('tool-selection-failure', data);
        }
    }

    /**
     * Handle editor state changes
     */
    handleEditorStateChange(data) {
        this.logger.debug('Editor state changed', data);

        // Could trigger lightweight validation here
    }

    /**
     * Generate unique diagnosis ID
     */
    generateDiagnosisId() {
        return `diag_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }

    /**
     * Get service identifier
     */
    getServiceId() {
        return this.serviceId;
    }

    /**
     * Get service statistics
     */
    getStatistics() {
        return { ...this.state.statistics };
    }

    /**
     * Get last diagnosis result
     */
    getLastDiagnosis() {
        return this.state.lastDiagnosis;
    }
}

// =============================================================================
// ENTERPRISE DIAGNOSTIC COORDINATOR
// =============================================================================

class EnterpriseDiagnosticCoordinator {
    constructor() {
        this.container = new DiagnosticContainer();
        this.isInitialized = false;
        this.services = new Map();

        this.setupContainer();
    }

    /**
     * Setup dependency injection container with all services
     */
    setupContainer() {
        // Register core services
        this.container
            .register('eventBus', DiagnosticEventBus)
            .register('logger', StructuredLogger, {
                namespace: 'enterprise-diagnostic',
                level: 'debug'
            })
            .register('toolDiagnostic', ToolSelectionDiagnosticService, {
                dependencies: ['eventBus', 'logger'],
                retryAttempts: 3,
                timeout: 5000
            });
    }

    /**
     * Initialize the diagnostic system
     */
    async initialize() {
        if (this.isInitialized) {
            return;
        }

        try {
            console.log('ğŸš€ Initializing Enterprise Diagnostic System...');

            // Resolve core services
            const eventBus = this.container.resolve('eventBus');
            const logger = this.container.resolve('logger');
            const toolDiagnostic = this.container.resolve('toolDiagnostic');

            // Store service references
            this.services.set('eventBus', eventBus);
            this.services.set('logger', logger);
            this.services.set('toolDiagnostic', toolDiagnostic);

            // Setup global event listeners
            this.setupGlobalEventListeners(eventBus, logger);

            this.isInitialized = true;

            logger.info('Enterprise Diagnostic System initialized successfully', {
                services: Array.from(this.services.keys()),
                timestamp: new Date().toISOString()
            });

            console.log('âœ… Enterprise Diagnostic System ready');

        } catch (error) {
            console.error('âŒ Failed to initialize Enterprise Diagnostic System:', error);
            throw error;
        }
    }

    /**
     * Setup global event listeners for system monitoring
     */
    setupGlobalEventListeners(eventBus, logger) {
        // Monitor all diagnostic events
        eventBus.subscribe('*', (event) => {
            if (event.type.startsWith('diagnosis-')) {
                logger.debug('Diagnostic event', {
                    type: event.type,
                    serviceId: event.data.serviceId,
                    timestamp: event.timestamp
                });
            }
        });

        // Monitor system errors
        window.addEventListener('error', (event) => {
            logger.error('Global error detected', {
                message: event.message,
                filename: event.filename,
                lineno: event.lineno,
                colno: event.colno,
                stack: event.error?.stack
            });
        });

        // Monitor unhandled promise rejections
        window.addEventListener('unhandledrejection', (event) => {
            logger.error('Unhandled promise rejection', {
                reason: event.reason,
                stack: event.reason?.stack
            });
        });
    }

    /**
     * Get a service instance
     */
    getService(serviceName) {
        return this.services.get(serviceName);
    }

    /**
     * Run comprehensive tool selection diagnosis
     */
    async diagnoseToolSelection(context = {}) {
        if (!this.isInitialized) {
            await this.initialize();
        }

        const toolDiagnostic = this.getService('toolDiagnostic');
        return await toolDiagnostic.diagnose('tool-selection', context);
    }

    /**
     * Get system health status
     */
    getSystemHealth() {
        const health = {
            initialized: this.isInitialized,
            services: {},
            overall: 'unknown'
        };

        if (this.isInitialized) {
            this.services.forEach((service, name) => {
                health.services[name] = {
                    available: !!service,
                    type: service.constructor.name
                };
            });

            const allServicesHealthy = Object.values(health.services).every(s => s.available);
            health.overall = allServicesHealthy ? 'healthy' : 'degraded';
        }

        return health;
    }
}

// =============================================================================
// GLOBAL INITIALIZATION
// =============================================================================

// Create global diagnostic coordinator instance
window.EnterpriseDiagnosticCoordinator = EnterpriseDiagnosticCoordinator;

// Auto-initialize when DOM is ready
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', async () => {
        window.enterpriseDiagnostic = new EnterpriseDiagnosticCoordinator();
        await window.enterpriseDiagnostic.initialize();
    });
} else {
    window.enterpriseDiagnostic = new EnterpriseDiagnosticCoordinator();
    window.enterpriseDiagnostic.initialize();
}

