/**
 * Enterprise Tool Selection Fix
 * 
 * Uses the Enterprise Diagnostic Architecture to:
 * 1. Diagnose the root cause of tool selection failures
 * 2. Implement systematic fixes with dependency injection
 * 3. Provide continuous monitoring and self-healing capabilities
 * 4. Ensure scalable, maintainable solution
 * 
 * Date: 2025-07-25
 * Architecture: Enterprise-grade, self-healing system
 */

class ToolSelectionRepairService {
    constructor(diagnosticCoordinator) {
        this.diagnosticCoordinator = diagnosticCoordinator;
        this.logger = null;
        this.eventBus = null;
        this.repairHistory = [];
        this.autoRepairEnabled = true;
        this.isRepairing = false;

        this.initializeService();
    }

    /**
     * Initialize the repair service with dependency injection
     */
    async initializeService() {
        try {
            // Ensure diagnostic system is initialized
            if (!this.diagnosticCoordinator.isInitialized) {
                await this.diagnosticCoordinator.initialize();
            }

            // Inject dependencies
            this.logger = this.diagnosticCoordinator.getService('logger');
            this.eventBus = this.diagnosticCoordinator.getService('eventBus');

            // Setup event listeners for automatic repair
            this.setupAutoRepairListeners();

            this.logger.info('ToolSelectionRepairService initialized', {
                autoRepair: this.autoRepairEnabled,
                timestamp: new Date().toISOString()
            });

        } catch (error) {
            console.error('Failed to initialize ToolSelectionRepairService:', error);
            throw error;
        }
    }

    /**
     * Setup listeners for automatic repair triggers
     */
    setupAutoRepairListeners() {
        // Listen for diagnosis failures
        this.eventBus.subscribe('diagnosis-completed', async (event) => {
            const { result } = event.data;

            if (!result.success && result.issues.length > 0 && this.autoRepairEnabled) {
                this.logger.warn('Tool selection issues detected, initiating auto-repair', {
                    issueCount: result.issues.length,
                    diagnosisId: result.id
                });

                await this.performAutomaticRepair(result);
            }
        });

        // Listen for manual repair requests
        this.eventBus.subscribe('repair-requested', async (event) => {
            await this.performManualRepair(event.data);
        });
    }

    /**
     * Perform comprehensive tool selection repair
     */
    async performComprehensiveRepair() {
        if (this.isRepairing) {
            this.logger.warn('Repair already in progress, skipping');
            return false;
        }

        this.isRepairing = true;
        const repairId = this.generateRepairId();

        this.logger.info('Starting comprehensive tool selection repair', { repairId });

        try {
            // Step 1: Run comprehensive diagnosis
            const diagnosis = await this.diagnosticCoordinator.diagnoseToolSelection({
                repairId,
                comprehensive: true
            });

            // Step 2: Create repair plan based on diagnosis
            const repairPlan = this.createRepairPlan(diagnosis);

            // Step 3: Execute repair plan
            const repairResult = await this.executeRepairPlan(repairPlan, repairId);

            // Step 4: Verify repair success
            const verification = await this.verifyRepair(repairId);

            // Step 5: Record repair history
            this.recordRepairHistory(repairId, diagnosis, repairPlan, repairResult, verification);

            this.logger.info('Comprehensive repair completed', {
                repairId,
                success: verification.success,
                issuesFixed: repairResult.fixesApplied.length
            });

            return {
                success: verification.success,
                repairId,
                diagnosis,
                repairPlan,
                repairResult,
                verification
            };

        } catch (error) {
            this.logger.error('Comprehensive repair failed', {
                repairId,
                error: error.message,
                stack: error.stack
            });

            return {
                success: false,
                repairId,
                error: error.message
            };

        } finally {
            this.isRepairing = false;
        }
    }

    /**
     * Create a repair plan based on diagnosis results
     */
    createRepairPlan(diagnosis) {
        const plan = {
            id: this.generateRepairId(),
            timestamp: Date.now(),
            diagnosis: diagnosis.id,
            repairs: [],
            priority: 'high',
            estimatedDuration: 0
        };

        // Analyze each issue and create repair actions
        diagnosis.issues.forEach(issue => {
            switch (issue.type) {
                case 'editor_missing':
                    plan.repairs.push({
                        type: 'create_editor',
                        priority: 'critical',
                        action: 'initializeEditor',
                        description: 'Create and initialize TLDrawEditor instance',
                        estimatedTime: 500
                    });
                    break;

                case 'rootState_missing':
                    plan.repairs.push({
                        type: 'create_rootState',
                        priority: 'critical',
                        action: 'initializeRootState',
                        description: 'Create and initialize RootState instance',
                        estimatedTime: 200
                    });
                    break;

                case 'getCurrentToolId_missing':
                    plan.repairs.push({
                        type: 'add_method',
                        priority: 'high',
                        action: 'addGetCurrentToolIdMethod',
                        description: 'Add getCurrentToolId method to RootState',
                        estimatedTime: 100
                    });
                    break;

                case 'getCurrentTool_error':
                    plan.repairs.push({
                        type: 'fix_method',
                        priority: 'high',
                        action: 'fixGetCurrentToolMethod',
                        description: 'Fix getCurrentTool method implementation',
                        estimatedTime: 150
                    });
                    break;

                default:
                    plan.repairs.push({
                        type: 'generic_fix',
                        priority: 'medium',
                        action: 'investigateAndFix',
                        description: `Address issue: ${issue.type}`,
                        estimatedTime: 300,
                        issue
                    });
            }
        });

        // Sort repairs by priority
        plan.repairs.sort((a, b) => {
            const priorities = { critical: 0, high: 1, medium: 2, low: 3 };
            return priorities[a.priority] - priorities[b.priority];
        });

        // Calculate total estimated duration
        plan.estimatedDuration = plan.repairs.reduce((total, repair) => total + repair.estimatedTime, 0);

        this.logger.info('Repair plan created', {
            planId: plan.id,
            repairCount: plan.repairs.length,
            estimatedDuration: plan.estimatedDuration
        });

        return plan;
    }

    /**
     * Execute the repair plan
     */
    async executeRepairPlan(plan, repairId) {
        const result = {
            planId: plan.id,
            repairId,
            timestamp: Date.now(),
            fixesApplied: [],
            fixesFailed: [],
            totalDuration: 0
        };

        const startTime = Date.now();

        this.logger.info('Executing repair plan', {
            planId: plan.id,
            repairCount: plan.repairs.length
        });

        for (const repair of plan.repairs) {
            try {
                this.logger.debug('Applying repair', {
                    type: repair.type,
                    action: repair.action,
                    description: repair.description
                });

                const repairStartTime = Date.now();
                const success = await this.applyRepair(repair);
                const repairDuration = Date.now() - repairStartTime;

                if (success) {
                    result.fixesApplied.push({
                        ...repair,
                        duration: repairDuration,
                        timestamp: Date.now()
                    });

                    this.logger.info('Repair applied successfully', {
                        type: repair.type,
                        duration: repairDuration
                    });
                } else {
                    result.fixesFailed.push({
                        ...repair,
                        duration: repairDuration,
                        timestamp: Date.now(),
                        reason: 'Repair method returned false'
                    });

                    this.logger.warn('Repair failed', {
                        type: repair.type,
                        reason: 'Repair method returned false'
                    });
                }

            } catch (error) {
                result.fixesFailed.push({
                    ...repair,
                    timestamp: Date.now(),
                    error: error.message,
                    stack: error.stack
                });

                this.logger.error('Repair threw error', {
                    type: repair.type,
                    error: error.message
                });
            }
        }

        result.totalDuration = Date.now() - startTime;

        this.logger.info('Repair plan execution completed', {
            planId: plan.id,
            successful: result.fixesApplied.length,
            failed: result.fixesFailed.length,
            duration: result.totalDuration
        });

        return result;
    }

    /**
     * Apply a specific repair
     */
    async applyRepair(repair) {
        switch (repair.action) {
            case 'initializeEditor':
                return this.initializeEditor();

            case 'initializeRootState':
                return this.initializeRootState();

            case 'addGetCurrentToolIdMethod':
                return this.addGetCurrentToolIdMethod();

            case 'fixGetCurrentToolMethod':
                return this.fixGetCurrentToolMethod();

            case 'investigateAndFix':
                return this.investigateAndFix(repair.issue);

            default:
                this.logger.warn('Unknown repair action', { action: repair.action });
                return false;
        }
    }

    /**
     * Initialize editor if missing
     */
    initializeEditor() {
        try {
            if (typeof window.tlEditor !== 'undefined') {
                this.logger.debug('Editor already exists, skipping initialization');
                return true;
            }

            const container = document.getElementById('canvas-container');
            if (!container) {
                this.logger.error('Canvas container not found');
                return false;
            }

            if (typeof window.TLDrawEditor === 'undefined') {
                this.logger.error('TLDrawEditor class not found');
                return false;
            }

            // Create new editor instance
            window.tlEditor = new window.TLDrawEditor(container);
            window.editorReady = true;

            this.logger.info('Editor initialized successfully');
            this.eventBus.emit('editor-initialized', { timestamp: Date.now() });

            return true;

        } catch (error) {
            this.logger.error('Failed to initialize editor', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Initialize RootState if missing
     */
    initializeRootState() {
        try {
            if (!window.tlEditor) {
                this.logger.error('Cannot initialize RootState: editor not found');
                return false;
            }

            if (window.tlEditor.rootState) {
                this.logger.debug('RootState already exists, skipping initialization');
                return true;
            }

            if (typeof window.RootState === 'undefined') {
                this.logger.error('RootState class not found');
                return false;
            }

            // Create new RootState instance
            window.tlEditor.rootState = new window.RootState(window.tlEditor);
            window.tlEditor.rootState.enter();

            this.logger.info('RootState initialized successfully');
            this.eventBus.emit('rootState-initialized', { timestamp: Date.now() });

            return true;

        } catch (error) {
            this.logger.error('Failed to initialize RootState', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Add getCurrentToolId method if missing
     */
    addGetCurrentToolIdMethod() {
        try {
            if (!window.tlEditor?.rootState) {
                this.logger.error('Cannot add method: RootState not found');
                return false;
            }

            const rootState = window.tlEditor.rootState;

            if (typeof rootState.getCurrentToolId === 'function') {
                this.logger.debug('getCurrentToolId method already exists');
                return true;
            }

            // Add the method dynamically
            rootState.getCurrentToolId = function() {
                return this.currentTool || 'select';
            };

            this.logger.info('getCurrentToolId method added successfully');
            this.eventBus.emit('method-added', {
                method: 'getCurrentToolId',
                target: 'rootState',
                timestamp: Date.now()
            });

            return true;

        } catch (error) {
            this.logger.error('Failed to add getCurrentToolId method', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Fix getCurrentTool method implementation
     */
    fixGetCurrentToolMethod() {
        try {
            if (!window.tlEditor) {
                this.logger.error('Cannot fix method: editor not found');
                return false;
            }

            // Ensure the method delegates to rootState.getCurrentToolId
            window.tlEditor.getCurrentTool = function() {
                if (!this.rootState) {
                    console.warn('RootState not available, returning default tool');
                    return 'select';
                }

                if (typeof this.rootState.getCurrentToolId !== 'function') {
                    console.warn('getCurrentToolId method not available, returning default tool');
                    return 'select';
                }

                return this.rootState.getCurrentToolId();
            };

            this.logger.info('getCurrentTool method fixed successfully');
            this.eventBus.emit('method-fixed', {
                method: 'getCurrentTool',
                target: 'editor',
                timestamp: Date.now()
            });

            return true;

        } catch (error) {
            this.logger.error('Failed to fix getCurrentTool method', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Investigate and fix unknown issues
     */
    investigateAndFix(issue) {
        this.logger.info('Investigating unknown issue', {
            type: issue.type,
            severity: issue.severity,
            message: issue.message
        });

        // Implement generic investigation logic here
        // For now, log the issue for manual review

        return false; // Requires manual intervention
    }

    /**
     * Verify that repairs were successful
     */
    async verifyRepair(repairId) {
        this.logger.info('Verifying repair success', { repairId });

        try {
            // Run diagnosis again to check if issues are resolved
            const postRepairDiagnosis = await this.diagnosticCoordinator.diagnoseToolSelection({
                repairId,
                verificationRun: true
            });

            const verification = {
                repairId,
                timestamp: Date.now(),
                success: postRepairDiagnosis.success,
                remainingIssues: postRepairDiagnosis.issues.length,
                diagnosis: postRepairDiagnosis
            };

            if (verification.success) {
                this.logger.info('Repair verification successful', {
                    repairId,
                    issuesRemaining: verification.remainingIssues
                });

                this.eventBus.emit('repair-verified', verification);
            } else {
                this.logger.warn('Repair verification failed', {
                    repairId,
                    issuesRemaining: verification.remainingIssues
                });

                this.eventBus.emit('repair-verification-failed', verification);
            }

            return verification;

        } catch (error) {
            this.logger.error('Repair verification error', {
                repairId,
                error: error.message
            });

            return {
                repairId,
                timestamp: Date.now(),
                success: false,
                error: error.message
            };
        }
    }

    /**
     * Perform automatic repair when issues are detected
     */
    async performAutomaticRepair(diagnosisResult) {
        this.logger.info('Performing automatic repair', {
            diagnosisId: diagnosisResult.id,
            issueCount: diagnosisResult.issues.length
        });

        return await this.performComprehensiveRepair();
    }

    /**
     * Perform manual repair when requested
     */
    async performManualRepair(requestData) {
        this.logger.info('Performing manual repair', requestData);

        return await this.performComprehensiveRepair();
    }

    /**
     * Record repair history for analysis
     */
    recordRepairHistory(repairId, diagnosis, repairPlan, repairResult, verification) {
        const historyEntry = {
            repairId,
            timestamp: Date.now(),
            diagnosis,
            repairPlan,
            repairResult,
            verification
        };

        this.repairHistory.push(historyEntry);

        // Keep only last 100 entries
        if (this.repairHistory.length > 100) {
            this.repairHistory.shift();
        }

        this.logger.debug('Repair history recorded', { repairId });
    }

    /**
     * Generate unique repair ID
     */
    generateRepairId() {
        return `repair_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }

    /**
     * Get repair history
     */
    getRepairHistory(limit = 10) {
        return this.repairHistory.slice(-limit);
    }

    /**
     * Enable/disable auto-repair
     */
    setAutoRepair(enabled) {
        this.autoRepairEnabled = enabled;
        this.logger.info('Auto-repair setting changed', { enabled });
    }
}

