            }
        }

        result.totalDuration = Date.now() - startTime;

        this.logger.info('Repair plan execution completed', {
            planId: plan.id,
            successful: result.fixesApplied.length,
            failed: result.fixesFailed.length,
            duration: result.totalDuration
        });

        return result;
    }

    /**
     * Apply a specific repair
     */
    async applyRepair(repair) {
        switch (repair.action) {
            case 'initializeEditor':
                return this.initializeEditor();

            case 'initializeRootState':
                return this.initializeRootState();

            case 'addGetCurrentToolIdMethod':
                return this.addGetCurrentToolIdMethod();

            case 'fixGetCurrentToolMethod':
                return this.fixGetCurrentToolMethod();

            case 'investigateAndFix':
                return this.investigateAndFix(repair.issue);

            default:
                this.logger.warn('Unknown repair action', { action: repair.action });
                return false;
        }
    }

    /**
     * Initialize editor if missing
     */
    initializeEditor() {
        try {
            if (typeof window.tlEditor !== 'undefined') {
                this.logger.debug('Editor already exists, skipping initialization');
                return true;
            }

            const container = document.getElementById('canvas-container');
            if (!container) {
                this.logger.error('Canvas container not found');
                return false;
            }

            if (typeof window.TLDrawEditor === 'undefined') {
                this.logger.error('TLDrawEditor class not found');
                return false;
            }

            // Create new editor instance
            window.tlEditor = new window.TLDrawEditor(container);
            window.editorReady = true;

            this.logger.info('Editor initialized successfully');
            this.eventBus.emit('editor-initialized', { timestamp: Date.now() });

            return true;

        } catch (error) {
            this.logger.error('Failed to initialize editor', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Initialize RootState if missing
     */
    initializeRootState() {
        try {
            if (!window.tlEditor) {
                this.logger.error('Cannot initialize RootState: editor not found');
                return false;
            }

            if (window.tlEditor.rootState) {
                this.logger.debug('RootState already exists, skipping initialization');
                return true;
            }

            if (typeof window.RootState === 'undefined') {
                this.logger.error('RootState class not found');
                return false;
            }

            // Create new RootState instance
            window.tlEditor.rootState = new window.RootState(window.tlEditor);
            window.tlEditor.rootState.enter();

            this.logger.info('RootState initialized successfully');
            this.eventBus.emit('rootState-initialized', { timestamp: Date.now() });

            return true;

        } catch (error) {
            this.logger.error('Failed to initialize RootState', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Add getCurrentToolId method if missing
     */
    addGetCurrentToolIdMethod() {
        try {
            if (!window.tlEditor?.rootState) {
                this.logger.error('Cannot add method: RootState not found');
                return false;
            }

            const rootState = window.tlEditor.rootState;

            if (typeof rootState.getCurrentToolId === 'function') {
                this.logger.debug('getCurrentToolId method already exists');
                return true;
            }

            // Add the method dynamically
            rootState.getCurrentToolId = function() {
                return this.currentTool || 'select';
            };

            this.logger.info('getCurrentToolId method added successfully');
            this.eventBus.emit('method-added', {
                method: 'getCurrentToolId',
                target: 'rootState',
                timestamp: Date.now()
            });

            return true;

        } catch (error) {
            this.logger.error('Failed to add getCurrentToolId method', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Fix getCurrentTool method implementation
     */
    fixGetCurrentToolMethod() {
        try {
            if (!window.tlEditor) {
                this.logger.error('Cannot fix method: editor not found');
                return false;
            }

            // Ensure the method delegates to rootState.getCurrentToolId
            window.tlEditor.getCurrentTool = function() {
                if (!this.rootState) {
                    console.warn('RootState not available, returning default tool');
                    return 'select';
                }

                if (typeof this.rootState.getCurrentToolId !== 'function') {
                    console.warn('getCurrentToolId method not available, returning default tool');
                    return 'select';
                }

                return this.rootState.getCurrentToolId();
            };

            this.logger.info('getCurrentTool method fixed successfully');
            this.eventBus.emit('method-fixed', {
                method: 'getCurrentTool',
                target: 'editor',
                timestamp: Date.now()
            });

            return true;

        } catch (error) {
            this.logger.error('Failed to fix getCurrentTool method', {
                error: error.message,
                stack: error.stack
            });
            return false;
        }
    }

    /**
     * Investigate and fix unknown issues
     */
    investigateAndFix(issue) {
        this.logger.info('Investigating unknown issue', {
            type: issue.type,
            severity: issue.severity,
            message: issue.message
        });

        // Implement generic investigation logic here
        // For now, log the issue for manual review

        return false; // Requires manual intervention
    }

    /**
     * Verify that repairs were successful
     */
    async verifyRepair(repairId) {
        this.logger.info('Verifying repair success', { repairId });

        try {
            // Run diagnosis again to check if issues are resolved
            const postRepairDiagnosis = await this.diagnosticCoordinator.diagnoseToolSelection({
                repairId,
                verificationRun: true
            });

            const verification = {
                repairId,
                timestamp: Date.now(),
                success: postRepairDiagnosis.success,
                remainingIssues: postRepairDiagnosis.issues.length,
                diagnosis: postRepairDiagnosis
            };

            if (verification.success) {
                this.logger.info('Repair verification successful', {
                    repairId,
                    issuesRemaining: verification.remainingIssues
                });

                this.eventBus.emit('repair-verified', verification);
            } else {
                this.logger.warn('Repair verification failed', {
                    repairId,
                    issuesRemaining: verification.remainingIssues
                });

                this.eventBus.emit('repair-verification-failed', verification);
            }

            return verification;

        } catch (error) {
            this.logger.error('Repair verification error', {
                repairId,
                error: error.message
            });

            return {
                repairId,
                timestamp: Date.now(),
                success: false,
                error: error.message
            };
        }
    }

    /**
     * Perform automatic repair when issues are detected
     */
    async performAutomaticRepair(diagnosisResult) {
        this.logger.info('Performing automatic repair', {
            diagnosisId: diagnosisResult.id,
            issueCount: diagnosisResult.issues.length
        });

        return await this.performComprehensiveRepair();
    }

    /**
     * Perform manual repair when requested
     */
    async performManualRepair(requestData) {
        this.logger.info('Performing manual repair', requestData);

        return await this.performComprehensiveRepair();
    }

    /**
     * Record repair history for analysis
     */
    recordRepairHistory(repairId, diagnosis, repairPlan, repairResult, verification) {
        const historyEntry = {
            repairId,
            timestamp: Date.now(),
            diagnosis,
            repairPlan,
            repairResult,
            verification
        };

        this.repairHistory.push(historyEntry);

        // Keep only last 100 entries
        if (this.repairHistory.length > 100) {
            this.repairHistory.shift();
        }

        this.logger.debug('Repair history recorded', { repairId });
    }

    /**
     * Generate unique repair ID
     */
    generateRepairId() {
        return `repair_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }

    /**
     * Get repair history
     */
    getRepairHistory(limit = 10) {
        return this.repairHistory.slice(-limit);
    }

    /**
     * Enable/disable auto-repair
     */
    setAutoRepair(enabled) {
        this.autoRepairEnabled = enabled;
        this.logger.info('Auto-repair setting changed', { enabled });
    }
}

// =============================================================================
// INTEGRATION & AUTO-INITIALIZATION
// =============================================================================

/**
 * Initialize the enterprise tool selection fix system
 */
async function initializeToolSelectionFix() {
    try {
        console.log('ğŸ”§ Initializing Enterprise Tool Selection Fix...');

        // Wait for enterprise diagnostic system
        if (!window.enterpriseDiagnostic) {
            console.log('â³ Waiting for enterprise diagnostic system...');
            await new Promise(resolve => {
                const checkInterval = setInterval(() => {
                    if (window.enterpriseDiagnostic) {
                        clearInterval(checkInterval);
                        resolve();
                    }
                }, 100);
            });
        }

        // Create repair service
        window.toolSelectionRepair = new ToolSelectionRepairService(window.enterpriseDiagnostic);

        // Run initial diagnosis and repair if needed
        setTimeout(async () => {
            console.log('ğŸ” Running initial tool selection diagnosis...');

            try {
                const result = await window.toolSelectionRepair.performComprehensiveRepair();

                if (result.success) {
                    console.log('âœ… Tool selection system repaired successfully');
                } else {
                    console.log('âš ï¸ Tool selection repair completed with issues');
                    console.log('Issues remaining:', result.verification?.remainingIssues || 'unknown');
                }

            } catch (error) {
                console.error('âŒ Initial repair failed:', error);
            }
        }, 2000); // Wait 2 seconds for all systems to load

        console.log('âœ… Enterprise Tool Selection Fix initialized');

    } catch (error) {
        console.error('âŒ Failed to initialize Tool Selection Fix:', error);
    }
}

// Auto-initialize when DOM is ready
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initializeToolSelectionFix);
} else {
    initializeToolSelectionFix();
}

// Export for manual testing
window.ToolSelectionRepairService = ToolSelectionRepairService;
window.initializeToolSelectionFix = initializeToolSelectionFix;

console.log('âœ… Enterprise Tool Selection Fix loaded');

