initializeEditor() {
    try {
        // Comprehensive environment checks
        if (typeof window === 'undefined') {
            this.logger.error('Window object not available');
            return false;
        }

        if (typeof document === 'undefined') {
            this.logger.error('Document object not available');
            return false;
        }

        if (typeof window.tlEditor !== 'undefined') {
            this.logger.debug('Editor already exists, skipping initialization');
            return true;
        }

        // Defensive DOM access
        if (!document.getElementById) {
            this.logger.error('document.getElementById not available');
            return false;
        }

        const container = document.getElementById('canvas-container');
        if (!container) {
            this.logger.error('Canvas container not found', {
                attempted_id: 'canvas-container',
                document_ready: document.readyState
            });
            return false;
        }

        if (typeof window.TLDrawEditor === 'undefined') {
            this.logger.error('TLDrawEditor class not found', {
                available_constructors: Object.getOwnPropertyNames(window).filter(name => 
                    typeof window[name] === 'function' && name.startsWith('TL')
                )
            });
            return false;
        }

        // Create new editor instance with additional error handling
        try {
            window.tlEditor = new window.TLDrawEditor(container);
            window.editorReady = true;

            this.logger.info('Editor initialized successfully', {
                container_id: container.id,
                container_dimensions: {
                    width: container.offsetWidth,
                    height: container.offsetHeight
                }
            });

            if (this.eventBus && this.eventBus.emit) {
                this.eventBus.emit('editor-initialized', { timestamp: Date.now() });
            }

            return true;

        } catch (constructorError) {
            this.logger.error('TLDrawEditor constructor failed', {
                error: constructorError.message,
                stack: constructorError.stack,
                container_exists: !!container,
                container_id: container?.id
            });
            return false;
        }

    } catch (error) {
        this.logger.error('Failed to initialize editor', {
            error: error.message,
            stack: error.stack,
            window_available: typeof window !== 'undefined',
            document_available: typeof document !== 'undefined'
        });
        return false;
    }
}

